// simple camera

struct Ray {
    vec3 eye;
    vec3 direction;
};

// from https://github.com/glslify/glsl-look-at
mat3 makeCamera( vec3 origin, vec3 target, float roll ) {
	vec3 up = vec3( sin( roll ), cos( roll ), .0 );
	vec3 zz = normalize( target - origin );
	vec3 xx = normalize( cross( zz, up ) );
	vec3 yy = normalize( cross( xx, zz ) );
	return mat3( xx, yy, zz );
}

Ray lookAtTheCamera( in vec2 uv, in vec3 eye, in vec3 look, in float roll, in float zoom ) {
    mat3 camera = makeCamera( eye, look, roll );
    vec3 direction = normalize( camera * vec3( uv.xy, zoom ) );

    return Ray( eye, direction );
}

Ray mouseCamera( in vec2 uv ) {    
    vec2 mx = ( iMouse.xy / iResolution.xy ) -.5;

    vec2 T = TRIG( CAMERA_SCALE, mx.x * PI2 );
    vec3 eye  = vec3( T.x, mx.y * CAMERA_SCALE + 2. , T.y );
    vec3 look = vec3( .0 );

    float roll = .0;
    float zoom = 1.8;

    return lookAtTheCamera( uv, eye, look, roll, zoom );
}

Ray demoCamera( in vec2 uv ) {
    vec2 T = TRIG( CAMERA_SCALE, iTime );
    vec3 eye  = vec3( T.x, CAMERA_SCALE * .5, T.y );
    vec3 look = vec3( .0 );

    float roll = 0.02 * T.x;
    float zoom = 1. + .25 * abs( sin( iTime * .66 ) );

    eye = vec3( cos(iTime *.1)*33., T.x * .2, 4. );
    look = eye;
    look.z -= 4.;
    roll = cos( iTime * .07 );
    zoom = 1.;

    return lookAtTheCamera( uv, eye, look, roll, zoom );
}

Ray cameraRay( in vec2 uv ) {
    float down = iMouse.z;

#ifndef GEAR_REPEAT
    down = 1.;
    //ray = lookAtTheCamera( uv, vec3(.0,.0,4.), vec3(.0), .0, 1. );
#endif
    if  ( down > .0 ) return mouseCamera( uv ); else return demoCamera( uv );

//    return ( down > .0 ) ? mouseCamera( uv ) : demoCamera( uv );
}

