// from https://www.shadertoy.com/view/tlccWN

#define FONT_SAMPLER    iChannel2
#define FONT_STRIDE(n)  ((n)*vec2( .5, .0 ))

#define FONT_STAR 28
#define FONT_0    48
#define FONT_DASH 45
#define FONT_DOT  46
#define FONT_T    84
#define FONT_e    101
#define FONT_s    115
#define FONT_t    116

float characterDistance( vec2 point, int character ) {
    vec2 charAt = vec2( character % 16, 15 - character / 16 ) / 16. + 1./32.;    
    float w = textureLod( FONT_SAMPLER, charAt + point / 16., .0 ).w;
    vec2 box = abs( point / .8 );
    return max( w, max( box.x, box.y ) );
}

float characterLength( int value ) {
    return floor( 1. + log2( float( abs( ( value == 0 ) ? 1 : value ) ) ) / log2( 10.) );
}

float numericStringDistance( vec2 point, int value ) {
    point -= FONT_STRIDE( characterLength( value ) - 1. );
    float d = 0 == value ? characterDistance( point, FONT_0 ) : 1e33;  
    for( int v = abs( value ) ; 0 != v ; v /= 10, point += FONT_STRIDE( 1. ) ) {
        d = min( d, characterDistance( point, FONT_0 + v % 10 ) );
    }  
    return min( d, value < 0 ? characterDistance( point, FONT_DASH ) : 1e33 );
}

float numericStringDistance( in vec2 point, in float value, int decimals ) {
    int vi = int( sign( value ) * floor( abs( value ) ) );
    float d = numericStringDistance( point, vi );  

    point -= FONT_STRIDE( characterLength( vi ) );
    d = min( d, characterDistance( point, FONT_DOT ) );

    point -= FONT_STRIDE(1.);

    float vf = fract( abs( value ) ) * 10.;
    int max = int( pow( 10., float( decimals ) ) );
    for ( int i = 1 ; i <= decimals ; i++, vf *=10. , point -= FONT_STRIDE( 1. ) ) {
        int n = int( vf ) % 10;
        d = min( d, characterDistance( point, FONT_0 + n % 10 ) );
    }

    return d;
}

float distanceToShape( float d, float t ) {
    return smoothstep( .5 - t, .5 + t, 1.-d );
}

/////////////////////////////////////////////////////////////////////////////
// main function

vec3 mainly( in vec2 fragCoord ) {
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    Ray ray = cameraRay( uv );

    vec2 d = rayMarch( ray.eye, ray.direction );
    float missed = step( RAY_MARCH_TOO_FAR, d.x );

    vec3 point = ray.eye + ray.direction * d.x;
    vec3 normal = sceneNormal( point );

    vec3 background = mix( vec3(.9,.4,.0), vec3(.0,.4,.9), uv.y * .8 );
    vec3 color = colorPoint( point, normal, d, ray.eye );


#ifdef REFLECTION_LEVEL
    if ( REFLECTION_NONE != REFLECTION_LEVEL ) {
        color = mixInReflection( point, d.y, normal ,color, ray.eye );
    }
#endif

#ifndef GEAR_REPEAT
    float q = 1e33;
    q = min( q, numericStringDistance( (uv - vec2(1.3,.8))/.1, ray.eye.x, 4 ) );
    q = min( q, numericStringDistance( (uv - vec2(1.3,.7))/.1, ray.eye.y, 4 ) );
    q = min( q, numericStringDistance( (uv - vec2(1.3,.6))/.1, ray.eye.z, 4 ) );

    q = min( q, numericStringDistance( (uv - vec2(1.3,.4))/.1, ray.direction.x, 4 ) );
    q = min( q, numericStringDistance( (uv - vec2(1.3,.3))/.1, ray.direction.y, 4 ) );
    q = min( q, numericStringDistance( (uv - vec2(1.3,.2))/.1, ray.direction.z, 4 ) );

    q = min( q, numericStringDistance( (uv - vec2(1.3,-.1))/.1, iMouse.x, 4 ) );
    q = min( q, numericStringDistance( (uv - vec2(1.3,-.2))/.1, iMouse.y, 4 ) );
    q = min( q, numericStringDistance( (uv - vec2(1.3,-.3))/.1, iMouse.z, 4 ) );
    q = min( q, numericStringDistance( (uv - vec2(1.3,-.4))/.1, iMouse.w, 4 ) );

    if ( missed > .0 ) {
        color = vec3( distanceToShape( q, .07 ) );
        missed = .0;
    }
#endif

    return mix( color, background, missed );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    handleInput();
#if ANTI_ALIAS_STEPS == 1
    fragColor = vec4( mainly( fragCoord ) , 1. );
#else
    vec3 color = vec3( .0 );
    float a = ANTI_ALIAS_DISTANCE / float( ANTI_ALIAS_STEPS );

    for ( int y = 0 ; y < ANTI_ALIAS_STEPS ; y++ ) {
        float ya = float( y ) * a;
        for ( int x = 0 ; x < ANTI_ALIAS_STEPS ; x++ ) {
            vec2 aa = vec2( float( x ) * a, ya );          
            color += mainly( fragCoord + aa );
        }
    }
    fragColor = vec4( color / float( ANTI_ALIAS_STEPS * ANTI_ALIAS_STEPS ), 1. );  
#endif
}

