#define MAX_STEPS 100
#define MAX_DIST 10.
#define SURF_DIST .001

#define S smoothstep
#define T iTime
#define AA

#define ANIME_PISTON S(2.0,3.0,time)-S(3.5,5.0,time)

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34,233.53));
    p += dot(p, p+23.234);
    return fract(p.x*p.y);
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.yz),p.x)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// https://www.shadertoy.com/view/4lyfzw
vec2 opRevolution( in vec3 p, float w )
{
    return vec2( length(p.xz) - w, p.y );
}
float opExtrussion( in vec3 p, in float sdf, in float h) {
    vec2 w = vec2(sdf, abs(p.z) - h);
  	return min(max(w.x,w.y),0.) + length(max(w,0.));
}


float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdRoundedX( in vec2 p, in float w, in float r )
{
    p = abs(p);
    return length(p-min(p.x+p.y,w)*0.5) - r;
}

// https://www.shadertoy.com/playlist/l3dXRf
// https://www.iquilezles.org/www/articles/intersectors/intersectors.htm
// cylinder defined by extremes pa and pb, and radious ra
vec4 cylIntersect( in vec3 ro, in vec3 rd, in vec3 pa, in vec3 pb, float ra )
{
    vec3 ca = pb-pa;
    vec3 oc = ro-pa;
    float caca = dot(ca,ca);
    float card = dot(ca,rd);
    float caoc = dot(ca,oc);
    float a = caca - card*card;
    float b = caca*dot( oc, rd) - caoc*card;
    float c = caca*dot( oc, oc) - caoc*caoc - ra*ra*caca;
    float h = b*b - a*c;
    if( h<0.0 ) return vec4(-1.0); //no intersection
    h = sqrt(h);
    float t = (-b-h)/a;
    // body
    float y = caoc + t*card;
    if( y>0.0 && y<caca ) return vec4( t, (oc+t*rd-ca*y/caca)/ra );
    // caps
    t = (((y<0.0)?0.0:caca) - caoc)/card;
    if( abs(b+a*t)<h ) return vec4( t, ca*sign(y)/caca );
    return vec4(-1.0); //no intersection
}

#define dot2(x) dot(x,x)

float sdCappedCone( vec3 p, float h, float r1, float r2 )
{
  vec2 q = vec2( length(p.xz), p.y );
  vec2 k1 = vec2(r2,h);
  vec2 k2 = vec2(r2-r1,2.0*h);
  vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
  vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
  float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
  return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// -- Text ------------------------------------------------
// Adapted from [iapafoto] https://www.shadertoy.com/view/tdyBDh
// --------------------------------------------------------

int[] gtxt = int[] (50,48,50,48); // 2021

float sdFont(in vec2 p, in int c) {
    vec2 uv = (p + vec2(float(c%16), float(15-c/16)) + .5)/16.;
    return max(max(abs(p.x) - .25, max(p.y - .35, -.38 - p.y)), textureLod(iChannel2, uv, 0.).w - 127./255.);
}

const float letterSpacing=2.45;

float sdMessage2D(in vec2 p, in int[4] txt) { 
 	float d = 99., w = letterSpacing; // letter width  spacing
    for (int id = 0; id<4; id++){
    	d = min(d, sdFont(p, txt[id]));   
    	p.x -= w; 
    }
    return d-.015;
}

// extrussion replaced by Cylinder mapping
float sdMessage3D(in vec3 p, in int[4] txt, in float scale, in float h) { 
    return opExtrussion(p, sdMessage2D(p.xy/scale, txt)*scale, h);
}

float sdPiston(vec3 p,float l) {
    float pusher = sdCappedCylinder(p,0.3,0.01)-0.03;
    float pilar=opExtrussion(p.yzx-vec3(0.0,0.0,l),sdRoundedX(p.yz,0.20,0.02),l);
    return min(pusher,pilar);
}

float sdSyringeBody(vec3 p) {
    float bottom = sdCappedCylinder(p,0.35,0.01)-0.03;
    bottom=max(bottom,-(length(p.yz)-0.18));
    float end = sdCappedCone(p.yxz-vec3(0.0,2.9,0.0),0.05,0.25,0.08)-0.01;
    end = min(end,sdCappedCylinder(p-vec3(3.0,0.0,0.0),0.08,0.10));
    float d = min(bottom,end);
    return d;
}

float sdGraduations(vec3 p) {
    float r=0.25;
    p.x-=0.8;
    // fold on a Cylinder along x
    float d = abs(length(p.yz)-r)-0.001;
    // box with the tube
    if ( d > 0.1 ) return d;
    float a=atan(p.y,p.z);
    vec2 uv = vec2(p.x,a*r);
    float scale=0.2;
    // let's try some animation of numbers
    float grad1 = letterSpacing*scale;
    float grad2 = letterSpacing*scale/5.0;
    float time=mod(T,6.0);
    float atime = S(0.0,2.0,time);
    float jump=0.0;
    if ( uv.x > letterSpacing*scale*2.5 && uv.x < letterSpacing*scale*3.5 ) {
        jump= atime > 0.5 ? (atime-1.0)*2.0 : atime*2.0;
    }
    gtxt[3]= time > 0.5 && time < 5.0 ? 49 : 48;   
    float dtxt = sdMessage2D((uv-vec2(0.0,jump))/scale, gtxt)*scale;
    uv.x -= round(uv.x/grad1)*grad1;
    float dgrad = sdBox(uv-vec2(0.0,0.2+abs(jump)),vec2(0.01,0.1));
    dgrad = min(dgrad,sdBox(uv-vec2(clamp(round(uv.x/grad2),1.0,2.0)*grad2,0.2+0.05),vec2(0.01,0.05)));
    dgrad = min(dgrad,sdBox(uv-vec2(clamp(round(uv.x/grad2),-2.0,-1.0)*grad2,0.2+0.05),vec2(0.01,0.05)));
    float dtxt3d = max(min(dtxt,dgrad),d);
    return max(dtxt3d-0.003,max(p.x-2.0,-0.8-p.x));  
}

float sdNeedleBase(vec3 p) {
    float d = sdCappedCylinder(p-vec3(3.1,0.0,0.0),0.15,0.01)-0.01;
    d = min(d,sdCappedCylinder(p-vec3(3.19,0.0,0.0),0.09,0.07)-0.01);
    float end = sdCappedCone(p.yxz-vec3(0.0,3.35,0.0),0.1,0.08,0.04);
    d = min(d,end);
    return d;
}

float sdNeedle(vec3 p) {
    float dPlan=-dot(p,normalize(vec3(1.0,0.0,1.0)))+3.0;
    return max(abs(sdCappedCylinder(p-vec3(4.30,0.0,0.0),0.015,0.9))-0.002, -dPlan);   
}

vec2 GetDistAndMat(vec3 p) {
    p.x-=-1.5;
    float d=sdSyringeBody(p-vec3(0.0,0.0,0.0));
    float m=0.0;
    float time=mod(T,6.0);
    float atime = ANIME_PISTON;
    float l=1.5;
    float piston=sdPiston(p-vec3(-2.6+2.2*atime,0.0,0.0),l);
    d = min(d,piston);
    float graduations=sdGraduations(p);
    d = min(d,graduations);
    float joint = sdCappedCylinder(p-vec3(-2.6+2.0*l+2.2*atime,0.0,0.0),0.19,0.05)-0.03;
    if ( joint < d ) { d=joint; m=1.0; };
    float needleBase=sdNeedleBase(p);
    if ( needleBase < d ) { d=needleBase; m=2.0; };
    float needle=sdNeedle(p);
    if ( needle < d ) { d=needle; m=3.0; };
   	return vec2(d,m);
}

float GetDist(vec3 p) {
   	return GetDistAndMat(p).x;
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (0.8-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=0; i<24; i++ )
    {
		float h = GetDist( ro + rd*t );
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.2 );
        if( res<0.004 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);    
    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));    
    return normalize(n);
}

float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.11*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos );
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

// BigWings's template
// https://www.youtube.com/watch?v=PBxuVlp7nuM
// https://www.shadertoy.com/view/WtGXDD
vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(f,vec3(0,1,0))),
        u = cross(r,f),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec3 col = vec3(0);
    vec3 tcol = vec3(0);
    vec3 ro = vec3(0, 0.5, 4);
    if ( iMouse.x > 10.0 ) {
        vec2 m = iMouse.xy/iResolution.xy-0.5;
        ro.yz *= Rot(-m.y*3.14);
        ro.xz *= Rot(-m.x*6.2831);
    } else {
        float time=mod(T,12.0);
        float atime = S(3.0,6.0,time)-S(9.0,12.0,time);
        ro.z-=(2.0-atime*2.0);
        ro.xz *= Rot(-atime*6.28);
    }

#ifdef AA
	for (float dx = 0.; dx <= 1.; dx++)
		for (float dy = 0.; dy <= 1.; dy++) {
			vec2 uv = (fragCoord + vec2(dx, dy) * .5 - .5 * iResolution.xy) / iResolution.y;
#else
			vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
#endif
    vec3 bgcol=vec3(.1+.01*Hash21(uv))*(.5-abs(uv.y));
    col = bgcol;            
    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);

    vec3  sundir = normalize(vec3(2,1,1.5));
    vec3  sun_hal = normalize( sundir-rd );    
    vec3  sun_color = vec3(8.10,6.00,4.20);
    float d = RayMarch(ro, rd);    
    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;
    	vec3 n = GetNormal(p);        
        float m = GetDistAndMat(p).y;
        float ks = 1.0;
        // https://iquilezles.org/www/articles/outdoorslighting/outdoorslighting.htm
        // lighting , in these few lines there is all the magic thanks to IQ 
        // I'am just starting to understand how it works, sorry
        float occ = calcOcclusion( p, n ); // occusion has a big impact
        vec3  sun_lig = sundir;
        float sun_dif = clamp(dot( n, sun_lig ), 0.0, 1.0 );
        float sun_sha = calcSoftshadow( p, sun_lig, 0.02, 3.5 );
		float sun_spe = ks*pow(clamp(dot(n,sun_hal),0.0,1.0),8.0)*sun_dif*(0.04+0.96*pow(clamp(1.0+dot(sun_hal,rd),0.0,1.0),5.0));
		float sky_dif = sqrt(clamp( 0.5+0.5*n.y, 0.0, 1.0 ));
        float bou_dif = sqrt(clamp( 0.1-0.9*n.y, 0.0, 1.0 ))*clamp(1.0-0.1*p.y,0.0,1.0);
        vec3 c = vec3(0); // material color
        if ( m < 1.0 ) {
            // body is white
            c = vec3(0.15);
        } else if ( m < 2.0 ) {
            // joint is black
            c = vec3(0.01);
        } else if ( m < 3.0 ) {
            // needle base is blue
            c = vec3(0.01,0.01,0.2);
        } else if ( m < 4.0 ) {
            // needle is metallic
            c = vec3(0.1,0.1,0.1);
        }
  		vec3 lin = vec3(0.0);
        lin += sun_dif*sun_color*sun_sha*.7;
        lin += sky_dif*vec3(0.50,0.70,1.00)*occ*2.0;
        lin += bou_dif*vec3(0.40,1.00,0.40)*occ*2.0*1.3;
		col = c*lin;
		col += sun_spe*vec3(8.10,6.00,4.20)*sun_sha;

    }

    // inner liquid
    vec4 liquid = cylIntersect(ro,rd,vec3(-1.5,0.0,0.0), vec3(1.37,0.0,0.0), 0.23 );
    float d3 = liquid.x; //RayMarch3(ro, rd);    
    if(d3 > 0.0 && d3 < d ) {
