        float sun_spe = ks*pow(clamp(dot(n,sun_hal),0.0,1.0),8.0)*dif*(0.04+0.96*pow(clamp(1.0+dot(sun_hal,rd),0.0,1.0),5.0));   
        vec3 c = vec3(1)*.2;
    	col = mix(col,c,fre);  
