// Signed distance and gradient to a pie. Probably
// faster than central differences or automatic 
// differentiation/dual numbers.

// List of other 2D distances+gradients:
//
// https://iquilezles.org/www/articles/distgradfunctions2d/distgradfunctions2d.htm
//
// and
//
// https://www.shadertoy.com/playlist/M3dSRf

// .x = f(p)
// .y = f(p)/x
// .z = f(p)/y
// .yz = f(p) with f(p) = 1
// c is the sin/cos of the angle. r is the radius
vec3 sdgPie( in vec2 p, in vec2 c, in float r )
{
    float s = sign(p.x); p.x = abs(p.x);

    float l = length(p);
    float n = l - r;
    vec2  q = p - c*clamp(dot(p,c),0.0,r);
	float m = length(q)* sign(c.y*p.x-c.x*p.y);

    vec3  res = (n>m) ? vec3(n,p/l) : vec3(m,q/m);
    return vec3(res.x,s*res.y,res.z);
}

#define AA 2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

        // animation
        float t =            3.14*(0.5+0.5*cos(iTime*0.52));
        vec2  w = vec2(0.50,0.25)*(0.5+0.5*cos(iTime*vec2(1.1,1.3)+vec2(0.0,2.0)));

        // sdf(p) and gradient(sdf(p))
        vec3  dg = sdgPie(p,vec2(sin(t),cos(t)), 0.65);
        float d = dg.x;
        vec2  g = dg.yz;

        // central differenes based gradient, for comparison
        // g = vec2(dFdx(d),dFdy(d))/(2.0/iResolution.y);

        // coloring
        vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.4,0.7,0.85);
        col *= 1.0 + vec3(0.5*g,0.0);
      //col = vec3(0.5+0.5*g,1.0);
        col *= 1.0 - 0.7*exp(-8.0*abs(d));
        col *= 0.9 + 0.1*cos(150.0*d);
        col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

float createStar(vec2 vUv, float amplitude, float offset) {
    float mipi = 3.14 / 2.;
    float a = mipi - cos(iTime);
    float time = a * 80.;
    float speed = 0.6;
    float radius = .5;
    float size = 0.0001;
    float angle = atan(vUv.y, vUv.x);
    uint count = 5u;
    float starAngle = angle + offset;
    float disp = sin(starAngle*float(count)) * amplitude;
    float dist = length(vUv) + disp / time;
    float sigma = abs(dist-radius);
    return smoothstep(size, size+0.01, sigma);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;    

    vec3 col1 = vec3(0, 0, 0);
    vec3 col2 = vec3(0.5, 0.5, 0.5);
    vec3 colfinal = vec3(0., 0., 0.);
    float distBetween = 0.07;
    for (int i = 0; i < 9; i++) {
      float offset = float(i) * distBetween;
      colfinal += mix(col2, col1, createStar(uv, 7., offset));
    }
    fragColor = vec4(colfinal,1.0);
}

float rand(vec2 n) { return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453); }

float noise(vec2 n) {
    const vec2 d = vec2(0.0, 1.0);
    vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
    return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

vec2 rot(vec2 p, in float an ) { float cc = cos(an); float ss = sin(an); return mat2(cc,-ss,ss,cc)*p;}

float heart(vec2 uv, float noise) { return length(vec2(uv.x,uv.y*1.2 - pow(abs(uv.x) * 0.2, 0.6)) * noise); }

float hMap(vec2 uv) {
    float h = heart(uv, 1.);
    float r = -(smoothstep(0.2,0.205, h) - 1. 
    + (cos(iTime / 4.) * smoothstep(0.17,0.1705, h)) / 3. 
    + smoothstep(0.14,0.1405, h) / 3.) * 1.2;

    r -= min(smoothstep(0.14,0.145, heart(uv, noise(uv*80.))) / 5., r);
    return r;
}

vec3 hCol(vec2 uv) {
	vec2 ld = vec2(cos(iTime),sin(iTime)) * 0.004;
    float c = hMap(uv);
    float c2 = hMap(uv + ld);  
    float b = max(c2 - c, 0.)*10.;
    vec3 col = vec3(0.5,0.2,0.4)*pow(b, 3.)*.01;
    col += c2 * vec3(0.55, 0.2,.39) * 1.2;
    return col;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0*fragCoord-iResolution.xy)/min(iResolution.y,iResolution.x) / 1.5;
    vec2 p = uv * 8.;
    p.y += iTime * 4. + sin(p.x+p.y * 0.7 + iTime * .2) * 0.4;
    p.x +=  cos(p.y * 1.5) * normalize(p).x * 0.5;
    vec2 cp = rot(fract(p.xy) -.5,  iTime * 2.);
    p -= vec2(cos(iTime), sin(iTime)) ;
    vec2 clp = (fract(uv.xy + p.xy * 0.2 - length(uv)) -.5)  + vec2(cos(iTime), sin(iTime)) / 5.;
    float clip = smoothstep(0.5, 0., length(clp));
    uv *= 0.8;
    vec3 col = hCol(uv); 
    vec3 bc = vec3(0.3, 0.07, 0.2);
    col = max(max(bc * max(hMap(cp) / 3., hMap(uv)) * clip, col), 0.);
    col += bc * (0.3 - hMap(uv)) * 1.2;
    col += ((max(smoothstep(0.3, 0.05, length(uv )), 0.) ) / 20.) *  max(hMap(uv), 0.);
    col += length(uv * 0.3) / 4.;

    fragColor = vec4(col * 1.9 ,1.0);
}

uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
const uint max32 = 0xffffffffu;
vec2 fragCoord_;
vec2[8] gtable2 = vec2[](   //0.92387953 = cos(pi/8), 0.38268343 = cos(pi/8)
    vec2(0.92387953, 0.38268343),
    vec2(0.38268343, 0.92387953),
    vec2(-0.92387953, 0.38268343),
    vec2(-0.38268343, 0.92387953),
    vec2(0.92387953, -0.38268343),
