// SURFACE NETS  by Kastorp
//            proof of concept for the popular tesselation algorithm, which is an alternative to Marching cubes
//            inspired by these articles: 
//            https://bonsairobo.medium.com/smooth-voxel-mapping-a-technical-deep-dive-on-real-time-surface-nets-and-texturing-ef06d0f8ca14
//            https://0fps.net/2012/07/12/smooth-voxel-terrain-part-2/
//--------------------------------------------------------------------------------------


const float TK=.1; //edge thickness 
const float csz = .5 ;//+ 0.1 * sin(iTime); // Cell size
const bool snet=true; //true=smoothed, false=cubes
 bool cover=true;

 struct GRIDCELL{     //calculated for each cube in the following sequence:
   vec3 p[8];         //  1. cube vertex positions
   float val[8];      //  2. isosurface value at each cube vertex
   float nVertex;     //  3. number of vertex where val[]>0; if 0<nVertex<8  => surface cube
   float nEdge;       //  4. number of cube edges with crossing 
   vec3 m;            //  5. surface vertex, as an average of all crossing edge positions
   int dirs;          //  6. 6bit bitmask of surface edges from  each cube; 
                      //  7. quads (not calculated here)   
} ;



// the shape to be tesselated
float sdShape(in vec3 p) {
    p = p/ 4.;
    vec3 q = abs(p) - 1.35;
    float cube = max(q.x, max(q.y, q.z));
    float sphere = length(p) - 1.6875;
    float cylinder1 = length(p.yz) - 0.6;
    float cylinder2 = length(p.xz) - 0.6;
    float cylinder3 = length(p.xy) - 0.6;
    return max(max(cube, sphere), -min(cylinder1, min(cylinder2, cylinder3))) * 1.5;
}



const vec3 v[8] =vec3[8]
(
   vec3(0,0,0), vec3(1,0,0),vec3(1,1,0),vec3(0,1,0),
   vec3(0,0,1), vec3(1,0,1),vec3(1,1,1),vec3(0,1,1)
);

const int  e[24] =int[24](
   0,1,   1,2,  2,3,   3,0, 
   4,5,   5,6,  6,7,   7,4,   
   0,4,   1,5,  2,6,   3,7);

const vec3 dir[6] =vec3[6]
(
   vec3(1,0,0), vec3(0,1,0),vec3(0,0,1),
   vec3(-1,0,0), vec3(0,-1,0),vec3(0,0,-1)
);

const ivec2 quads[12]=ivec2[12]
( 
    ivec2(0,1),ivec2(0,2),ivec2(0,4),ivec2(0,5),
    ivec2(1,2),ivec2(1,3),ivec2(1,5),ivec2(2,3),
    ivec2(2,4),ivec2(3,4),ivec2(3,5),ivec2(4,5)
);

vec2 mmin(vec2 a, vec2 b) {return (a.x<b.x?a:b);}
int gFrame=0; 

vec3 pc=vec3(-1000.);


void  getSurface(vec3 c, inout GRIDCELL g)
{

    g.m=vec3(0.);
    g.nVertex=0.;
    g.nEdge=0.;
    g.dirs=0;

    //gFrame unrolling fails here...
    for(int i=0;i<8;i++)
    {

        //1. cube vertex positions
        vec3 vp=c+ csz* v[i];
        g.p[i]=vp;

        //  2. isosurface value at each cube vertex
        float val =sdShape(vp);
         g.val[i]=val;

        //3. number of vertex where val[]>0;
        g.nVertex+= (val<=0.?1.:0.);

    }

     if(g.nVertex>0. && g.nVertex<8.)
     {


          for(int i=gFrame;i<24;i+=2)
          {
              //  isosurface weights at each cube edge vertexes
              float d1 = g.val[e[i]],
                  d2 = g.val[e[i+1]],
                  d= d1/(d1-d2);

            //  4. number of cube edges with crossing 
             if(d1*d2<0.){
                 g.nEdge++;
                 g.m+= g.p[e[i+1]]*d + g.p[e[i]]*(1.-d);

                 for(int k =gFrame;k<6;k++) {

                     //  6. 6bit bitmask of surface edges from  each cube
                     if(dot((g.p[e[i+1]] +g.p[e[i]])/2.- c -csz/2. , dir[k] )<0. )  g.dirs=g.dirs | (1<<k); 

                 }
             }
          }
         if(snet){
            //  5. surface vertex, as an average of all crossing edge positions
             g.m/= g.nEdge;
             g.m = min(max(g.m,c),c+csz); //must be inside the cube
         }
         else g.m = c+csz/2.;



    }    
}

