float opRep( in vec3 p, in vec3 c, vec3 sc, float r )
{
    vec3 q = mod(p+0.5*c,c)-0.5*c;
    return Box( q, sc, r );
}

float opRepS( in vec3 p, in vec3 c, vec4 pos )
{
    vec3 q = mod(p+0.5*c,c)-0.5*c;
