    bp = p - vec3(0.0, 0.0, 6.0);
    object[2] = opRep(bp,  vec3(0.0, 0.0, 10.0), vec3(5.0, 1.0, 5.0), 0.2);
    objectm[2] = 3.0;

    bp = p - vec3(0.0, 10.0, 6.0);
    object[3] = opRep(bp,  vec3(0.0, 0.0, 10.0), vec3(5.0, 1.0, 5.0), 0.2);
    objectm[3] = 3.0;

    bp = p - vec3(-3.0, 0.0, 6.0);
    object[4] = Plane(bp,  vec3(1.0, 0.0, 0.0), 2.0);
    objectm[4] = 4.0;

    bp = p - vec3(30.0, 0.0, 6.0);
    object[5] = Plane(bp,  vec3(0.0, 1.0, 0.0), 5.0);
    objectm[5] = 5.0;

    object[5] += Noise3D(p*0.7)*0.5 - sin(p.x+iTime*2.0)*0.2-cos(p.z+iTime*2.0)*0.1+sin(p.y*3.0+iTime)*0.1;;


      if(sd < sd2){
          d.x = sd;
