vec3 RayMarch2(vec3 eye, vec3 viewRayDirection, vec2 uv){
    vec3 t = vec3(0.);
    float max = -100000.0;
    vec2 dd;
    float depth = 0.0, end = 10.0;
    for (int i = 0; i < 100; i++) {
        t.yz = GetDist(eye + t.x * viewRayDirection, uv).xy;


        if (abs(t.y) < 0.01)break;

        t.x += t.y;

        if (t.x >= 100.0)break;

    }
    if (t.x >= 100.0)t.x = -1.0;

    return t;


}


vec3 GetLightM(vec3 p, vec2 uv, vec3 lg, out vec3 n, vec3 ro, vec3 rd){
    vec3 lightpos = lg;
    //lightpos.xz += vec2(sin(iTime), cos(iTime));
    vec3 l = normalize(lightpos-p);

    vec2 d = GetDist(p, uv);
    vec2 e = vec2(0.01, 0);




    n = d.x - vec3(
        GetDist(p-e.xyy, uv).x,
        GetDist(p-e.yxy, uv).x,
        GetDist(p-e.yyx, uv).x);


    n = normalize(n);




    float dif = clamp(dot(n, l), .0, 1.);


    vec3 dd = RayMarch2(p+n*.01, l, uv);
    p = ro + reflect(n, l) * dd.x;



    if((dd.x < length(lightpos-p)) && (dd.x > 0.0)){
        if(dd.z == -1.0){
            vec3 colXZ = texture(iChannel0, p.xz*0.1).rgb;
             vec3 colYZ = texture(iChannel0, p.yz*0.1).rgb;
             vec3 colXY = texture(iChannel0, p.xy*0.1).rgb;

             n = abs(n);

             n *= pow(n, vec3(20));
             n /= n.x+n.y+n.z;

             vec3 col = colYZ * n.x + colXZ * n.y + colXY*n.z;

             uv = vec2(atan(p.x, p.z)/6.2832+.5, p.y/3.+0.5);
             vec4 st = texture(iChannel0, uv);

             col = vec3(dif) * mix(col, st.rgb, st.a);
             return col;

        }
        else if(dd.z == 2.0)
        {
            vec3 col = vec3(length(uv))*texture(iChannel2, vec2(noise(uv+iTime*10.0))).rgb;

            return col;
        }
        else if(dd.z == 3.0)
        {
            float NdotL = max( 0., dot( n, lightpos-p ) );
             float SpecularColor = 0.5;
            SpecularColor = SpecularColor + ( 1. - SpecularColor ) * pow( ( 1. - NdotL ), 5. );


             vec3 col = mix(vec3(dif) * vec3(0.5, 0.5, 0.5)+SpecularColor*0.001, 
                       vec3(0., 0.5, 5.0)*vec3(dif)+vec3(0.2,0.5, 0.5)+SpecularColor*0.001, 0.7 );
             col += normalize(vec3(0.1, 0.1, 0.1));
             col -= vec3(.1);
             return col;

        }
        else if(dd.z == 4.0){
             return texture(iChannel0, vec2(noise(uv+iTime*0.01))).rgb * vec3(dif);

         }
         else if(dd.z == 5.0){
             //return vec3(1.0, 0.0, 1.0) * vec3(dif);

             float NdotL = max( 0., dot( n, lightpos-p ) );
             float SpecularColor = 0.5;
            SpecularColor = SpecularColor + ( 1. - SpecularColor ) * pow( ( 1. - NdotL ),2. );

             vec3 col = vec3(0.0, 1.0, 1.0) + SpecularColor*0.0001;
             col = mix(col, vec3(dif) * vec3(0.0, 1.0, 1.0), smoothstep(0., 1.0, vec3(dif)));
             return col;


         }
        else
            return vec3(dif);

     }
     else
         return vec3(dif);

}
