             float SpecularColor = 0.5;
            SpecularColor = SpecularColor + ( 1. - SpecularColor ) * pow( ( 1. - NdotL ), 5. );

             col = mix(diff2 * vec3(0.5, 0.5, 0.5)+SpecularColor*0.001, 
                       vec3(0., 0.5, 5.0)*diff2+vec3(0.2,0.5, 0.5)*SpecularColor*0.0001, 0.7 );
             col += normalize(vec3(0.1, 0.1, 0.1));
             col =  texture(iChannel0, vec2(noise(uv+iTime*0.01))).rgb;

         }
         else if(t.z == 5.0){


             col = mix(XaterColor(p*0.1)*diff2*exp(-0.001*t.x), vec3(0.0, 1.0, 1.0), 0.4); 


         }

         vec2 j = uv*3.0;
         j.x += 0.0;
         j.y += .1;
         float sparkle = 1./dot(j,j);

         col += diff2*(sparkle*sin(mod(iTime*10.0, 3.1415))*0.01) ;





     }
     else
     {
         col = RayMarchCloud( ro, rd);

     }


    // Output to screen
    fragColor = vec4((col),alpha);
}

const vec3 lightDir=vec3(-0.57,0.57,0.57);


float distFuncCube(vec3 p){
	vec3 q=abs(p);
	return length(max(q-vec3(1.0,0.1,0.5),0.0));
}

float distFuncSphere(vec3 p){
	float s=0.5;
	return length(p)-s;
}


vec2 pmod(vec2 p,float n){
	float np=3.141592*2.0/n;
	float r=atan(p.x,p.y)-0.5*np;
	r=mod(r,np)-0.5*np;
	return length(p)*vec2(cos(r),sin(r));
}


float distFunc(vec3 p){
	p.xy=pmod(p.xy,6.0);
	p=mod(p,0.8)-0.4;
	float d3=distFuncCube(p);
	float d4=distFuncSphere(p);

	//return(d3);
	//return(d4);

	//return min(d3,d4);//OR

	//return max(d3,d4);//AND

	return max(d3,-d4);

	//return max(-d3,d4);

}


vec3 genNormal(vec3 p){
	float d=0.0001;
	return normalize(vec3(
		distFunc(p+vec3(d,0.0,0.0))-distFunc(p+vec3(-d,0.0,0.0)),
		distFunc(p+vec3(0.0,d,0.0))-distFunc(p+vec3(0.0,-d,0.0)),
		distFunc(p+vec3(0.0,0.0,d))-distFunc(p+vec3(0.0,0.0,-d))
		));
}


void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 p=(2.0*fragCoord.xy-iResolution.xy)/min(iResolution.x,iResolution.y);


	float radius=0.0;
	float phi=iTime*0.2;


	vec3 cPos=vec3(0.0,0.0,3.0-iTime*0.5);
	vec3 cDir=vec3(0.0,0.0,-1.0);
	vec3 cUp=vec3(0.0,1.0,0.0);
	vec3 cSide=cross(cDir,cUp);
	float targetDepth=1.0;

	vec3 ray=normalize(cSide*p.x+cUp*p.y+cDir*targetDepth);

	float tmp,dist;
	tmp=0.0;
	vec3 dPos=cPos;

	float emission=0.0;

	for(int i=0;i<48;i++){
		dist=distFunc(dPos);
		tmp+=dist;
		dPos=cPos+tmp*ray;
		emission+=exp(abs(dist)*-0.2);
	}

	vec3 color;

	if(abs(dist)<0.001){
		vec3 normal=genNormal(dPos);
		float diff=clamp(dot(lightDir,normal),0.1,1.0);
		color=0.02*emission*vec3(1.0,sin(iTime),1.0)*diff;
	}else{
		color=vec3(0.0);
	}
	fragColor=vec4(color,1.0);
}

#define PI (atan(1.)*4.)
#define TAU atan(1.)*8.

void lookAt(inout vec3 rd,vec3 ro,vec3 ta,vec3 up){
    vec3 w=normalize(ta-ro),u=normalize(cross(w,up));
    rd=rd.x*u+rd.y*cross(u,w)+rd.z*w;
}

void rot(inout vec3 p,vec3 a,float t){
	a=normalize(a);
	vec3 u=cross(a,p),v=cross(a,u);
	p=u*sin(t)+v*cos(t)+a*dot(a,p);   
}

float map(vec3 p){
	rot(p,vec3(cos(iTime*.3),sin(iTime*.5),.5*sin(iTime*.2)),iTime*.7);
    float r1=.7;
    float r2=.3;
    float r3=.05;
    float n1=1.;
    float n2=.6;
    //float a=.3;
    float a=.5;//floor(30.*fract(iTime*.5))*.1;
    float b=.1;
    p.zx=vec2(atan(p.z,p.x)/PI*n1, length(p.zx)-r1);
    p.xy=vec2(atan(p.x,p.y)/PI*n2, length(p.xy)-r2);
    p.x+=a*p.z;
    p.x=mod(p.x,b*2.)-b;
    return length(p.xy)-r3;
}


vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<70;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

vec3 doColor(vec3 p)
{
    return vec3(.7,.5,.3)+cos(p*2.)*.5+.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0,3);
    vec3 rd = normalize(vec3(uv,3));
    vec3 ta =vec3(0);
    lookAt(rd,ro,ta,vec3(0,1,0));    
    vec3 col= vec3(0);
	const float maxd=50.;
    float t=march(ro,rd,0.,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=doColor(p); 
        vec3 n=calcNormal(p);      
		vec3 lightPos=ro+vec3(2,5,2);
    	vec3 li=lightPos-p;
		float len=length(li);
		li/=len;
		float dif=clamp(dot(n,li),0.,1.);
        col*=max(dif,0.);
        float rimd=pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn=rimd+2.2*(1.-rimd);
    	col*=frn*.8;
        col*=max(.5+.5*n.y,.1);
        col*=exp2(-2.*pow(max(0.,1.-map(p+n*.8)/.8),2.));
        col+=vec3(.8,.6,.2)*pow(clamp(dot(reflect(rd,n),li),0.,1.),10.);
    }
    col=pow(col,vec3(1./2.2));
    fragColor.xyz=col;
}

float noise(float a) {
    return fract(sin(a) * 1337.1337 + 7.2718);
}

float noise2(vec2 a) {
    return noise(dot(a, vec2(3.91239, 7.139938)));
}


void pattern(vec2 uv, out vec4 fragColor) {
    float r = noise2(uv*31. + vec2(sin(iTime/10000.0), 0));
    float g = noise2(uv*17. + cos(iTime/10000.) * 7.5 * noise2(uv*3.14 + vec2(2.5, 0.3)));
    float b = noise2(uv*51. + cos(iTime/3959.) *
        noise2(uv * uv * 13.5
         + noise2(uv * uv * uv * 7. + vec2(6.9, 0.25))));
    fragColor = vec4(
        r * smoothstep(1., (sin(iTime)+0.9)/2., fwidth(r)),
        g * smoothstep(1., (cos(iTime+0.5)+0.9)/2., fwidth(g)),
        b * smoothstep(tan(iTime), 1., fwidth(b)),
        //g*b,
        1.0
    );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    pattern(uv, fragColor);
}

