// http://iquilezles.org/www/articles/smin/smin.htm
vec2 smin( vec2 a, vec2 b, float k )
{
    float h = clamp( 0.5+0.5*(b.x-a.x)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// http://iquilezles.org/www/articles/smin/smin.htm
float smax( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*0.25/k;
}
vec2 cactus (vec3 pos){
    //add cactus
    vec2 uv4 = vec2(atan(pos.x,pos.z)/(3.14159*1.),pos.y/1.);
    float disp4 = texture(iChannel1,uv4).r;
    vec2 cactus = sdCapsule(pos, vec3(10,0,0), vec3(10,1,0), .2 );
    vec2 thorn = sdCapsule(pos, vec3(10,0,0), vec3(10,1,0), .14 );
    thorn.x-=disp4*.1;
    thorn.y = 17.;
    cactus = opU(cactus,thorn);

    vec3 nP = pos;
    nP.y += (sin(nP.x*4.) * .1 * smoothstep(1.1,1.2,nP.x));
    vec2 cactusR = sdCapsule(nP, vec3(10,.5,0), vec3(10.5,.5,0), .13 );
    vec2 thornR = sdCapsule(nP, vec3(10,.5,0), vec3(10.5,.5,0), .08 );

    thornR.x-=disp4*.1;
    thornR.y = 17.;
    cactusR = opU(cactusR,thornR);
    cactus.x = smin(cactusR,cactus, .1).x;
    return cactus;
}
