    vec2 gyroid = sdGyroid(pos-vec3(10.,-3.,5.), 0.3, 1., .8);

    //add floor to scene
    vec2 floorP = vec2(pos.y,20.);
    //apply rocky displacement to floor
    floorP.x-=disp2*.1;
    res = opU(floorP,res);

    //add gyriod to scene
    res = opU(gyroid,res);
    for(int i = 0; i < 2; i++){
        for(int k = 0; k < 2; k++){
            vec3 nP = pos;
            nP.xz *= Rot(float(k)*3.1415);
            res = opU(cactus(abs(nP-vec3(i+k*10,0,k+i*10))),res);
        }
    }


    //add flag
    vec2 pole = sdCylinder(pos, vec2(.01,1.4) );
    res = opU(pole,res);
    //add cloth
    pos.x += (sin(pos.z + iTime*5.)*.1) * smoothstep(.1,1.1,pos.z);
    vec2 cloth = sdBox( pos-vec3(0,1.1,0.5), vec3(.01,.3,.5) );
    //cloth.x -=disp3*.03;
    res = opU(cloth,res);
    return res;
}

// http://iquilezles.org/www/articles/boxfunctions/boxfunctions.htm
vec2 iBox( in vec3 ro, in vec3 rd, in vec3 rad ) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
	return vec2( max( max( t1.x, t1.y ), t1.z ),
	             min( min( t2.x, t2.y ), t2.z ) );
}

vec2 raycast( in vec3 ro, in vec3 rd )
{

    // raymarch primitives   
    // distance from origin
    float dO=0.;
    float ID = -1.;
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO*rd;

        // get distance to seam
        vec2 getObj = map(p);
        ID = getObj.y;
        float ds = getObj.x;
        //move origin to new point
        if(ID == 15.){
            dO+=ds*.1;
        }
        if(ID == 17.){
            dO+=ds*.8;
        }else{
            dO+=ds;
        }
        if(ds < SURFACE_DIST || dO > MAX_DIST){
            break;
        }
    }
    return vec2(min(dO, MAX_DIST),ID);
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (0.8-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=ZERO; i<24; i++ )
    {
		float h = map( ro + rd*t ).x;
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.2 );
        if( res<0.004 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
#if 0
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
#else
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.0005*e).x;
      //if( n.x+n.y+n.z>100.0 ) break;
    }
    return normalize(n);
#endif    
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

// http://iquilezles.org/www/articles/checkerfiltering/checkerfiltering.htm
float checkersGradBox( in vec2 p, in vec2 dpdx, in vec2 dpdy )
{
    // filter kernel
    vec2 w = abs(dpdx)+abs(dpdy) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    // xor pattern
    return 0.5 - 0.5*i.x*i.y;                  
}

vec3 render( in vec3 ro, in vec3 rd, in vec3 rdx, in vec3 rdy )
{ 
    // background
    vec3 col = vec3(0.7, 0.7, 0.9) - max(rd.y,0.0)*0.3;

    // raycast scene
    vec2 res = raycast(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = (m<1.5) ? vec3(0.0,1.0,0.0) : calcNormal( pos );
        vec3 ref = reflect( rd, nor );
        float ks = .3;
        // material        
        if(m == 1.){
            col = vec3(1,0,0);
            float ks = .1;
        }
        else if( m==20.)
        {
            // project pixel footprint into the plane
            /*vec3 dpdx = ro.y*(rd/rd.y-rdx/rdx.y);
            vec3 dpdy = ro.y*(rd/rd.y-rdy/rdy.y);

            float f = checkersGradBox( 3.0*pos.xz, 3.0*dpdx.xz, 3.0*dpdy.xz );
            col = 0.15 + f*vec3(0.05);*/
            col = vec3(0.4,.1,0);
            ks = .1;
        }
        else if( m==15.){
            col = vec3(0.4,.1,0);
            ks = .1; 
        }
         else if( m==16.){
            col = vec3(0,.1,0);
            ks = .1; 
        }
        else if( m==21.){
            col = vec3(1,1,1);
            ks = 5.; 
        }
        // lighting
        float occ = calcAO( pos, nor );

		vec3 lin = vec3(0.0);

        // sun
        {
            vec3  lig = normalize( vec3(0.6, 0.7, -0.9) );
            vec3  hal = normalize( lig-rd );
            float dif = clamp( dot( nor, lig ), 1.0, 1.0 );
          //if( dif>0.0001 )
        	      dif *= calcSoftshadow( pos, lig, 0.02, 2.5 );
			float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),16.0);
                  spe *= dif;
                  spe *= 0.04+0.96*pow(clamp(1.0-dot(hal,lig),0.0,1.0),5.0);
            lin += col*2.20*dif*vec3(1.30,1.00,0.70);
            lin +=     5.00*spe*vec3(1.30,1.00,0.70)*ks;
        }
        // sky
        {
            float dif = sqrt(clamp( 0.5+0.5*nor.y, 0.0, 1.0 ));
                  dif *= occ;
            float spe = smoothstep( -0.2, 0.2, ref.y );
                  spe *= dif;
                  spe *= 0.04+0.96*pow(clamp(1.0+dot(nor,rd),0.0,1.0), 5.0 );
          //if( spe>0.001 )
                  spe *= calcSoftshadow( pos, ref, 0.02, 2.5 );
            lin += col*0.60*dif*vec3(0.40,0.60,1.15);
            lin +=     2.00*spe*vec3(0.40,0.60,1.30)*ks;
        }
        // back
        {
        	float dif = clamp( dot( nor, normalize(vec3(0.5,0.0,0.6))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
                  dif *= occ;
        	lin += col*0.55*dif*vec3(0.25,0.25,0.25);
        }
        // sss
        {
            float dif = pow(clamp(1.0+dot(nor,rd),0.0,1.0),2.0);
                  dif *= occ;
        	lin += col*0.25*dif*vec3(1.00,1.00,1.00);
        }

		col = lin;

        col = mix( col, vec3(0.7,0.7,0.9), 1.0-exp( -0.0001*t*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 32.0 + iTime*1.5;

    // camera	
    vec3 ta = vec3( 0.5, -0.5, -0.6 );
    vec3 ro = ta + vec3( 4.5*cos(0.1*time + 7.0*mo.x), 1.3 + 2.0*mo.y, 4.5*sin(0.1*time + 7.0*mo.x) );
    // camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );

    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif

        // ray direction
        vec3 rd = ca * normalize( vec3(p,2.5) );

         // ray differentials
        vec2 px = (2.0*(fragCoord+vec2(1.0,0.0))-iResolution.xy)/iResolution.y;
        vec2 py = (2.0*(fragCoord+vec2(0.0,1.0))-iResolution.xy)/iResolution.y;
        vec3 rdx = ca * normalize( vec3(px,2.5) );
        vec3 rdy = ca * normalize( vec3(py,2.5) );

        // render	
        vec3 col = render( ro, rd, rdx, rdy );

        // gain
        // col = col*3.0/(2.5+col);

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    fragColor = vec4( tot, 1.0 );
}

uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
const uint max32 = 0xffffffffu;
vec2 fragCoord_;  //set as a uniform variable
vec3[12] gtable3 = vec3[](
    vec3(1.0, 1.0, 0.0),
    vec3(1.0, -1.0, 0.0),
    vec3(-1.0, 1.0, 0.0),
    vec3(-1.0, -1.0, 0.0),
    vec3(1.0, 0.0, 1.0),
    vec3(1.0, 0.0, -1.0),
    vec3(-1.0, 0.0, 1.0),
    vec3(-1.0, 0.0, -1.0),
    vec3(0.0, 1.0, 1.0),
    vec3(0.0, 1.0, -1.0),
    vec3(0.0, -1.0, 1.0),
    vec3(0.0, -1.0, -1.0)
);
vec2[8] gtable2 = vec2[](   //0.92387953 = cos(pi/8), 0.38268343 = cos(pi/8)
    vec2(0.92387953, 0.38268343),
    vec2(0.38268343, 0.92387953),
    vec2(-0.92387953, 0.38268343),
    vec2(-0.38268343, 0.92387953),
    vec2(0.92387953, -0.38268343),
    vec2(0.38268343, -0.92387953),
    vec2(-0.92387953, -0.38268343),
    vec2(-0.38268343, -0.92387953)
);
uint hash11u(uint n) {
    n ^= (n << 24);
    n ^= (n >> 1);
    n ^= (n << 1);
    return n * k.x;
}
uvec2 hash22u(uvec2 n) {
    n ^= (n.yx << 24);
    n ^= (n.yx >> 1);
    n *= k.xy;
    n ^= (n.yx << 1);
    return n * k.xy;
}
vec2 hash22(vec2 p) {
    if(fragCoord_.x < 0.5 * iResolution.x){
        //down-left
        uvec2 n = uvec2(p);
        vec2 v = vec2(hash22u(n)) / vec2(max32);
        return normalize(2.0 * v - vec2(1.0));
    } else {
        //down-right

        uint n = hash11u(hash11u(uint(p.x)) + uint(p.y));
        n = n >> 29;
        n = n % 8u;
        return gtable2[n];
    }
}
uvec3 hash33u(uvec3 n) {
    n ^= (n.yzx << 24);
    n ^= (n.yzx >> 1);
    n *= k;
    n ^= (n.yzx << 1);
    return n * k;
}
vec3 hash33(vec3 p) {
    if(fragCoord_.x < 0.5 * iResolution.x){
        //up-left
        uvec3 n = uvec3(p);
        vec3 v = vec3(hash33u(n)) / vec3(max32);
        return normalize(2.0 * v - vec3(1.0));
    } else {
        //up-right
        uint n = hash11u(hash11u(hash11u(uint(p.x)) + uint(p.y)) + uint(p.z));
        n = n >> 28;
        n = n % 12u;
        return gtable3[n] * 0.70710678; //0.70710678 = 1/sqrt(2)
    }
}
float gnoise21(vec2 p) {
    vec2 f = fract(p);
    vec2 g00 = hash22(p);
    vec2 g01 = hash22(p + vec2(0.0, 1.0));
    vec2 g10 = hash22(p + vec2(1.0, 0.0));
    vec2 g11 = hash22(p + vec2(1.0, 1.0));
    float v00 = dot(g00, f);
    float v01 = dot(g01, f - vec2(0.0, 1.0));
    float v10 = dot(g10, f - vec2(1.0, 0.0));
    float v11 = dot(g11, f - vec2(1.0, 1.0));
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f); 
    float v = mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y);
    return 0.5 * v + 0.5;
}
float gnoise31(vec3 p) {
    vec3 f = fract(p);
    vec3 g000 = hash33(p);
    vec3 g001 = hash33(p + vec3(0.0, 0.0, 1.0));
    vec3 g010 = hash33(p + vec3(0.0, 1.0, 0.0));
    vec3 g011 = hash33(p + vec3(0.0, 1.0, 1.0));
    vec3 g100 = hash33(p + vec3(1.0, 0.0, 0.0));
    vec3 g101 = hash33(p + vec3(1.0, 0.0, 1.0));
    vec3 g110 = hash33(p + vec3(1.0, 1.0, 0.0));
    vec3 g111 = hash33(p + vec3(1.0, 1.0, 1.0));

    float v000 = dot(g000, f);
    float v001 = dot(g001, f - vec3(0.0, 0.0, 1.0));
    float v010 = dot(g010, f - vec3(0.0, 1.0, 0.0));
    float v011 = dot(g011, f - vec3(0.0, 1.0, 1.0));
    float v100 = dot(g100, f - vec3(1.0, 0.0, 0.0));
    float v101 = dot(g101, f - vec3(1.0, 0.0, 1.0));
    float v110 = dot(g110, f - vec3(1.0, 1.0, 0.0));
    float v111 = dot(g111, f - vec3(1.0, 1.0, 1.0));
     f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f); 
    float xy0 = mix(mix(v000, v100, f.x), mix(v010, v110, f.x), f.y);
    float xy1 = mix(mix(v001, v101, f.x), mix(v011, v111, f.x), f.y);
    return 0.5 * mix(xy0, xy1, f.z) + 0.5;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    fragCoord_ = fragCoord;
    vec2 pos = fragCoord.xy / min(iResolution.x, iResolution.y);
    pos *= 20.0;
    pos += iTime;
    float v;
    if(fragCoord.y < 0.5 * iResolution.y){
        v = gnoise21(pos);  //down
    } else {
        v = gnoise31(vec3(pos, iTime));   //up 
    }
    fragColor = vec4(vec3(v), 1.0);
}

vec2 N22(vec2 p){
    vec3 a = fract(p.xyx*vec3(123.34, 234.34, 345.65));
