 * @src https://thebookofshaders.com/13/
 */
float fbm ( in vec2 _st, in int octaves) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < octaves; ++i) {
        v += a * noise(_st, 2000. + abs(1000. * sin(iTime * 0.0009)));
        _st = rot * _st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

/**
 * HSB to RGB
 * @src https://thebookofshaders.com/06/
 */
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0);
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}

/** 
 * Genuary 01
 * @main
 */
void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy*1.;
    vec3 color = vec3(0.0);

    vec2 r = vec2(0.);
    r.x = fbm(st  + 0.10, 1);

    float f = fbm(st+r, 10);
    f = f* 0.5 + fbm(vec2(f *0.5), 2);
    f = f* 0.5 + fbm(vec2(f *0.5), 2);

    float t = smoothstep(0., 1., abs(map(fract(iTime * .075), 0., 1., -1., 1.)));

    color = hsb2rgb(mix(vec3(1,1,1),
                mix(
                    vec3(0.716,0.725, 0.75 + 0.25 * sin(iTime * 0.1)),
                    vec3(0.991,0.625, 0.75 + 0.25 * sin(iTime * 0.1)),
                    t * 0.5 + st.y * 0.5
                ),
                clamp((f*f)*4.0,0.0,1.0)));


    fragColor = vec4((f*f*f+.3*f*f+.8*f)*color,1.);
}

