    float lb = mix(0.5, 1.0, sin(iTime)); //0.5; //intercept    
    vec2 P = vec2(uv.x, uv.y);
    //float dist = dfLine(vec2(0.0, lb), vec2(1.0, lm),  vec2(uv.x,uv.y));
    float dist = dfLine(vec2(0.0, lb), vec2(1.0, lm),  P);

    //vec3  color  = dist * vec3(1.0); 
