#define PI acos(-1.)
#define ITER 64.

#define od(p,s) (dot(p,normalize(sign(p)))-s)
#define mo(p,d)p=abs(p)-d;if(p.y>p.x)p=p.yx
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define palette(t,c,d)(vec3(0.5)+vec3(0.5)*cos(2.*PI*(c*t+d)))

#define time iTime
#define dt(speed) fract(time*speed)
#define anim (dt(1./3.)*252.)

struct obj
{
  float d;
  vec3 shadowcol; 
  vec3 lightcol;
};

obj minobj (obj a, obj b)
{
  if (a.d < b.d) return a;
  else return b;
}

// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

float pulseheart (vec2 uv)
{
  uv.y -= 0.1;
  uv.x = -abs(uv.x);
  uv *= rot(PI/6.+uv.y*0.2);
  mo(uv,vec2(0.01,0.5));

  return clamp(smoothstep(0.6,0.8,texture(iChannel1,vec2(uv.x,0.025)).r),0.,1.);
}

float box (vec3 p, vec3 c)
{
  vec3 q = abs(p)-c;
  return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

float roadwidth = 20.;
obj asphalt (vec3 p)
{
  float ad = box(p,vec3(roadwidth,0.15,1e10));
  return obj(ad,vec3(0.01,0.01,0.05),vec3(0.01,0.01,0.08)*(1.-abs(p.x*0.04))*2.);
}

obj roadlines (vec3 p)
{
  vec3 pp = p;
  p.y -= 0.5;
  p.x = abs(p.x)-roadwidth;
  float rld = box(p,vec3(1.,0.05,1e10));

  float per = 8.;
  p.z = mod(p.z,per)-per*0.5;
  p.x += roadwidth/2.;
  rld = min(rld,box(p,vec3(.5,0.05,2.5)));

  p.x += roadwidth/2.;
  rld = min(rld,box(p,vec3(.5,0.05,1.5)));

  return obj(rld, vec3(0.01,0.01,0.04),floor(palette(pp.z,vec3(0.02),vec3(0.,0.37,0.63))*8.)/8.);
}

obj road (vec3 p)
{
  p.y += 0.2;
  p.y += cos(p.x*0.15);
  return minobj(asphalt(p),roadlines(p));
}

obj field (vec3 p)
{
  float fd = p.y+3.4+(floor(texNoise(p.xz*0.001).x*25.)/8.)*10.;
  fd = max(-box(p,vec3(roadwidth*1.5,10.,1e10)),fd);  
  return obj(fd,vec3(0.1,0.0,0.3),vec3(0.1,0.9,0.9));
}

float poleper = 25.;
obj poles (vec3 p)
{
  p.y -= 5.;

  p.x = abs(p.x)-(roadwidth+1.5);
  vec3 cp = vec3(abs(p.x)-1.5,p.y+sqrt(abs(sin((p.z+(poleper/2.))*(PI/poleper))*2.)),p.z);
  float pd = cyl(cp-vec3(0.,8.5,0.),0.15,1e10);

  p.z = mod(p.z,poleper)-poleper*0.5;
  pd = min(pd,cyl(p.xzy,0.5, 10.));
  pd = min(pd,cyl(p.yzx-vec3(8.5,0.,0.), 0.2,2.5));
  return obj(pd,vec3(0.01,0.0,0.02),vec3(0.,0.,0.1)*p.y*0.2);
}

obj mountains (vec3 p)
{
    p.z -= 170.+anim; 

    float sy = 1.8;
    float sx = 1./sy;
    vec3 size = vec3(sx,sy,sx);

    p.y += 10.;
    float md = od(p*size+vec3(115.,0.,0.),30.);
    p.x = abs(p.x)-120.;
    p.xz *= rot(-PI/6.);

    md = min(md,od(p*size,20.));
    return obj(md/length(size),vec3(0.,0.,0.01),vec3(0.,0.02,0.07));
}

obj SDF (vec3 p)
{
  obj M = mountains(p);

  p.x += sin(p.z*0.05)*3.;

  obj scene = road(p);
  scene = minobj(scene,field(p));
  scene = minobj(scene,poles(p));
  scene = minobj(scene,M);

  return scene;
}

vec3 getnorm (vec3 p)
{
  vec2 eps = vec2(0.01,0.);
  return normalize(SDF(p).d-vec3(SDF(p-eps.xyy).d,SDF(p-eps.yxy).d,SDF(p-eps.yyx).d));
}

vec3 getcam (vec3 ro, vec3 tar, vec2 uv)
{
  vec3 f = normalize(tar-ro);
  vec3 l = normalize(cross(vec3(0.,1.,0.),f));
  vec3 u = normalize(cross(f,l));
  return normalize(f+l*uv.x + u*uv.y);
}

float point_light (vec3 p, vec3 n, vec3 lpos)
{
  vec3 ldir = normalize(lpos-p);
  float ldist = length(lpos-p);
  float dotNL = max(dot(n , ldir),0.);
  return clamp((dotNL) / (0.0004 * ldist * ldist),0.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

  vec2 uu = uv-vec2(0.,0.);
  uv *= (1.-pulseheart(uu)*0.7);

  vec3 ro=vec3(1.2,11.,-3.+anim), 
  rd=getcam(ro,vec3(0.,4.,5.+anim),uv), 
  p=ro, 
  col=vec3(0.), 
  l=normalize(vec3(-1.,5.,-5.));

  bool hit=false; 
  float shad;
  obj O;

  for(float i=0.;i<ITER;i++)
  {
    O = SDF(p);
    if (O.d<0.001)
    {
      hit=true;shad=i/ITER;break;
    }
    p += O.d*rd;
  }

  float t = length(ro-p);
  if (hit)
  {
    vec3 n = getnorm(p);
    float lighting = max(dot(n,l),0.);
    col = mix(O.shadowcol, O.lightcol, point_light(p,n,vec3(0.,3.,10.+anim))*1.5);
  }

  col = mix(col,vec3(0.1,length(uv)*0.2,0.4),1.-exp(-0.00004*t*t));
  col = mix(col,1.-col,pulseheart(uu));

  fragColor = vec4(sqrt(clamp(col,0.,1.)),1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    float t;

    for(int c=0;c<3;c++){
	    vec2 uv = (-fragCoord*5.0-iResolution.xy)/iResolution.y/10.0;
        uv += vec2(iTime/2.0,iTime/3.0)/8.0;
        t = iTime+float(c)/10.;
        float scale = 5.0;
        float scale1 = 1.5;
        float scale2 = 2.0;
        for(int i=0;i<3;i++)
        {
            uv=fract(uv/(scale2+fract((uv.x+uv.y)*2.5))-(uv.yx/(scale2+(fract(uv.x+uv.y))))/scale)*scale/1.5+scale1*scale;
            uv /= scale1;
        	uv=uv.yx+col.xy;
        }
     col[c] = ((uv.x)-(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}

