void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = q - 0.5;
	p.x*=iResolution.x/iResolution.y;

    vec3 ro = vec3(0,0,-6.7);
    vec3 rd = normalize(vec3(p,1.3));
    vec2 mo = iMouse.xy / iResolution.xy-.5;
    mo = (mo==vec2(-.5))?mo=vec2(-0.1,0.1):mo;
	mo.x *= iResolution.x/iResolution.y;
    rd.yz *= mm2(mo.y);
    rd.xz *= mm2(mo.x + sin(time*0.05)*0.2);

    vec3 col = vec3(0.);
    vec3 brd = rd;
    float fade = ss(0.,0.01,abs(brd.y))*0.1+0.9;

    col = bg(rd)*fade;

    if (rd.y > 0.){
        vec4 aur = ss(0.,1.5,aurora(ro,rd))*fade;
        col += stars(rd)*ss(0.0,0.5,brd.y);
        col = col*(1.-aur.a) + aur.rgb;
    }

    else         //Reflections
    {
        rd.y = abs(rd.y);
        col = bg(rd)*fade*0.6;
        vec4 aur = smoothstep(0.0,2.5,aurora(ro,rd));
        col += stars(rd)*0.1;
        col = col*(1.-aur.a) + aur.rgb;
        vec3 pos = ro + ((0.5-ro.y)/rd.y)*rd;
        float nz2 = triNoise2d(pos.xz*vec2(.5,.7), 0.);
        col += mix(vec3(0.2,0.25,0.5)*0.08,vec3(0.3,0.3,0.5)*0.7, nz2*0.4);
    }


	fragColor = vec4(col, 1.);
}

// Created by Ramon Viladomat

#define PI 3.1415
#define STEP_REDUCTION  0.8
#define NUM_ITERATIONS  150
#define NUM_REFLECTIONS 2

vec3 lightDir 	= normalize(vec3(-0.5,2.0,-1.0));
vec3 lightColor = vec3(1.0);

// MORPHOLOGY
//--------------------------------------------------------------------------
float hash(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

//--------------------------------------------------------------------------
float smin(float a, float b, float k)
{
	return log2(exp2(k*a)+exp2(k*b))/k;
}

//--------------------------------------------------------------------------
vec2 mapUbi( in vec3 p )
{  
    //global mask
    float mask = max(length(p)-0.96,-p.z);

    //spheres
    float cl = abs(length(p)-0.9)-0.06;
    float c2 = abs(length(p + vec3(0.0,0.105,0.0))-0.37)-0.034;
    float c3 = abs(length(p + vec3(-0.07 ,0.18,0.0))-0.223)-0.028;
    float c4 = length(p + vec3(-0.075,0.12,0.0))-0.14;

    //Arc
    vec2 arcpos = p.xy*vec2(1.0,0.94) + vec2(mix(0.0,-0.1,smoothstep(0.5,-1.0,p.y)),0.0);
    float arcplus  = length(arcpos + vec2(-0.165,0.08))- 0.61; 
    vec2 arcpos2 = p.xy * vec2(1.0,mix(1.0,0.8,smoothstep(0.0,-0.4,p.y)));
    float arcminus = length(arcpos2 + vec2(-0.15,0.13))- 0.53;

    float arc = max(arcplus,-arcminus);

    float logobg = -p.z + 0.1;

    //mix shapes
    float dist = min(min(cl,c2),min(c3,c4));
    dist = min(arc,dist);

    dist = max(smin(dist,logobg,-50.0),mask);

    return vec2(dist,0.0);
}

//--------------------------------------------------------------------------
vec2 mapLogoBox( in vec3 p)
{
   float box     = length(max(abs(p-vec3(0.0,0.0,0.65))-vec3(1.0,1.4,0.5),0.0))-0.2;
   box           = max(box,-(length(p)-0.96)); //Hole
   return vec2(box,1.0);
}

//--------------------------------------------------------------------------
float signedBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

//--------------------------------------------------------------------------
vec2 mapServers( in vec3 p)
{
    float wall    = -p.z+0.5;       

    float bottom  = length(max(abs(vec3(p.x,abs(p.y-0.7),p.z)-vec3(0.0,7.0,0.6))-vec3(50.0,3.0,0.5),0.0))-0.05;
    wall          = min(wall,bottom);

    vec3 lightpos = p; 
    lightpos.xy   = mod(p.xy,0.3);

    float light   = length(lightpos-vec3(0.15,0.15,0.5))-0.03;

    vec2 ret = vec2(wall,1.0); 
    if ( light < wall ) ret = vec2(light,2.0);

    //Holes
    vec3 holePos  = p; 
    holePos.x     = mod(holePos.x,5.0);
    holePos       -= vec3(2.5,0.0,0.0);
    float hole    = signedBox(holePos,vec3(0.3,10.0,3.0));

    holePos.x     = abs(holePos.x);    
    ret.x         = max(ret.x,-hole);

    float border  = length(max(abs(holePos-vec3(0.3,0.0,10.3))-vec3(0.0,10.0,10.0),0.0))-0.05;

    if (ret.x > border ) ret = vec2(border,3.0);

    return ret;
}

//--------------------------------------------------------------------------
vec2 mapRoom( in vec3 p )
{
    float ret    = p.z+40.0;
    ret          = min(ret,p.x+22.8);
    ret          = min(ret,-p.x+22.8);
    ret          = min(ret,p.y+5.0);
    ret          = min(ret,-p.y+7.0);   

    return vec2(ret,1.0);
}

//--------------------------------------------------------------------------
vec2 map( in vec3 p )
{    
   vec2 ret     = mapUbi(p);
   vec2 box     = mapLogoBox(p);
   vec2 servers = mapServers(p);
   vec2 room    = mapRoom(p);

   if (ret.x > box.x) ret = box;
   if (ret.x > servers.x) ret = servers; 
   if (ret.x > room.x) ret = room; 
   return ret;
}

//--------------------------------------------------------------------------
vec2 intersect( in vec3 ro, in vec3 rd )
{
	float maxd = 100.0;
	float precis = 0.001;
    float h=precis*2.0;
    float t = 0.0;
    float m = -1.0;
    for( int i=0; i<NUM_ITERATIONS; i++ )
    {
        if( abs(h)<precis||t>maxd ) continue;
        t += h*STEP_REDUCTION;
	    vec2 res = map( ro+rd*t );
        h = res.x;
	    m = res.y;
    }

    if( t>maxd ) m=-1.0;
    return vec2( t, m );
}

// MATERIALS
//--------------------------------------------------------------------------
vec4 calcColorUbi( in vec3 pos )
{
    vec3 colorBack = vec3(0.2);

    vec3 colorA = vec3(0.2);
    vec3 colorB = vec3(0.05,0.05,0.2);
    vec3 ubicolor = mix(colorA,colorB,clamp(10.0*abs(pos.z),0.0,1.0));
    ubicolor = mix(colorBack,ubicolor,step(length(pos),0.96));
	return vec4(ubicolor,0.2);
}

//--------------------------------------------------------------------------
vec4 calcColorLed( in vec3 pos)
{
    vec2 position = vec2(float(int((pos.x+40.0)/0.3)),float(int((pos.y+40.0)/0.3)));
    float rand = hash(position);

    float led = clamp(sin(20.0*(iTime+10.0)*rand),0.0,1.0);

    float colorChose = rand*4.0; 
    if (colorChose < 2.9) return led*vec4(1.0,0.0,0.0,0.0);
    if (colorChose < 3.8) return led*vec4(0.1,1.0,0.0,0.0);

    return led*vec4(0.0,0.0,1.0,0.0);
}

//--------------------------------------------------------------------------

float distToLine( in vec2 pos, in vec2 origin, in vec2 end )
{
    vec2 dir     = end - origin;
    vec2 toPos   = pos - origin; 
    vec2 nDir    = normalize(dir);
    vec2 projPos = origin+clamp(dot(toPos,nDir),0.0,length(dir))*nDir;
    return length(pos-projPos);
}

//--------------------------------------------------------------------------
float wathcdogsDist( in vec2 pos )
{
    pos.x = abs(pos.x);

    vec2 a = vec2(0.0,0.8);
    vec2 b = vec2(1.0,-1.0);
    vec2 c = vec2(1.0,1.0);
    vec2 d = vec2(0.0,-1.4);

    float dist1 = distToLine(pos,a,b);
    float dist2 = distToLine(pos,b,c);
    float dist3 = distToLine(pos,c,d);   

    return min(dist1,min(dist2,dist3));
}

//--------------------------------------------------------------------------
float HashNoise( float n )
{
	return fract(sin(n)*43758.5453);
}

//--------------------------------------------------------------------------
float Noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0 + 113.0*p.z;
    return mix(mix(mix( HashNoise(n+  0.0), HashNoise(n+  1.0),f.x),
                   mix( HashNoise(n+ 57.0), HashNoise(n+ 58.0),f.x),f.y),
               mix(mix( HashNoise(n+113.0), HashNoise(n+114.0),f.x),
                   mix( HashNoise(n+170.0), HashNoise(n+171.0),f.x),f.y),f.z);
}

//--------------------------------------------------------------------------
float Fbm(in vec3 p)
{
	float f;
    f  = 0.5000*Noise( p ); p = p*2.02;
    f += 0.2500*Noise( p ); p = p*2.03;
    f += 0.1250*Noise( p ); p = p*2.01;
    f += 0.0625*Noise( p );
	return f*2.0- 1.0; 
}

//--------------------------------------------------------------------------
vec4 CalcWallColor( in vec3 pos, in vec3 nor)
{
    float groundFactor = 1.0-(clamp(pos.y+5.0,0.0,0.1)*10.0);
    float midBandFactor = 1.0-(clamp(abs(pos.x),5.0,5.1)-5.0)*10.0;

    float dist = 1.0-clamp(wathcdogsDist(0.3*pos.xz+vec2(0.0,1.4)),0.0,0.06)*(1.0/0.06);     

    float dirt = 0.6*(1.0-Fbm(pos*0.2))+0.4;

    vec4 floor = mix(vec4(vec3(0.0),0.2),vec4(vec3(0.05,0.0,0.0),0.05),dist); 

    vec4 col = mix(vec4(0.05),floor,groundFactor*midBandFactor);

    return col*dirt;
}

//--------------------------------------------------------------------------
vec4 calcColor( in vec3 pos, in vec3 nor, float matID, out vec3 normalmod )
{
    normalmod = vec3(0.0);
    if (matID < 0.5 )  return calcColorUbi(pos);
    if (matID < 1.5 )  return CalcWallColor(pos, nor);
    if (matID < 2.5 )  return calcColorLed(pos);
    else return vec4(0.0,0.0,0.1,0.2);
}

//--------------------------------------------------------------------------
vec3 calcNormal( in vec3 pos) 
{
    vec3 e = vec3(0.0,0.0002,0.0);
	float d = map(pos).x;
    return normalize(vec3( map(pos+e.yxx).x-d, map(pos+e.xyx).x-d, map(pos+e.xxy).x-d) );	
}

//--------------------------------------------------------------------------
float softShadow( in vec3 ro, in vec3 rd, float mint, float k )
{
    float res = 1.0;
    float t = mint;
    for( int i=0; i<45; i++ )
    {
        float h = map(ro + rd*t).x;
        res = min( res, k*h/t );
        t += clamp( h, 0.04, 0.1 );
    }
    return clamp(res,0.0,1.0);
}

//--------------------------------------------------------------------------
//IQ ray-marched ambient occlusion algorithm 
float ambientOcclusion( in vec3 pos, in vec3 nor )
{
	float totao = 0.0;
    float sca = 2.0;
    for( int aoi=0; aoi<8; aoi++ )
    {
        float hr = 0.01 + 1.2*pow(float(aoi)/8.0,1.5);
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        totao += -(dd-hr)*sca;
        sca *= 0.85;
    }
    return clamp( 1.0 - 0.6*totao, 0.0, 1.0 );
}

//--------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

    // camera
	vec2 mousePos = iMouse.xy/iResolution.xy;

    float defaultCameraAngle = PI*0.88;
    float defaultCameraDist = 20.0;
    float inputCamAngle = 3.1415*(1.5-mousePos.x);
    float inputCamDist = 2.0+((1.0-mousePos.y)*22.0); 

	float camAngle = mix(defaultCameraAngle,inputCamAngle,clamp(iMouse.z,0.0,1.0));
	float camDist = mix(defaultCameraDist, inputCamDist, clamp(iMouse.z,0.0,1.0));

    vec3 camPosition = camDist*normalize(vec3(sin(camAngle), 0.0, cos(camAngle)));
    vec3 camTarget	 = vec3( 0.0, 0.0, 0.0 );
    vec3 camFront 	 = normalize( camTarget - camPosition );
    vec3 camRight 	 = normalize( cross(camFront,vec3(0.0,1.0,0.0) ) );
    vec3 camUp 		 = normalize( cross(camRight,camFront));
    vec3 rayDir 	 = normalize( p.x*camRight + p.y*camUp + 2.0*camFront );

	// render
    vec3 color = vec3(0.0);
	float attenuation = 1.0;
	for( int k=0; k<NUM_REFLECTIONS; k++ )
	{
        // raymarch
        vec2 tmat = intersect(camPosition,rayDir);
        if( tmat.y>-0.5 )
        {
            // geometry
            vec3 position 	= camPosition + tmat.x*rayDir;
            vec3 normal 	= calcNormal(position);
            vec3 reflDir 	= reflect(rayDir,normal);

            float occlusion = ambientOcclusion(position,normal);

            // material
            vec3 normalmod = vec3(0.0);
            vec4 mate = calcColor( position, normal, tmat.y, normalmod );
            normal = normalize( normal + normalmod );

            // shading       
            float ambient  		= 0.7 + 0.3*normal.y;
			vec3 ambientColor   = ambient*mate.rgb;

			float diffuse = max(dot(lightDir,normal),0.0); 
			float darkRim = smoothstep(0.0,0.5,max(dot(-rayDir,normal),0.0));
			float shadow  = softShadow(position, lightDir, 0.01, 10.0); 

            float specular = pow(clamp(dot(lightDir,reflDir),0.0,1.0),3.0);

			vec3 diffuseColor = darkRim*diffuse*lightColor*mate.rgb;
			vec3 ilumColor = occlusion*shadow*(diffuseColor+pow(specular,30.0)); //TODO ~ remove specular and do it via reflection

			// mixing lights
			color += attenuation*mix(ambientColor,ilumColor,0.6);

            attenuation *= mate.w;
            camPosition = position + 0.001*normal;
            rayDir = reflDir;
        }
    	else
    	{
            color += attenuation*vec3(0.02,0.02,0.05); 
            attenuation = 0.0; 
        }
	}

	// desat
	color = mix( color, vec3(dot(color,vec3(0.33))), 0.3 );

    // gamma
	color = pow( color, vec3(0.45) );

	// tint
	color *= vec3(1.0,1.04,1.0);

    fragColor = vec4( color,1.0 );
}

#define TAU 6.28318530718

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 40.;
