void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #define time iTime
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y+vec2(0.,.8);
	float s = 0.;
	for (float l = 30.; l > 1.; l--) {
		float sw = 0., t = l*.1+time;
		for (float w = 1.; w < 10.; w++) sw += pow(.5+.5*sin(t*(1.+w*.1)+uv.x*w*w*.9812),w)/w;
		sw *= .6;
		float v = max(0.,1.-abs(uv.y-sw)*20.)/l;
		s = mix(s,1.,v);
	}
	fragColor = vec4(s);
}

vec3 colors[5] = vec3[5](
    vec3(   0,   7, 100 ) / 256.0,
    vec3(  32, 107, 203 ) / 256.0,
    vec3( 237, 255, 255 ) / 256.0,
    vec3( 255, 170,   0 ) / 256.0,
    vec3( 106,  53,   3 ) / 256.0
