void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	fragCoord = (2.0 * fragCoord - iResolution.xy ) / iResolution.y;


    vec2 pos = fragCoord;
    vec2 center = vec2(-0.05, 0.68);

    float tz = 0.85 * (0.85 - cos(0.15 * iTime));
    float zoom = pow(0.5, 10.0 * tz);


    vec2 C = vec2(pos * zoom + center);
    vec2 Z = vec2(0.0, 0.0);
    int iter = 0;
    vec2 Zsqr = Z;

    while (Zsqr.x + Zsqr.y <= 4.0 && iter < 600)
    {
        Z = vec2(Zsqr.x - Zsqr.y + C.x, 2.0 * Z.x * Z.y + C.y);
        Zsqr = Z*Z;
        ++iter;
    }
    if (iter < 600) {
        int idx = int(float(iter) * 5.0 / 125.0);
        fragColor = vec4(mix(colors[idx%5], colors[(idx+1)%5], float(iter) * 5.0 / 125.0 - float(idx)), 1.0);

    } else {
        fragColor = vec4(0, 0, 0, 1);
    }

}

