//from https://www.shadertoy.com/view/MsS3Wc thanks iq! \(`v`)/
vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}

float invLerp(float val, float small, float big){
    return ( abs(val-small) )/(big-small);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    uv = vec2(  1.-invLerp(mod(atan(uv.y,uv.x)+pi*.5, 2.*pi),0.,2.*pi), length(uv));

    float n = 7.;
    vec2 ax = vec2(1.,1.);
    uv -= iTime*.75;

    uv *= mat2(normalize(ax),normalize(vec2(ax.x,-ax.y)));
    uv = floor(uv*n*length(ax))/n;
    float base = sin((uv.x-uv.y));
    fragColor = vec4(hsv2rgb(vec3(base,.6,1.0)),1.0);
}

#define S(a,b,t) smoothstep(a,b,t)
#define T iTime
#define MAX_STEP 100
#define MAX_DIST 100.
#define SURFACE_DIST .001
mat2 Rot(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(c,-s,s,c);
}
float intersectSDF(float distA,float distB){
    return max(distA,distB);
}



float unionSDF(float distA,float distB){
    return min(distA,distB);
}
float differenceSDF(float distA,float distB){
    return max(distA,-distB);
}
float sdBox(vec3 p,vec3 c,vec3 s){
    return length(max(abs(c - p) - s,0.));
}
float sdSphere(vec3 p,vec3 ori,float s){
    return length(p - ori) - s;
}
float GetDist(vec3 p){
    float boxDist = sdBox(p,vec3(0.,0.,0.),vec3(4.));
    float sphereDistInner = sdSphere(p,vec3(0.,0.,0.),6. * abs(sin(T)));
    float sphereDist = sdSphere(p,vec3(0.,0.,0.),6. * abs(sin(T)));
    float d = differenceSDF(boxDist,sphereDistInner);
    d = intersectSDF(d,sphereDist);
    return d;
}
vec3 GetNormal(vec3 p){
    float d = GetDist(p);
    vec2 offset = vec2(.001,0.);
    return normalize(d - vec3(
       GetDist(p - offset.xyy),
       GetDist(p - offset.yxy),
       GetDist(p - offset.yyx)
    ));
}  
float RayMarch(vec3 ro,vec3 rd){
    float d = 0.;
    for(int i = 0; i < MAX_STEP; i++){
        vec3 p = ro + rd * d;
        float dS = GetDist(p);
        d += dS;
        if(d > MAX_DIST || dS < SURFACE_DIST)break;
    }
    return d;
}
vec3 GetRayDir(vec2 uv,vec3 ro,vec3 look,float z){
    vec3 forward = normalize(look - ro);
    vec3 right = normalize(cross(vec3(0.,1.,0.),forward));
    vec3 top = cross(forward,right);
    vec3 center = ro + z * forward;

    vec3 i = center + right * uv.x + top * uv.y;
    vec3 dir = normalize(i - ro);

    return dir;
}
void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2. - 1.;
    if(iResolution.y > iResolution.x)uv.y *= iResolution.y / iResolution.x;
    else uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.);
    vec3 ro = vec3(8. * sin(T),5.,7.* cos(T));

    vec3 rd = GetRayDir(uv,ro,vec3(0.,0.,0.),1.);

    float d = RayMarch(ro,rd);
    if(d < MAX_DIST){
        vec3 p = ro + rd * d;
        vec3 normal = GetNormal(p);
        float diffuse = clamp(dot(normal,normalize(vec3(1.,2.,-3.))),0.,1.) * .5 + .5;
        col += diffuse;
    }
    fragColor = vec4(col,1.);
}

float sphere_map(in vec3 pos) {

    float t = fract(iTime);
    float y = 4.0 * t*(1.0-t);
    // origin of the sphere
    vec3 cen = vec3(0.0,y,0.0);

    return length(pos-cen)-1.0;

}

float map (in vec3 pos) {


    float sphere = sphere_map(pos);
    float plane = pos.y - (-1.0 );

    float d = min(sphere, plane) ;

    return d;
}



vec3 calcNormal(in vec3 pos) {
    vec2 e = vec2(0.0001,0.0);
    return normalize(  vec3( map(pos+e.xyy)-map(pos-e.xyy),
                             map(pos+e.yxy)-map(pos-e.yxy),
                             map(pos+e.yyx)-map(pos-e.yyx)));

}



float castRay(in vec3 ro, vec3 rd) {
float t = 0.0;
 for(int i=0;i<100;i++) {
        vec3 pos = ro + t*rd;
        float h = map(pos);

        if (h < 0.001) {
            break;
        }
        t += h;
        if (t > 20.0) {
            break;
        }


     }

     if (t>20.0) t = -1.0;
     return t;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = (2.0 * fragCoord -iResolution.xy)/iResolution.y;


    vec3 ro = vec3(.0,0.5, 3.0);
    vec3 rd = normalize(vec3(p,-1.5));


     // sky color 
     vec3 sky_col = vec3(0.5,0.7,0.9);

     // sky color by default;
     vec3 col = sky_col - 0.5*rd.y;

     // ray marching
     float t = castRay(ro,rd);



     if ( t>0.0 ) {

         float theta = sin(iTime) / 10.0;

         vec3 sun_dir   =  normalize(vec3(theta + 0.7,0.4,0.2));
         vec3 sky_dir = vec3(0.0,1.0,0.0);
         vec3 bounce_dir = vec3(0.0,-1.0,0.0);


         vec3 pos = ro + t*rd;
         vec3 nor = calcNormal(pos);


         // normal material
         vec3 mate = vec3(0.18);

         // diffuse color from sun
         float sun_dif = clamp(dot(nor,sun_dir),0.0,1.0);
         col  = mate*vec3(7.0,5.0,3.0) * sun_dif;

         // sun shadow
         float sun_sha = step(castRay(pos+nor*0.001, sun_dir),0.0);
         col *= sun_sha;

         // diffuse color from sky
         float sky_dif = clamp(0.5 + 0.5*dot(nor,sky_dir),0.0,1.0);
         col +=  mate*0.5 *  sky_col * sky_dif;


        // bounce light from other materials back to itself
        float bou_dif =  clamp(0.5 + 0.5*dot(nor,bounce_dir),0.0,1.0);
        col +=  mate* vec3(0.7,0.3,0.2) * bou_dif;


    }



 // color correct 
 col = pow(col, vec3(0.4545));


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define display iResolution

#define offsetX 0.
#define offsetY 0.
#define speed .05
#define scale 1.

//border radius or smth like that
#define BR_X 1.
#define BR_Y 1.

float step = .0;
void mainImage( out vec4 fragColor, in vec2 pos )
{
    vec3 clr = vec3(1,1,1);

    step = .3 + iTime * speed;//you can disable shrinking (try set 1. or 2.)

    float distX = pow((display.x/2. + offsetX - pos.x) / scale, 2. *BR_X);
    float distY = pow((display.y/2. - offsetY - pos.y) / scale, 2. *BR_Y);

    clr.r = cos(pow(distX + distY, 1. *step) + iTime * 10.);
    clr.g = cos(pow(distX + distY, 1. *step) + iTime * 10.);
    clr.b = 1.;

    fragColor = vec4(clr,1.0);
}

// A single iteration of Bob Jenkins' One-At-A-Time hashing algorithm.
uint hash(uint x) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}

// Generate random 4bit RGB color
vec3 rcol(uint seed) {
    uint x = hash(seed);
    return vec3(x & 15u, x>>4u & 15u, x>>8u & 15u) / 15.;
}

vec3 col(float x) {
    uint seed = uint(int(x));
    return  rcol(seed)      * smoothstep(0.04, 0.02, fract(x))
          + rcol(seed + 1u) * smoothstep(0.00, 0.02, fract(x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scales coords so that the diagonals are all dist 1 from center
    float scale = length(iResolution.xy);
    vec2 uv = (fragCoord / scale
	- (iResolution.xy / scale / 2.)) * 2.;

    float r = sqrt(length(uv));
    float theta = atan(uv.y, uv.x) / 2. / 3.141592653589;

    float bands = 24.;
    float band = mod(
        theta * bands + r * 16. + iTime * 4.
    , bands);

    // Time varying pixel color
    vec3 col = col(iTime * 0.8 + floor(iTime * 150.) + floor(band) * 384. + r * 8.);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

#define sabs(x, k) 2.0 * k * log(exp(-abs(x) / k) + 1.0) + abs(x)

float mapScene(in vec3 p) {
    float c = cos(iTime), s = sin(iTime);
    p.xz *= mat2(c, -s, s, c);
    p.yz *= mat2(c, -s, s, c);
    float k = 0.2 + 0.2 * sin(iTime);
    return length(sabs(p, k) - 0.75) - 0.75;
}

vec3 getNormal(in vec3 p) {
    vec3 e = vec3(0.01, 0.0, 0.0);
    return normalize(vec3(mapScene(p + e.xyy) - mapScene(p - e.xyy),
                          mapScene(p + e.yxy) - mapScene(p - e.yxy),
                          mapScene(p + e.yyx) - mapScene(p - e.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float t = 0.0;
    for (int i=0; i < 100; i++) {
        vec3 p = ro + rd * t;
        float d = mapScene(p);
        if (d < 0.001) {
            vec3 n = getNormal(p);
            vec3 l = vec3(-0.58, 0.58, 0.58);
            fragColor.rgb += max(0.2, dot(n, l));
            break;
        }

        if (t > 10.0) {
            break;
        }

        t += d;
    }
}

vec2 fmod(float i, float m)
{
    return vec2(mod(i,m)*(1.0/m), abs(i/m));
}
vec4 c(vec2 uv)
{
    //uv.x += cos(uv.y*50.0+iTime)*0.01;
    // Plane tilting
    uv.x *= sin(iTime);
    uv.y *= cos(iTime);
    float r = iTime;
    // Rotating around Z axis
    vec2 uvR = mat2(cos(r),-sin(r),sin(r),cos(r))*uv;
    // Angle around Z axis

    float angl = atan(uvR.y,uvR.x);

    // Size of white stripe spread
    float spread = tan(iTime)*sin(5.0*angl);

    float l = length(uvR);
    vec2 a = fmod(l+spread*angl*0.9,0.1);
    float stripe = (a.x*(0.3/a.y));

    return vec4(1.0,0.0,0.0,0.0)*stripe+a.y*0.005*tan(iTime*5.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(col,1.0);
    vec4 cl = c(uv);
    //cl *= 1.0/(5.0*length(uv)+1.0);
    fragColor = cl;
}

// gives pure saturated color from input [0, 6) for phase
vec3 hue(float x) {
    x = mod(x, 6.);
    return clamp(vec3(
        abs(x - 3.) - 1.,
        -abs(x - 2.) + 2.,
        -abs(x - 4.) + 2.
    ), 0., 1.);
}

// does pseudo overexposure filter
vec3 deepfry(vec3 rgb, float x) {
    rgb *= x;
    return rgb + vec3(
      max(0., rgb.g - 1.) + max(0., rgb.b - 1.),
      max(0., rgb.b - 1.) + max(0., rgb.r - 1.),
      max(0., rgb.r - 1.) + max(0., rgb.g - 1.)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scales coords so that the diagonals are all dist 1 from center
    float scale = length(iResolution.xy);
    vec2 uv = (fragCoord / scale
	- (iResolution.xy / scale / 2.)) * 2.;

    vec2 rt = vec2(length(uv), atan(uv.y, uv.x));
    // probably better to just do this:
    // float r = length(uv);
    // float theta = atan(uv.y, uv.x);

    // alternate distance functions are also useful
    // for wormhole or perspective effects, like:
    // float r = log(uv.x*uv.x+uv.y*uv.y);

    // if you want the angle in range [0, 1) and not (-, ]
    // divide angle by 2pi and mod1 it
    // float theta = fract(atan(uv.y, uv.x) / 6.2831853071795);

    // Time varying pixel color
    vec3 col = deepfry(
        hue(rt.x * 3. + iTime),
        1. + 0.5 * sin(rt.x * 6. + rt.y * 3. + iTime * 4.)
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}

