#define BOZE(p) (sdBoze(p - vec3(0.,0.3, 0.), 5.0, (sin(iTime * 0.5) * 0.5 + 0.5)*1.5))

float sdDome(vec3 p)
{
    float d1 = sdRoundBox(p, vec3(100, 6, 100), 0.01);
    float d2 = sdSphere(p, 6.);
    return opSubtraction(d2, d1) - (fbm(p * 0.35 + iTime * 0.1)-(p.y*0.35));
    //return opSubtraction(d2, d1);
}

float map(vec3 p)
{
    // cloud boze
    float r = BOZE(p);

    // cloud sea
    float d = sdDome(p);
    return min(r, d);
}

float densitycalc(vec3 p){
    // Boze Cloud
	float r = BOZE(p);	
    float density = fbm(p * 15. + iTime * 0.5) - min(r, 1.) * 10.5;

    // Cloud sea
    float d = sdDome(p);
    float sdensity = fbm(p * 3. + iTime * 0.15) -min(d, 1.)*1.5;

    return max(density, sdensity);
}

float checkMap(vec3 p, vec3 ray, float depth)
{
    float t = 0.;

    for(int i = 0; i < 5; i++){
        float d = map(p);
        if(abs(d) < 0.1 || d > depth)
            break;
        t += d;
        p += ray * d;
    }

    return t;
}

#define MAX_MARCH 64
#define MAX_MARCH_L 6

vec3 raycast(vec3 p, vec3 ray, float depth)
{
    vec3 result = vec3(0.);

    float radius = 2.0;
    float d = checkMap(p, ray, depth);

    if(d < depth)
    {
        float zstep = 7.0 / float(MAX_MARCH);
    	float lightZStep = 2.0 / float(MAX_MARCH_L);

        vec3 ro2 = p + ray * (d - zstep);
        float t = hash13(ro2 * 2165.) * 0.001;
        float alpha = 0.;
        float transmittance = 1.0;
        float absorption   = 20.;
        //vec3 sunDir = normalize(vec3(cos(iTime),1,sin(iTime)));
        vec3 sunDir = normalize(vec3(-1,1,-0.25));
        vec3 col = vec3(0.0);

        for(int i = 0; i < MAX_MARCH; i++){
            t += zstep;
            vec3 pp = ro2 + ray * t;
            if(t >= depth)
                break;

            float density = densitycalc(pp);

            if(density > 0.){
                float dd = density / float(MAX_MARCH);
                transmittance *= 1.0 - dd * absorption;
                if(transmittance < 0.01)
                    break;
                alpha += 100. * dd * transmittance;

                float transmittanceLight = 1.0;

                for(int j = 0; j < MAX_MARCH_L; j++){
                	float densityLight = densitycalc(pp + sunDir * float(j) * lightZStep);
                    if(densityLight > 0.0){
                    	float dl = densityLight * lightZStep;
                        transmittanceLight *= 1.0 - dl * absorption * 0.1;
                        if(transmittanceLight < 0.01){
                        	transmittanceLight = 0.;
                            break;
                        }
                    }
                }

                col += vec3(1.) * (10. * dd * transmittance); // base
                col += vec3(1.,.7,.5) * (80. * dd * transmittance * transmittanceLight); // light
            }
        }
        result = col;
    }

    return result;
}

mat3 camera(vec3 ro, vec3 ta, float cr )
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

float luminance(vec3 col)
{
    return dot(vec3(0.298912, 0.586611, 0.114478), col);
}

vec3 reinhard(vec3 col, float exposure, float white) {
    col *= exposure;
    white *= exposure;
    float lum = luminance(col);
    return (col * (lum / (white * white) + 1.0) / (lum + 1.0));
}

vec3 sky(vec2 p, vec3 rd)
{
    vec3 r = vec3(0.);

    vec3 up = mix(vec3(0.25,0.5,0.8), vec3(0.001, 0.15, 0.5), saturate(abs(rd.y-0.3) * 5.0));
    vec3 down = vec3(0.25,0.5,0.8);
    return rd.y > 0.3 ? up : down;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float time = iTime * 0.5;
    float y = 0.6;
    vec3 ro = vec3(0., -0.5, 3.5);
    vec3 ta = vec3(0., y, 0.);
    mat3 c = camera(ro, ta, 0.);
    vec3 ray = c * normalize(vec3(p, 3.5));
    vec3 col = sky(p, ray);

    col += raycast(ro, ray, 100.);

    col = reinhard(col.xyz, 2.9, 100.0);
    col = pow(col.xyz, vec3(1.0/0.9));

    fragColor = vec4(col,1.0);
}

