vec4 GetVoronoiColor(in vec2 point)
{
    float nearDistSq = 1e20;
    vec4 color;
    vec2 offset;
    //offset.x = 0.3*FractalNoise(vec3(point, iTime * 0.3), 3.0, 0.5, 3);
    offset.x = 0.3*FractalNoise( vec3(point, iTime+texture(iChannel0, vec2(0,0)).r),1.0, 0.5, 5);
    offset.y = 0.3*FractalNoise(vec3(point, iTime +texture(iChannel0, vec2(0,0)).r + 100.0), 3.0, 0.5, 3);
    for ( int i = 0; i < 8; i++ )
    {
        vec2 diff = point+offset-GetPoint(i);
        float d2 = dot(diff,diff);
        if ( d2 < nearDistSq )
        {
            nearDistSq = d2;
            color = GetColor(i);
        }
    }
    return color;
}

bool sphere(in vec3 position, in vec3 direction, in vec3 centre, in float radius, in mat3 rotMat, out vec3 hitPosition,
                        out vec3 hitNorms, out vec3 hitPosLocal)
{
    float a = dot(position - centre, direction) * 2.0;
    float b = dot(position - centre, position - centre) - radius * radius;

    float sq = sqrt(a * a - 4.0 * b);
    float sol1 = 0.5 * (-b - sq);
    float sol2 = 0.5 * (-b + sq);

    if(a * a < 4.0 * b)
    {
        return false;
    }

    if(sol1 > sol2)
    {
        sol1 = sol2;
    }

    hitPosition = position + direction * sol1;
    hitNorms = normalize(hitPosition - centre);
    hitPosLocal = transpose(rotMat) * (hitPosition - centre);

    return true;

}


