mat2 rot(float ang){

    return mat2(cos(ang), -sin(ang), sin(ang), cos(ang));

}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x /= iResolution.y / iResolution.x;	
    uv *= 20.0;
    float sc = sin(iTime / 50.) * 5.;
    //uv *= sc;
    //uv *= rot(iTime / 50.);

    float kk = iTime / 3.;

    float ar = 0.;

    for(float j = 1.; j < ((sin(iTime / 4.) + 1.) / 2. + 0.5) * 5.; j += 1.){

        uv *= j;

        float ok = 0.;
        float a = 1.;
        float b = 0.;
        float c = 0.;    	

        for(float i = 0.; i < 8.; i += 1.){

            a += c;
            c = b;
            b = a;        	

            uv *= rot(i * (pi / 180. * 137.5));

            ok += sin(uv.x + a + kk);
            ok += sin(uv.y + a + kk);

        }
        ar += ok;
    }
    ar = (ar + 1.) / 2.;
    vec3 col = vec3(ar, ar, ar);

    // Output to screen
    fragColor = vec4(col,1.0); 

}

