// random hash via https://www.shadertoy.com/view/3d2GRh
// I didnt update this hash until after I styled it -_- so it stays
float hash( in ivec2 q )
{
    // You SHOULD replace this by something better. Again, Do Not Use in production.
    int n = q.x*131 + q.y*57;
    n = (n << 13) ^ n;
    n = n * (n * n * 15731 + 789221) + 1376312589;
    return float((n>>8)&0x007fffff)/float(0x007fffff);
}

float noise (in vec2 x, in int p)
{
    ivec2 i = ivec2(floor(x));
    vec2 f = fract(x);

    f = f*f*(3.-2.*f);

    return mix(mix( hash((i+ivec2(0,0))&(p-1)), 
                    hash((i+ivec2(1,0))&(p-1)),f.x),
               mix( hash((i+ivec2(0,1))&(p-1)), 
                    hash((i+ivec2(1,1))&(p-1)),f.x),f.y);
}

float fbm (in vec2 x, in int p)
{
    float f = 0.;
    float s = .5;
    for (int i=0; i<9; i++)
    {
        f += s*noise(x,p);
        s *= .5;
        x *= 2.;
        p *= 2;
    }
    return f;
}

vec3 eye(in vec2 uv)
{
    vec2 ar = vec2(atan(uv.y, uv.x)/PI, length(uv));
    vec3 bkgd = vec3(251.,247.,245.)/256.;
    float f = fbm(8.*ar, 4);
    vec3 col = vec3(0., .3, .4);
    col = mix(col, vec3(.2, .5, .4), f);

    float t = .8 * iTime;
    float ss = pow(abs(sin(t)), 4.) + pow(abs(sin(t + 2.)), 4.);
    float anim = 1. + .2*ss*clamp(1.-2.*ar.y, 0., 1.);
    ar.y *= anim;

    ar.x += .05 * fbm(20.*ar, 8);

    //pupil glow
    f = smoothstep(.25, .15, ar.y);
    vec3 glow = vec3(.9, .6, .2);
    col = mix(col, glow, f);

    //strands
    f = smoothstep(.3, 1., fbm(vec2(64., 10.)*ar, 16));
        //mixed mainlines
    vec3 strands = mix(vec3(12, 122, 4)/256., vec3(57, 237, 171)/256., fbm(8.*ar, 12));
        //white striations
    strands = mix(strands, vec3(1), pow(abs(sin(18.*PI*ar.x)), 8.));
    col = mix(col, strands, f);

    //black
    f = smoothstep( .4, .9, fbm(vec2(24., 8.)*ar, 8));
    col *= 1. - .5*f;

    //dim edges
    f = smoothstep(.55, .3, ar.y);
    col *= f;

    //pupil
    col *= smoothstep(.1, .15, ar.y);

    //reflection
    f = smoothstep(.25, .0, length(uv - vec2(.1, .1)));
