#define PI 3.1415
#define PI2 3.1415 * 2.


float Rand2To1(in vec2 val)
{
     return fract(sin(val.x*3.23+val.y*54.342)*854.53)
     ;
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float line(in vec2 p, in vec2 a, in vec2 b, in float thick){

    return 1.-smoothstep(thick - 1.5/iResolution.y, thick, sdSegment(p,a,b));

}

float Pattern(in vec2 uv, in float edgeCount, in float rad){
    float thick = 8./iResolution.y;

    float result = 0.;

    float segA = PI2 / edgeCount;
    mat2x2 segmentRotation = mat2x2(cos(segA),-sin(segA),sin(segA),cos(segA));

    vec2 circleOrigin = uv+vec2(-.5,0.);
    for(float i = 0.; i < edgeCount; i++){
        vec2 curr = vec2( cos( i* (PI2 / edgeCount)), sin( i* (PI2 / edgeCount)) ) * rad;
        vec2 next = vec2(cos( (i+1.)* (PI2 / edgeCount)), sin( (i+1.)* (PI2 / edgeCount) ))*rad;

        //Outer line
        result += line(circleOrigin, curr, next, thick);

        vec2 currToNextDir = next - curr;
        vec2 halfWayP = curr + (currToNextDir*.5);

        vec2 perpendCurr = cross(vec3(0.,0.,1.),normalize(vec3(currToNextDir,0.))).xy;
        vec2 perpendNext = perpendCurr * segmentRotation;

        float distToCenter = length(  halfWayP )* (0.8 + sin(iTime)*0.2);
        vec2 currMid = halfWayP + (perpendCurr*distToCenter);
        vec2 nextMid =  (halfWayP*segmentRotation) + (perpendNext*distToCenter);

        //current & next point connecting to midway point perpendicular
        result += line(circleOrigin, curr, currMid, thick*.3);
        result += line(circleOrigin, next , currMid, thick*.3);
        result += line(circleOrigin, currMid, nextMid, thick*.3);
        //midway point connecting to center of circle to create inner triangles
        result += line(circleOrigin, currMid, vec2(0), thick*.3);
    }


    return clamp(result,0.,1.);

}


