    // smoothstep to create blur effect of the middle circle 

    float colorVal = circle(st+0.5,extension(abs(cos(iTime))),random(vec2(10.)*smoothstep(0.,PI,tan(iTime))));

    // Scale the coordinate system to see
    // some noise in action

    // find the center of the shortest side 
    st-=min(st.x/2.,st.y/2.);
    st = rotate2d( sin(cos(iTime))*PI*(abs(cos(iTime))/1e2+1.)) * st/abs(sin(iTime));
    vec2 pos = vec2(st*0.756*abs(tan(iTime))*10.);
    st+=min(st.x/2.,st.y/2.);


    st-=min(st.x/2.,st.y/2.);
    st *= rotate2d( PI*(fract(iTime))/1e3+fract(sin(iTime))) * st/(0.1+abs(sin(iTime)));
    st *= rotate2d( PI*(cos(iTime))/1e2+1.) * st/3.280;
    // Use the noise function with distance field 
    float n = noise(pos)*distance(st-vec2(0.760,0.670)*abs(sin(iTime)),pos);
    st+=min(st.x/2.,st.y/2.);

    // color manipulation based on time and shapes 
    color=vec3(n,colorVal*abs(cos(iTime)),random(st-vec2(1e2*iTime/2.)));
    color*=vec3(st.x,colorVal*abs(cos(iTime))*st.y,random(st-vec2(clamp(abs(sin(iTime)),0.,0.8))));
    color-=color*colorVal*10.;

    fragColor = vec4(color+abs(cos(iTime)), 1.0);
}

// Using hardware derivativs to approximate image sharpening / blurring using a single tap.
//
// Left side - sharpening
// Middle - original image (bilinear filtering)
// Right side - smoothing
//
// Based on ideas from Juan https://twitter.com/reduzio/status/1350895712494563329,
// and GPU Pro 2 article - "Shader Amortization using Pixel Quad Message Passing" (PQA)
//
// Note image swimming with animation due to 2x2 quad "point sampling" nature of this technique.

vec3 SharpenQuad2x2(in vec2 fragCoord, in vec3 v)
{ 
    vec2 q = vec2(uvec2(fragCoord.xy) & uvec2(1));
    q = 2.0 * q - vec2(1.0);    
    v += 0.5 * dFdx(v) * q.x;
    v += 0.5 * dFdy(v) * q.y;
    return v;
}

vec3 SmoothQuad2x2(in vec2 fragCoord, in vec3 v)
{ 
    vec2 q = vec2(uvec2(fragCoord.xy) & uvec2(1));
    q = 2.0 * q - vec2(1.0);    
    v -= 0.5 * dFdx(v) * q.x;
    v -= 0.5 * dFdy(v) * q.y;
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float lg = uv.x - (0.35*sin(iTime)+0.5) - 0.15;
    float lr = uv.x - (0.35*sin(iTime)+0.5) + 0.15;

#if 1 // Animated
    uv = 0.8 * uv + vec2(0.1) + vec2(0.05*sin(0.5*iTime),0.08*cos(0.3*iTime));
#endif

    vec3 col = texture(iChannel0, uv).rgb;
    vec3 col_sharpen = SharpenQuad2x2(fragCoord, col);
    vec3 col_smooth = SmoothQuad2x2(fragCoord, col);

    col = mix(col_sharpen,col,smoothstep(0.0,2.0/iResolution.x,lr));
    col = mix(col,col_smooth,smoothstep(0.0,2.0/iResolution.x,lg));

    col = mix(vec3(0.6,0.2,0.15),col,smoothstep(0.0,2.0/iResolution.x,abs(lr)));
    col = mix(vec3(0.2,0.6,0.15),col,smoothstep(0.0,2.0/iResolution.x,abs(lg)));

    fragColor = vec4(col,1.0);
}

