    	color1 += magnify(U,i+2.0);
    }
    O = O-vec4(color1/(maximum+0.),0.1);
}

#define ITER 3
#define MAX_STEPS 100
#define MAX_DIST 10.
#define SURF_DIST 0.002
#define SPEED 2.

float dist(vec2 p, vec2 t){
    float dx = p.x-t.x;
    float dy = p.y-t.y;
    return sqrt(dx*dx+dy*dy);
}

// 3d mandelbulb
float mandel3(in vec3 v, out vec3 map){
    vec3 r = v;
    vec3 rNext = r;
    float m = dot(v, v);
    vec4 trap = vec4(abs(r), m);

    int i = 0;
    float d = 1.;

    float n =  7.;

    for(i=0; i<ITER; i++){
        float cr = length(r);
        float nr = pow(cr, n);
        //*abs(sin(iTime/5.) )
        float accos = acos(r.z/cr*abs( sin(iTime/2.)*.2+.8 ));
        float actan = atan(r.y/r.x);

        d = pow(cr, n-1.) *n * d + 1.0;

        rNext.x = nr*sin(accos*n)*cos(n*actan);
        rNext.y = nr*sin(accos*n)*sin(n*actan);
        rNext.z = nr*cos(n*accos);
        r = v + rNext;

        trap = min(trap, vec4(abs(r), m));
        map = vec3(trap.yzw);

        m = dot(r,r);

        if(m > 256.){
            break;
        }
    }

    return  .25*log(m) * sqrt(m) / d;
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d; 
}

float RayMarch(in vec3 ro, in vec3 rd, out vec3 col){
    float dO = 0.;

    for(int i=0; i<MAX_STEPS; i++){
        vec3 p = ro+rd*dO;
        p.xz *= Rot(iTime/SPEED);
        p.yz *= Rot(iTime/SPEED-2.);

        float mbd = mandel3(abs(p), col);
        float dS = mbd;

        if(dO > MAX_DIST || dS<SURF_DIST) break;
        dO += dS;
    }

    return dO;
}
vec3 GetNormal(vec3 p){
    vec3 col;
    // my e value made black artifacts
    // so I took a look at iq's normal epsilone. 
    // don't exactly know how this is calculated.
    // looks like it is relevant to some screen pixel size calculation..?
    vec2 e = vec2(1.0,-1.0)*0.5773*0.25*2.0/(iResolution.y*1.5);

    // getting vector with very small vector
    vec3 n = vec3(
        e.xyy*mandel3(p+e.xyy, col)+
        e.yxy*mandel3(p+e.yxy, col)+
        e.yyx*mandel3(p+e.yyx, col)+
        e.xxx*mandel3(p + e.xxx,col ) 
    );

    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/(iResolution.y);
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 col = vec3(0);

    vec3 ro = vec3(0,1.3, 1.3);
    vec3 rd = R(uv, ro, vec3(0,0,0), .7);

    vec3 backgrounduv;

    col = vec3(.05, .12, .1);
    float d = RayMarch(ro, rd, backgrounduv);
    col *= 3.-d;
    col += pow(clamp(backgrounduv.z, 0., .97), 10.)*vec3(.8, .1, .1);

    //light
    vec3 lp = vec3(1000, 1, 0);
    lp.xz *= Rot(iTime/3.);
    lp.yz *= Rot(iTime/3.);
    vec3 lr = ro+rd*d;
    lr.xz *= Rot(iTime/SPEED);
    lr.yz *= Rot(iTime/SPEED-2.);
    lr = abs(lr);
    vec3 l = normalize(lp - lr);
    vec3 n = GetNormal(lr);
    float shadow = clamp(dot(n, l), 0.2, 1.);
