#define gridSize 10.0

float sdBox( vec2 uv, vec2 position, in vec2 bounds )
{
    vec2 d = abs(position - uv)-bounds;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdCircle(vec2 uv, vec2 pos, float radius) {
    return length(uv - pos) - radius;
}

float sdf(vec2 uv) {
    float radius1 = 0.4 * (1.0 + sin(iTime * 0.6));
    vec2 position1 = vec2(sin(iTime * 0.3), sin(iTime * 0.2));
    float circle1 = sdCircle(uv, position1, radius1);   

    float radius2 = 0.3 * (1.0 + sin(iTime * 0.1));
    vec2 position2 = vec2(sin(iTime * 0.2), sin(iTime * 0.5));
    float circle2 = sdCircle(uv, position2, radius2);   

    return min(circle1, circle2);
}

bool insideBoundingSquare(vec2 position, vec2 bound, vec2 pixelSize) {

    // If all points inside the grid's outer circle are negative, then we're definitely inside.
    float midpointD = sdf(position);
    if (midpointD < -2.0 * length(bound)) {
        return true;
    }

    float i = 0.;
    float pixelsHigh = (2.0 * bound.y / pixelSize.y);
    vec2 rd = vec2(1.0, 0.0);
    vec2 ro = position - bound;
    while (i < pixelsHigh) {
        i += 1.0;
        ro.y += pixelSize.y;
        vec2 pos = ro;
        float dist = 0.;
        while (dist < bound.x * 2.0) {
            float d = sdf(pos);
            d = clamp(d, 0., 100.);
            if (d > pixelSize.x) {
                return false;
            }

            if (d < pixelSize.x) {
                d = pixelSize.x;
            }

            dist += d;
            pos = ro + rd * dist;
        }  
    }

    return true;
}

bool touchingBoundingSquare(vec2 position, vec2 bound, vec2 pixelSize) {

    // If all points inside the grid's outer circle are outside,
    // then we're definitely outside and not touching.
    float midpointD = sdf(position);
    if (midpointD > 2.0 * length(bound)) {
        return false;
    }

    float i = 0.;
    float pixelsHigh = (2.0 * bound.y / pixelSize.y);
    vec2 rd = vec2(1.0, 0.0);
    vec2 ro = position - bound;
    while (i < pixelsHigh) {
        i += 1.0;
        ro.y += pixelSize.y;
        vec2 pos = ro;
        float dist = 0.;
        while (dist < bound.x * 2.0) {
            float box1 = sdBox(pos, position, bound);
            float d = sdf(pos);
            d = abs(max(abs(d), box1));
            if (d <= pixelSize.x) {
                return true;
            }

            if (d < pixelSize.x) {
                d = pixelSize.x;
            }

            dist += d;
            pos = ro + rd * dist;
        }  
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = 2.0 * (fragCoord - (0.5*iResolution.xy))/iResolution.y;
    vec2 pixelSize = 2.0 * ((vec2(iResolution.x, iResolution.y) - (0.5*iResolution.xy))/iResolution.y) / iResolution.xy;

    // Calculate SDF
    float d = sdf(uv);

    // Bounding Box
    vec2 voxelMidpoint = vec2((floor(uv.x * gridSize) / gridSize) + (0.5 / gridSize), (floor(uv.y * gridSize) / gridSize) + (0.5 / gridSize));
    vec2 voxelSize = vec2(0.5 / gridSize, 0.5 / gridSize);
    vec2 minExtent = voxelMidpoint - voxelSize;
    vec2 maxExtent = voxelMidpoint + voxelSize;
    float box1 = sdBox(uv, voxelMidpoint, voxelSize);

    // Work out if the SDF is inside the box or not.
    bool inside = insideBoundingSquare(voxelMidpoint, voxelSize, pixelSize);
    bool touching = touchingBoundingSquare(voxelMidpoint, voxelSize, pixelSize);
    bool outside = !touching;

    vec3 statusColor = vec3(1.0, 1.0, 0.0);
    if (inside) {
        statusColor = vec3(0.0, 1.0, 0.0);
    } else if (outside) {
        statusColor = vec3(1.0, 0.0, 0.0);
    }

    // Set base colour depending on inside or outside bounding box.
    vec3 baseColor = vec3(0.1,0.4,0.7);
    if (uv.x > minExtent.x && uv.x < maxExtent.x && uv.y > minExtent.y && uv.y < maxExtent.y) {
        baseColor = vec3(0.8,0.2,0.2);
    }

    // Taken from iq to visualize sdf distances (https://www.shadertoy.com/view/4lcBWn)    
    vec3 col = vec3(1.0) - sign(d)*baseColor;
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.008,abs(d)));

    // Add the grid
    col = mix( col, statusColor, 1.0-smoothstep(0.0,0.008,abs(box1)) );

    // Output to screen
    fragColor = vec4(col,1.0);

}

