mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdOctahedron( vec3 p, float s)
{
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}

vec3 random3(vec3 c) {
    float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
    vec3 r;
    r.z = fract(512.0*j);
    j *= .125;
    r.x = fract(512.0*j);
    j *= .125;
    r.y = fract(512.0*j);
    return r-0.5;
}

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float map(vec3 p)
{
    float t = floor(iTime) + pow(fract(iTime ),6.);
    float t2 = floor(iTime /2.) + pow(fract(iTime /2.),6.);
    randomd = pow( length( random( floor(p.yz/ (pi *2.) + pi * 2.)  + floor(iTime) ) ),3. )* 10.;
   // if(randomd < 3.)return 1.;
  // p.x = abs(p.x) - 5.5;
   // p.yz= sin(p.yz) * 2.;
   // p.x = sin(p.x) * 2.4;
    p.xz *= rot(t * 2.);
    float o = sdOctahedron(p,1.);
    float shape = (clamp( sin(t * pi/2.) * 1. ,0.,1.));
    float shape2= abs(sin(t2 * pi/2.) * 1.);

    o = mix(mix( sdRoundBox(p,vec3(.8),.1),length(p) - 1., shape ),o,shape2 );
    return o;
}

float march(vec3 cp , vec3 rd)
{
    float depth = 0.;
    for(int i = 0; i< 66; i++)
    {
        vec3 rp = cp + rd * depth;
        float d = map(rp);
        if(abs(d) < 0.01)
        {
            return depth;
        }
        depth += d;
    }
    depth *= -1.;
    return depth;
}



//https://www.shadertoy.com/view/XsX3zB
/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
    /* 1. find current tetrahedron T and it's four vertices */
    /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
    /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

    /* calculate s and x */
    vec3 s = floor(p + dot(p, vec3(F3,F3,F3)));
    vec3 x = p - s + dot(s, vec3(G3,G3,G3));

    /* calculate i1 and i2 */
    vec3 e = step(vec3(0.,0.,0.), x - x.yzx);
    vec3 i1 = e*(1.0 - e.zxy);
    vec3 i2 = 1.0 - e.zxy*(1.0 - e);

    /* x1, x2, x3 */
    vec3 x1 = x - i1 + G3;
    vec3 x2 = x - i2 + 2.0*G3;
    vec3 x3 = x - 1.0 + 3.0*G3;

    /* 2. find four surflets and store them in d */
    vec4 w, d;

    /* calculate surflet weights */
    w.x = dot(x, x);
    w.y = dot(x1, x1);
    w.z = dot(x2, x2);
    w.w = dot(x3, x3);

    /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
    w = max(0.6 - w, 0.0);

    /* calculate surflet components */
    d.x = dot(random3(s), x);
    d.y = dot(random3(s + i1), x1);
    d.z = dot(random3(s + i2), x2);
    d.w = dot(random3(s + 1.0), x3);

    /* multiply d by w^4 */
    w *= w;
    w *= w;
    d *= w;

    /* 3. return the sum of the four surflets */
    return dot(d, vec4(52.0,52.0,52.0,52.0));
}

float wmap(vec3 p)
{
    float t = floor(iTime) + pow(fract(iTime),6.);
    float o = sin( (p.y + 0.1 ) * 2.) - ( cos(sin(t * 1.3) * 2.14) / 1.2 ) + .6;
        p.xz *= rot(0.01);
    	o -= simplex3d(p * 1.7 - t)/(9.) ;
    o = max(map(p) , o);
    return o;
}

float wmarch(vec3 cp,vec3 rd)
{
    float depth = 0.;
    float bdepth = 0.;
    w = wmap(cp + rd * 0.01);
    for(int i = 0;  i< 99 ; i++)
    {
        vec3 rp = cp + rd * depth;
        float d = wmap(rp);
        //float bd = map(rp);
        if(abs(d) < 0.01)
        {
            return depth;
        }
        depth += d;
    }
    return -depth;
}

float getWaterThickness(vec3 cp , vec3 rd)
{
    float thickness = 0.;
    for(int i = 0; i < 44 ; i++)
    {
        vec3 rp = cp + rd * (thickness + 0.001);
        float d = wmap(rp);
        if(d < 0.)
        {
            return thickness;
        }
        thickness += d;
    }
    return thickness;
}

