void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    float reresol = 40.;
    p = floor(p * (reresol * iResolution.x/iResolution.y)) / (reresol * iResolution.x/iResolution.y);
    float r = random( vec2(floor(iTime)/100.) );
	vec3 col = vec3(0.,0.5,1.);
    //col = mix(hsv2rgb(r,r,1.),col,.8);
    p.y *= -1.;

    vec3 forward = vec3(0.,0.,0.);
    //forward.z += time;
    vec3 cp = vec3(0.,1.,-5.) + forward;

    cp.y += sin(iTime);
    vec3 target = vec3(0.) + forward;
    vec3 cd = normalize(target - cp);
    vec3 cs = normalize(cross(cd,vec3(0.,1.,0.)));
    vec3 cu = normalize(cross(cd,cs));

    float fov = 4.;
	vec3 rd = normalize(cd * fov + cs * p.x + cu * p.y);

    float d = march(cp,rd);
    if(d > 0.)
    {
        col *= 0.;
        vec2 e = vec2(0.,0.01);
        vec3 pos = cp + rd * d;
        vec3 N = -normalize(map(pos) - vec3(map(pos - e.xyy),map(pos - e.yxy),map(pos - e.yyx)));
        float wd = wmarch(cp , rd);

        vec3 sun = normalize(-vec3(2.,4.,8.));
        float wstep = 3.;
        float bstep = 2.5;
        float sp = 0.;
        if(wd > 0. )
        {
        	vec3 wpos = cp + rd * wd;
            vec3 wN = normalize(wmap(wpos) - vec3(wmap(wpos - e.xyy),wmap(wpos - e.yxy),wmap(wpos - e.yyx)));
            float sp2 = max(0.,dot(rd,reflect(wN,sun)));
            float diff = max(0. , dot(N,sun)) * .7 + .3;
            float thickness = getWaterThickness(wpos,rd);
            float wrim = max(0.,1. - abs(dot(wN , rd)));
            wrim =  pow(wrim,3.) * 1.1;
            vec3 wcolor = hsv2rgb(r,r,1. ) * 2.;
            sp2 = pow(sp2 , 2.) + .1;
            col = floor( (sp2 * vec3(1.) + diff * wcolor + (thickness * 13.)) * wstep + wrim * vec3(1.)) / wstep;
            //vec3(0.,.4,.9)
        }

        //
        sp = max(0.,dot(rd,reflect(N,sun)));
        sp = pow(sp,5.) / 2.;
        float rim = max(0.,1. - abs(dot(N , rd)));
        rim = pow(rim , 30.);
        float diff = max(0. , dot(N,sun)) * .5 + .5;
        col += step(length(col),0.1) * floor( (diff * vec3(0.8,0.4,1.) * 1.5 + rim * vec3(1.7) ) * bstep ) / bstep;

   		float t = 1. - exp( -0.003 * d * d *d );
    	col = mix(col,vec3(0.,0.5,1.),t);
    }
   // vec2 uv = fragCoord.xy / iResolution.xy;
   // col = mix(texture2D(backbuffer,uv).rgb,col,.2);

    fragColor = vec4(col, 1.0);
}

mat2 Rot(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

mat3 RotX(float a){
    float s = sin(a);
    float c = cos(a);

    return mat3(1., .0, .0, .0, c, -s, .0, s, c);

}

mat3 RotY(float a){
    float s = sin(a);
    float c = cos(a);

    return mat3(c, .0, s, .0, 1., .0, -s, .0, c);

}

mat3 RotZ(float a){
    float s = sin(a);
    float c = cos(a);

    return mat3(c, -s, .0, s, c, .0, .0, .0, 1.);

}

vec2 map2(vec3 p, out vec4 resColor){

    vec2 d = vec2(0.0);

    //float pl = p.y;

   vec3 w = p;

    float m = dot(w,w);

    vec4 trap = vec4(abs(w),m);
	float dz = 1.0;


	for( int i=0; i<4; i++ )
    {

        dz = 8.0*pow(sqrt(m),7.0)*dz + 1.0;
		//dz = 8.0*pow(m,3.5)*dz + 1.0;

        float r = length(w);
        float b = 8.0*acos( w.y/r);
        float a = 8.0*atan( w.x, w.z );
        w = p + pow(r,8.0) * vec3( sin(b)*sin(a), cos(b), sin(b)*cos(a) );


        trap = min( trap, vec4(abs(w),m) );

        m = dot(w,w);
		if( m > 256.0 )
            break;
    }

    resColor = vec4(m,trap.yzw);

    d.x = 0.25*log(m)*sqrt(m)/dz;
    d.y = 1.0;


    return d;

}

