vec3 RM(vec3 ro, vec3 rd, float _d){
    vec3 d = vec3(_d, 0.0, 0.0);
    for(int i = 0;i < 200;i++){
        d.yz = map(ro + d.x * rd).xy;

        if((d.y) <(0.005))
            break;

        d.x += d.y;

        if(d.x > 1000.0)break;

    }
    if(d.x > 1000.0)d.x = -1.0;

    return d;

}

