mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
float bo(vec3 p , vec3 s){p = abs(p) - s; return max(p.x,max(p.y,p.z));}
float nearest = 10.;
float pi = acos(-1.);

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233))) * 43758.5453123);
}

float Pile(vec3 p)
{
    float o = 10.;

    p.xz *= rot(p.y/1. + iTime);
    p.x = abs(abs(p.x) - 1.) ;
    p.z = abs(abs(p.z) - 1.) ;
    float b = bo(p,vec3(.4,4.,.4));
    o = b;
    return o;
}

vec2 map(vec3 p)
{
    float o = 10.;
    float id = 0.;
    float ii = 10.;
    vec2 dd = floor(p.xz / ii);
    float t = random(dd);
    p.xz = clamp(p.xz , -pi * 1.5*2.,pi * 1.5*2.);
    p.xz = mod(p.xz , ii) - ii/2.;
    float piller = Pile(p);
    p.y += sin(t * pi  + iTime) * 2.6;
    float sp = length(p) - .8;
    o = min(piller,o);
    if(o > sp)
    {
        id = random(dd);
        o = sp;
    }
    return vec2(o,id);
}

vec2 march(vec3 cp, vec3 rd)
{
    float depth = 0.;
    for(int i = 0 ; i < 99 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        nearest = (d.x < nearest)?d.x:nearest;
        if(abs(d.x) < 0.01)
        {
            return vec2(depth,d.y);
        }
        depth += d.x;
    }
    return vec2(-depth,0.);
}

vec3 hsv2rgb(float h,float s,float v)
{
    return ((clamp(abs(fract(h+vec3(0,2,1)/3.)*6.-3.)-1.,0.,1.)-1.)*s+1.)*v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec3 col = vec3(.8);
    vec3 bcol = col;
    vec3 cp = vec3(0.,-0.,-20.);
    float tim = iTime/1.6;
    float t = floor(tim) + pow(fract(tim),4.);
    cp.xz *= rot(t*pi/4.);
    vec3 target = vec3(0.,0.,0.);

    vec3 cd = normalize(target - cp);
    vec3 cs = vec3(cross(cd , vec3(0.,1.,0.)));
    vec3 cu = vec3(cross(cd , cs));

    float fov = 2.5;
    vec3 rd = normalize(cd * fov + cs * p.x + cu * p.y);

    vec2 d = march(cp,rd);

    if(d.x > 0.)
    {
        vec3 pos = cp + rd * d.x;
        vec2 e = vec2(0.,0.01);

        vec3 N = -normalize(map(pos).x - vec3(map(pos - e.xyy).x , map(pos - e.yxy).x,map(pos - e.yyx).x));
        col = N;
        vec3 sun = normalize(vec3(2.,4.,8.));
        float rim = 1. - abs(dot(rd , N));
        float stepdiff = 6.;
        float stepsp = 2.;
        float diff = max(0.,dot(N,sun)) * .7 + .3  ;
        float sp = max(0.,dot(reflect(-N,sun),rd));

        if(d.y == 0.)
        {
            sp = pow(sp , 20.);
        	col = vec3(.8);
        }
        else
        {
            sp = pow(sp , 20.);
        	col = hsv2rgb(d.y * d.y * 1.5 , 1.,1.);
        }
        sp = floor(stepsp * sp) / stepsp;
        diff = floor(stepdiff * diff) / stepdiff;

        sp = smoothstep(0.,1.,sp);
        col = col + sp * vec3(1.);
        float fine = 29.* max(0., (1. -  diff )) * step(diff , 0.6);
        float density = .9 * max(0., (diff ));
        col =col* step(.2, length((mod(pos * fine ,density) - density/2.)));

        vec3 pos1 = pos + cu *  0.001 + cs * 0.001;
        vec3 pos2 = pos + cs * -0.001 + cu * 0.001;
        vec3 N1 = -normalize(map(pos1).x - vec3(map(pos1 - e.xyy).x , map(pos1 - e.yxy).x,map(pos1 - e.yyx).x));
        vec3 N2 = -normalize(map(pos2).x - vec3(map(pos2 - e.xyy).x , map(pos2 - e.yxy).x,map(pos2 - e.yyx).x));
        float NN = length(N - N1) + length(N - N2);
        if(NN > .07)
        {
            col *= 0.;
        }
    }else if(abs(nearest) < 0.02)
    {
        col *= 0.;
    }

    fragColor = vec4(col, 1.0);
}

#define RES iResolution
#define LIGHT_ON
#define DIFFUSE_TERM_ON
#define SPECULAR_ON
#define SHADOWS_ON
float is(vec3 ro, vec3 rd, vec3 sc, float sr, out float tmin, out float tmax)
{
    vec3 Z = ro-sc;
    float B = 2.0 * dot(rd, Z);

    float C = Z.x*Z.x+Z.y*Z.y+Z.z*Z.z-sr*sr;
    float D = B*B - 4.0*C;
    float d = -1.0;
    if(D>=0.0) {
        if(C==0.0) {
            tmin = -B/2.0;
            tmax = -1.;
            d = 0.0;
        }else{
            tmin = (-B-sqrt(D))/2.0;
            tmax = (-B+sqrt(D))/2.0;
            d = 1.0;
        }
    }
    return d;
}

void build(vec3 ro, vec3 rd, vec3 pt, vec3 N, vec3 ic, inout vec4 s, inout vec3 oc)
{
    float _d = dot(rd, N);
    float d = _d!=0.?(dot(pt-ro, N)/_d):100.; d = d<0.?1000.:d;
    if(d>0. && d<s.x)
    {
        s = vec4(d, N);
        oc = ic;
    }
}

void scene(vec3 ro, vec3 rd, inout vec4 s, inout vec3 oc)
{
    s = vec4(1000.f, vec3(0., 1., 0.));
    build(ro, rd, vec3(0.), vec3(0., 1., 0.), vec3(1., 1., 0.), s, oc);
    build(ro, rd, vec3(2., 0., 0.), vec3(-1., 0., 0.), vec3(1., 0., 0.), s, oc);
    build(ro, rd, vec3(-2., 0., 3.), normalize(vec3(0.)-vec3(-2, 0., 3.)), vec3(0.5, 0.5, 1.), s, oc);
    float tmin = 100., tmax = -100.;
    float sp = is(ro, rd, vec3(-0.2, .5, 0.), 1., tmin, tmax);
    if(sp>-1. && s.x>tmin && tmin>0. && tmin>0.)
    {
        s = vec4(tmin, normalize((ro+rd*tmin)-vec3(-0.2, .5, 0.)));
        oc = vec3(0., 1., 0.);
    }
}

float shadows(vec3 ro, vec3 Lp)
{
    float tmin = 0., tmax = 0.;
    vec4 s = vec4(1000., 0., 0., 0.);
    vec3 oc = vec3(0.);
    vec3 d = normalize(Lp-ro);
    scene(ro, d, s, oc);
    float ds = max(0., dot(d, normalize(Lp-(ro+d*s.x))));
    return (1.-ds)+ds*exp(-1.8*.5*max(0., 2.-s.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*RES.xy)/RES.y;

    // setup
    vec3 ro = vec3(0., 1., -4.);
    vec3 rd = normalize(vec3(uv, 1.));
    vec3 tgt = vec3(0.);
    vec3 lw = vec3(2., 0., 0.);
    vec3 up = vec3(0., 1., 0.);
    vec3 fwd = normalize(tgt-ro);
    vec3 left = normalize(cross(fwd, up));
    up = normalize(cross(left, fwd));

    rd = mat3(left, up, fwd)*rd;

    vec3 Lp = vec3(2.0*sin(iTime), 0.7, 2.0*cos(iTime))+vec3(-0.5, 0., 0.);
    vec3 N = vec3(0., 1., 0.);
    vec4 s = vec4(1000.f, N);
    vec3 c = vec3(0.);
    scene(ro, rd, s, c);

    vec3 p = ro+rd*s.x;
    vec3 L = normalize(p-Lp);
    vec3 H = normalize(L+rd);
    float Ld = length(Lp-p);

    vec3 col = vec3(0.);
    // https://github.com/stackgl/glsl-specular-cook-torrance/blob/master/index.glsl
    float NoV = dot(-rd, s.yzw);
    float NoL = dot(L, s.yzw);
    float HoN = max(0., dot(H, s.yzw));
    float HoV = max(0., dot(H, rd));
    float HoL = max(0., dot(H, L));
    float F = 0.02+0.98*pow(1.-max(0., NoV), 5.);
    float R = 0.6;
    float D = 1./max(0.001, ((NoV+sqrt((NoV-R*NoV) * NoV + R))*(NoL+sqrt((NoL-R*NoL) * NoL + R))));
    float G = clamp(min((2.*HoN*NoV)/HoV, (2.*HoN*NoL)/HoL), 0.001, 1.);

    col =
    #if defined(LIGHT_ON)
    (
    c
    #if defined(DIFFUSE_TERM_ON)
    *max(0., NoV)/Ld
    #endif
    #if defined(SPECULAR_ON)
    +D*F*G/max(0.01, 3.14*NoV*NoL)
    #endif
    )
    #if defined(SHADOWS_ON)
    *max(.0, shadows(p+s.yzw*0.01, Lp))
    #endif
    #else
    vec3(1.);
    #endif
    ;
    // Output to screen
    col = col / (1.+col);
    fragColor = vec4(pow(col*col*(3.-2.*col), vec3(0.7 / 2.2)),1.0);
    //fragColor.rgb = vec3(1./(NoL+sqrt((NoL-R*NoL) * NoL + R)));
    //fragColor.rgb = vec3(HoV-HoL);
    //fragColor.rgb = L;
}

#define MAX_STEPS 100
#define SURFACE_DIST  .01
#define MAX_DIST  100.
float GetDist(vec3 p){
    vec4 sphere = vec4(0.,1.5,6.,1.5);
    float dS = length(p - sphere.xyz) - sphere.w;
    float dG = p.y;
    return min(dS,dG);
}
float RayMartch(vec3 ro,vec3 rd){
    float d = 0.;
    vec3 p = ro;
    for(int i = 0; i < MAX_STEPS; i++){
        p = ro + rd * d;
        float dS = GetDist(p);
        d += dS;
        if(dS < SURFACE_DIST || d > MAX_DIST)break;
    }
    return d;
}
vec3 GetNormal(vec3 p){
    float d = GetDist(p);
    vec2 e = vec2(.001,0.);
    vec3 n = d - vec3(
        GetDist(p - e.xyy),GetDist(p - e.yxy),GetDist(p - e.yyx)
    );
    return normalize(n);
}
float GetLight(vec3 p){
    vec3 lightPos = vec3(0.,8.,1.);
    lightPos.xz += vec2(sin(iTime),cos(iTime));
    vec3 ld =  normalize(lightPos - p);
    vec3 normal = GetNormal(p);
    float dif = clamp(dot(normal,ld),1.,1.);

    //shadow
    float d = RayMartch(p + normal * SURFACE_DIST * 2.,ld);
    if(d < length(lightPos - p))dif *= .5;
    return dif;
}
void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    uv.y  += 1.;
    vec3 ro = vec3(0.,1.,-3.);
    vec3 rd = normalize(vec3(uv.x,uv.y,0.) - ro);
    float d = RayMartch(ro,rd);

    vec3 p = ro + rd * d;

    float diffuse = GetLight(p);
    vec3 col = vec3(diffuse);

    fragColor = vec4(col,1.);
}

float GetNoise(float y, float seed, float time) // -> (0.0, 1.0)
{
    #define NOISE_RESOLUTION 0.25
    float s1 = texture(iChannel1, vec2(NOISE_RESOLUTION * y + time, seed)).r;
    float s2 = texture(iChannel1, vec2(NOISE_RESOLUTION * y - time, seed)).r;

    #define NOISE_THRESHOLD .65
    s1 = (clamp(s1, NOISE_THRESHOLD, 1.) - NOISE_THRESHOLD) * 1. / (1. - NOISE_THRESHOLD);
    s2 = (clamp(s2, NOISE_THRESHOLD, 1.) - NOISE_THRESHOLD) * 1. / (1. - NOISE_THRESHOLD);

    return max(s1, s2);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates
    vec2 _uv = fragCoord/iResolution.xy;
    //uv.y /= (iResolution.x / iResolution.y); // obey aspect ratio

    // uv: 0. -> 1.

    //
    // Here we simulate this shader running in OpenGL as follows:
    // - The panel occupies only a part of the screen
    // - The texture lives in an Atlas and thus the extent
    //   of the texture coordinates are somewhere arbitrary in the
    //   texture coordinates space
    // - The non-underscored variables are vertex attributes
    //
    // NDC screen x,y: -1.0 -> 1.0
    // Texture x,y: 0.0 -> 1.0 (in reality it will be from atlas coordinates)
    // Panel size NDC: w=1.0 h=0.5 (width is half of the screen)
    // Quad x: -0.5 -> 0.5 (twice as wide as texture)
    // Quad y: -0.25 -> 0.25

    float paramWidthNdc = 1.0;
    vec2 _quadTopLeft = vec2(-0.5, 0.25);
    vec2 _quadBottomRight = vec2(0.5, -0.25);

    // Time: phases:
    // 1) from 0 to  4: inside=1, opening
    // 2) from 4 to  5: still
    // 3) from 5 to  9: inside=0. closing
    // 4) from 9 to 10: still
    #define SPEED 1.2
    float _tmpTime = mod((float(iTime) * SPEED), 10.); // 0 -> 10
    float vertexIsOpening = 1. - step(5., _tmpTime);
    _tmpTime = mod(_tmpTime, 5.) / 4.; // 0 -> 1.25
    _tmpTime = min(_tmpTime, 1.); // Pause

    // tmpTime: 0. -> 1.

    vec2 vertexCoordinatesNdc = _uv * 2. - vec2(1.0); // -1.0 -> 1.0

    // Vertex texture coords are rescaled so that they are 0->1 inside the quad NDC
    vec2 vertexTextureCoordinates = vec2(
        vertexCoordinatesNdc.x + 0.5, // -0.5 -> 1.5
        (vertexCoordinatesNdc.y + 0.25) * 2.); // -1.5 -> 2.5

    // Opening/Closing xLimits
    float _width = paramWidthNdc * _tmpTime; // 0.0 -> 1.0
    vec2 xLimitsNdc = vec2(
        vertexIsOpening * (0. - _width / 2.) + (1. - vertexIsOpening) * (-paramWidthNdc / 2. + _width / 2.), 
        vertexIsOpening * (0. + _width / 2.) + (1. - vertexIsOpening) * (paramWidthNdc / 2. - _width / 2.)); // -0.5 -> 0.5        

    // Simulate quad by clipping outside of it
    vec2 _uvNdc = (_uv - vec2(.5)) *  2.; // -1.0 -> 1.0
    if (_uvNdc.x < _quadTopLeft.x
        || _uvNdc.x > _quadBottomRight.x
        || _uvNdc.y < _quadBottomRight.y
        || _uvNdc.y > _quadTopLeft.y)
    {
        fragColor = vec4(0.);
        return;
    }

    ///////////////////////////////////////////////////////////////////////////

    //
    // Flanges
    //

    float currentWidthNdc = xLimitsNdc.y - xLimitsNdc.x; // 0.0 -> 0.5
    float midXNdc = xLimitsNdc.x + currentWidthNdc / 2.;
    float widthFraction = currentWidthNdc / paramWidthNdc;

    float noise = GetNoise(
        vertexCoordinatesNdc.y / (.4 * paramWidthNdc * .5), 
        .2 * step(midXNdc, vertexCoordinatesNdc.x), 
        widthFraction / 5.);

    float flangeLengthNdc = (0.25 + noise * .4) * paramWidthNdc / 2.;

    // Flatten flange when panel is too small or almost fully open
    flangeLengthNdc *= (smoothstep(.0, .46, widthFraction) - smoothstep(.54, 1., widthFraction));

    // Make flanges close to x Limits, either external or internal
    // depending on whether we're closing or opening
    float leftFlange = 
        vertexIsOpening * smoothstep(0.0, flangeLengthNdc, vertexCoordinatesNdc.x - xLimitsNdc.x)
        + (1. - vertexIsOpening) * (1. - smoothstep(0.0, flangeLengthNdc, xLimitsNdc.x - vertexCoordinatesNdc.x));
    float rightFlange = 
        vertexIsOpening * smoothstep(0.0, flangeLengthNdc, xLimitsNdc.y - vertexCoordinatesNdc.x)
        + (1. - vertexIsOpening) * (1. - smoothstep(0.0, flangeLengthNdc, vertexCoordinatesNdc.x - xLimitsNdc.y));

    float panelDepth = 
        step(vertexCoordinatesNdc.x, midXNdc) * leftFlange
        + step(midXNdc, vertexCoordinatesNdc.x) * rightFlange;

    float inPanelQuad = step(xLimitsNdc.x, vertexCoordinatesNdc.x) * step(vertexCoordinatesNdc.x, xLimitsNdc.y);

    //
    // Texture
    //

    vec4 cTexture = texture(iChannel0, vertexTextureCoordinates);


    //
    // Final color
    //

    vec4 color = vec4(cTexture.xyz, panelDepth);

    // Add white contour
    float panelBorderDepth = max(
        abs(inPanelQuad - panelDepth),
        1.0 - smoothstep(0.0, 0.005, min(abs(vertexCoordinatesNdc.x - xLimitsNdc.x), abs(vertexCoordinatesNdc.x - xLimitsNdc.y))));
    color = mix(
        color,
        vec4(1.),
        panelBorderDepth);

    /* TEST: visualize noise
    color = vec4(GetNoise(
        vertexCoordinatesNdc.y / paramWidthNdc * 2., 
        .2 * step(midXNdc, vertexCoordinatesNdc.x), 
        widthFraction / 4.));
    */

    /////////////////////////////////////////////////////////////////////////////

    fragColor = mix(vec4(0.), color, color.w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    float t;

    for(int c=0;c<3;c++){
	    vec2 uv = (fragCoord*50.0-iResolution.xy)/iResolution.y/10.0;
        uv += iTime/10.0;
        t = iTime+float(c)/10.;
        float scale = 5.0;
        float scale1 = 1.4;
        for(int i=0;i<20;i++)
        {

            uv=cos(uv/(2.0+sin((uv.x+uv.y)/10.0))-(uv.yx/(2.0+sin(fract(iTime/2.0+uv.x+uv.y))))/scale)*scale/1.5+scale1*scale;
            uv /= scale1;
        	//uv += cos(uv/100.0);
        	uv=uv.yx+col.xy;
        }
     col[c] = fract((uv.x)-(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}

