            float x = -1.0 + 2.0*abs(fract(iTime*0.5)-0.5)/0.5;
            sph.x = x*10.5;

            vec3 q = p-sph.xyz;
             col = Eye(q, sp)*diff2;

         }
         else if(t.z == -2.0){

             vec4 sp = eyer;
             vec4 sph = sphball;
             sph.y = sin(sph.y*3.1415+iTime*10.0)*5.0+5.0;
            sph.z -= iTime*2.1;

            float x = -1.0 + 2.0*abs(fract(iTime*0.5)-0.5)/0.5;
            sph.x = x*10.5;

            vec3 q = p-sph.xyz;

             col = Eye(q, sp)*diff2;

         }
         else if(t.z == 3.0){

              col = diff2*vec3(0.88, 0.69, 0.65);
              float f = smoothstep(0.5, 1., fbm(p.xy*25.0));
              col =mix(col,  texture(iChannel0, vec2(fbm(p.xy*25.0)) ).rgb*0.2, f)  ;

         }
         else if(t.z == 4.0){
             vec3 colXZ = texture(iChannel2, p.xz*0.1).rgb;
             vec3 colYZ = texture(iChannel2, p.yz*0.1).rgb;
             vec3 colXY = texture(iChannel2, p.xy*0.1).rgb;


             col = colYZ * n1.x + colXZ * n1.y + colXY*n1.z;
             col += diff2 * vec3(1.0, 0.5, 0.5);
         }
         else if(t.z == 5.0){
             col = vec3(1.0, 0.0, 0.0) * diff2;


         }





     }
     else
     {
         col = RayMarchCloud( ro, rd);


     }


    // Output to screen
    fragColor = vec4((col),alpha);
}

float rand(vec2 coord){
	return fract(sin(dot(coord, vec2(12.9898, 78.233)))* 43758.5453123);
}

float noise(vec2 coord){
	vec2 i = floor(coord);
	vec2 f = fract(coord);

	// 4 corners of a rectangle surrounding our point
	float a = rand(i);
	float b = rand(i + vec2(1.0, 0.0));
	float c = rand(i + vec2(0.0, 1.0));
	float d = rand(i + vec2(1.0, 1.0));

	vec2 cubic = f * f * (3.0 - 2.0 * f);

	return mix(a, b, cubic.x) + (c - a) * cubic.y * (1.0 - cubic.x) + (d - b) * cubic.x * cubic.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 texture_coords = fragCoord/iResolution.xy;

	float f = 2.0;
	float cellSize = 50.0;

	vec2 pos = texture_coords;

	pos.x -= mod(pos.x, 1.0 / cellSize); // pixelation
	pos.y -= mod(pos.y, 1.0 / cellSize);

	pos *= 5.0;
	float threshold = pos.y;
	pos.y -= iTime*2.0;

	float result = 1.0 - abs(noise( vec2((pos.x*f), (pos.y*f)) ) *2.0-1.0) + noise( vec2((pos.x*f), (pos.y*f)) * 2.5 ) * 0.8;
	if(result < threshold){result = 0.0;}

    fragColor = vec4(result, result - 0.5, 0.0, 1.0);
}

//mix of https://www.shadertoy.com/view/ldcGWH
//and https://www.shadertoy.com/view/Xds3Rr

//from https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdRoundCone( vec3 p, float r1, float r2, float h )
{
  vec2 q = vec2( length(p.xz), p.y );

  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);
  float k = dot(q,vec2(-b,a));

  if( k < 0.0 ) return length(q) - r1;
  if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

  return dot(q, vec2(a,b) ) - r1;
}

float sdBerry( vec3 p, float s )
{
  vec3 centerCone = p + s*vec3(0,0.03,0);
  float dRoundCone = sdRoundCone( centerCone, s*0.01, s*0.02, s*0.03 );

  vec3 centerSmallSphere = p + vec3(0.3,0.02,0.1);
  float invS = 1.0/s;
  invS = 1.0;//TOFO?
  float dSpheres = dot(abs(sin(invS*centerSmallSphere*350.0)), vec3(0.001,0.001,0.004));
  return dRoundCone - dSpheres;
}

