float berryScale;
float mapExterior(in vec3 p)
{
    float d = sdBerry(p, berryScale);
	return d;
}

vec3 grad(in vec3 p)
{
    vec2 off = vec2(0.0001, 0.0);
    vec3 g = vec3(mapExterior(p + off.xyy) - mapExterior(p - off.xyy),
                  mapExterior(p + off.yxy) - mapExterior(p - off.yxy),
                  mapExterior(p + off.yyx) - mapExterior(p - off.yyx));
    return normalize(g);
}

float trace(in vec3 p, in vec3 d, out vec3 closestPoint)
{
    float closest = 99.0;
    bool hit = false;
    for (int i = 0; i < 250; ++i)
    {
        float dist = mapExterior(p);
        if (dist < closest)
        {
        	closest = dist;
            closestPoint = p;
        }

        if(dist < 0.0)
        {
            hit = true;
            break;
        }
        p += d * max(dist * 0.5, 0.00001);
    }
    return closest;
}

// (too much) tweakable parameters.
// falloff control
const float density = 8.;
const float ss_pow = 3.; 
const float ss_scatter = 0.4;
const float ss_offset = .5;

// color controls
const float ss_intensity = 0.5;
const float ss_mix = 0.5;
const vec4 ss_color = vec4(.75, .05, .12, 0.0);

// determines how deep from surface to start scattering tracing, not something to tweak really.
const float surfaceThickness = 0.008;

// Fresnel
vec4 rimCol = vec4(1.0, 0.8, 0.8, 1.0);
float rimPow = 3.0;
float rimAmount = 2.;
float F = 2.2;

float rand(vec2 co)
{
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

/**
* Gets thickness for subsurface scattering calculation. 
* I first offset the ray hit position a bit along opposite of the surface normal 
* to get a starting position inside the volume.
* From there I step towards the light and calculate distance travelled inside the negative volume of
* the distance function.
* 
* Outer loop is for approximating scattering of the light that travels in the volume,
* I just offset the light direction by some scatter value for each sample.
*/
float ssThickness(in vec3 raypos, in vec3 lightdir, in vec3 g, in vec3 rd)
{
    vec3 startFrom = raypos + (-g * surfaceThickness);
    vec3 ro = raypos;

    float len = 0.0;
    const float samples = 12.;
    const float sqs = sqrt(samples);

    for (float s = -samples / 2.; s < samples / 2.; s+= 1.0)
    {
        vec3 rp = startFrom;
        vec3 ld = lightdir;

        ld.x += mod(abs(s), sqs) * ss_scatter * sign(s);
        ld.y += (s / sqs) * ss_scatter;

        ld.x += rand(rp.xy * s) * ss_scatter;
        ld.y += rand(rp.yx * s) * ss_scatter;
        ld.z += rand(rp.zx * s) * ss_scatter;

        ld = normalize(ld);
        vec3 dir = ld;

        for (int i = 0; i < 50; ++i)
        {
            float distExterior = mapExterior(rp);
            if(distExterior < 0.0) distExterior  = min(distExterior, -0.0001);
            if(distExterior >= 0.0) break;

            dir = normalize(ld);
            rp += abs(distExterior * 0.5) * dir;  
        }
        len += length(ro - rp);
    }

    return len / samples;
}

//from https://www.shadertoy.com/view/Xds3Rr
vec3 getColorFromSound(in vec2 fragCoord)
{
    // create pixel coordinates
    vec2 uv = fragCoord.xy / iResolution.xy;

    // the sound texture is 512x2
    int tx = int(uv.x*512.0);

	// first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    // second row is the sound wave, one texel is one mono sample
    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;

	// convert frequency to colors
	vec3 col = vec3( fft, 4.0*fft*(1.0-fft), 1.0-fft ) * fft;

    // add wave form on top	
	col += 1.0 -  smoothstep( 0.0, 0.15, abs(wave - uv.y) );

	// output final color
	return col;
}

float getBerryScaleFromSound()
{
    int numFetch = 25;

    // the sound texture is 512 pixel wide
    int stepBetweenFetch = 512/numFetch;
    float sumfftBass = 0.0;
    for(int i = 0; i <512; i+=stepBetweenFetch)
    {
        float fft = texelFetch( iChannel0, ivec2(i,0), 0 ).x; 
        sumfftBass += fft*fft;
    }

	// output final color
	return 3.0f * sumfftBass/float(numFetch);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5);
    uv.y /= iResolution.x / iResolution.y;

    vec2 m = (iMouse.xy / iResolution.xy) - vec2(0.5);

    berryScale = (m.x);
    if(iMouse.z <= 0.0)
    {
    	berryScale = sin(iTime * 0.5) * 0.1 + 0.5;
        berryScale += getBerryScaleFromSound();
    }

    fragColor = vec4(0.0);
    vec3 rd = normalize(vec3(uv, 1.0));
    vec3 rp = vec3(0.0, -0.01, -.3);

    // closest point is used for antialising outline of object
    vec3 closestPoint = vec3(0.0);
    float hit = trace(rp, rd, closestPoint);
    vec4 color = vec4(.0);
    rp = closestPoint;

    vec3 ld = normalize( vec3(14.0, 1.0, 20.0) - closestPoint);
    vec3 g = grad(closestPoint);
    float d = dot(g, ld);
    d = clamp(d, 0.0, 1.0);

    //fresnel
    vec3 r = reflect(-ld, g);
    float rimd = 1.0 - dot(r, -rd);
    rimd = clamp(rimd, 0.0, 1.0);
    rimd = pow(rimd, rimPow);

    float frn = rimd + F * (1.0 - rimd);
    color += frn * rimCol * rimAmount * d;

    // subsurface        
    float t = ssThickness(rp, ld, g, rd);
    t = exp(ss_offset -t * density);
    t = pow(t, ss_pow);

    vec4 sscol = t * ss_color * ss_intensity;
    sscol = mix(sscol, ss_color, 1.0 - ss_mix);
    color += sscol;

    fragColor = vec4(getColorFromSound(fragCoord), 1.0);
    // color + AA
    fragColor = 0.75*mix(color, fragColor, mix(0.0, 1.0, clamp(hit / surfaceThickness * 16.0, 0.0, 1.0)));

    // vignette
    fragColor *= smoothstep(0.55, 0.48, abs(uv.x));
    fragColor *= smoothstep(0.31, 0.27, abs(uv.y));
}

#define IT 800. //iterations

float log10(float x){
    //log10(x) = log(x) / log(10) = (1 / log(10)) * log(x)
    const float d = 1. / log(10.);
    return d * log(x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;

    float zoom = pow(iTime*.1, 5.);

    uv *= 4.;
    uv /= 1. + zoom;
    uv -= vec2(.7394, -.24215);

    float cr = uv.x;
    float ci = uv.y;


    /*
    uv *= 4.5;
    uv += vec2(0., .7);

    float cr = -uv.y;
    float ci = uv.x;
    */

    vec3 color = vec3(0);

	float zr = 0.;
    float zi = 0.;

    //last escaped complex module value
    float zn_abs = 0.;

    float n = 0.;
    while(n < IT){
        float fr = zr*zr - zi*zi + cr;
        float fi = 2.*zr*zi + ci;

        zr = fr;
        zi = fi;

        if(abs(fr)+abs(fi) > 500.){
            zn_abs = sqrt(fr*fr + fi*fi);
            break;
        }

        n++;
    }

    if(n == IT){
    	//color += .5;
    }else{
    	//color += mix(vec3(0,0,0), vec3(0,2,2), n / IT *mix(8., 1., iTime*.02));
        //color += vec3(0, 1, 1) * log10(n) * .4;
        //color += vec3(0, 1, 1) * pow(n/IT, .5f);

        //color += 1.;

        //color += n/IT*20.;

        //smooth n
        float ns = n + 1. - log(log(zn_abs))/log(2.);
        //color += fract(ns*10./IT)*3.;
        color += hsv2rgb(vec3(fract(ns*10./IT)+3.7 +iTime*.03, .7, 1.));
    }

    fragColor = vec4(color,1.0);
}

#define PI 3.14159265359
#define TAU 2.0*PI
#define ANIMATE true
#define MOUSE_LOOK false
#define TIME_OFFSET 60.0

#define COL_OUTER_EYE vec3(0.5, 0.5, 0.7)
#define COL_BACKGROUND vec3(0.04, 0.06, 0.1)
#define COL_PUPIL_OUTER_START vec3(0.1, 0.3, 0.9)
#define COL_PUPIL_OUTER_END vec3(0.8, 0.0, 0.0)
#define COL_PUPIL_INNER vec3(1.0)
#define COL_EYEBALL_COLOR vec3(0.1, 0.1, 0.1)
#define COL_EYEBALL_TEXTURE_COLOR vec3(0.3, 0.1, 0.5)
#define COL_ORB_BACKGROUND vec3(0.5, 0.1, 0.9)
#define COL_ORB_OUTLINE vec3(0.8, 0.0, 0.0)
#define COL_CANTHUS vec3(0.3, 0.0, 0.0)

#define TIMER_PHASE_LOOK 30.0
#define TIMER_PHASE_SPLIT 50.0
#define TIMER_PHASE_FADEOUT 56.0
#define TIMER_PHASE_RESET 60.0
#define TIMER_PHASE_ALL 64.0


struct phaseInfo
{
    int id;
    float time;
};


struct orbInfo
{
    vec2 position;
    float size;
    float timedSeed;
    float fixedSeed;
};


float ang(in vec2 from, in vec2 to)
{
    return atan(from.y - to.y, from.x - to.x);
}


float calculatePixelValue(in float currentDistance, in float maxWidth)
{
    return max(0.0, 1.0 - pow(abs(currentDistance - 1.0), 2.5) / (maxWidth));
}


float calculatePixelValueFilled(in float currentDistance, in float maxWidth)
{
    return max(0.0, 1.0 - pow(currentDistance - 1.0, 5.0) / (maxWidth));
}


float getTimedMix(in float phaseTimer, in float phaseTimeStart, in float duration)
{
    phaseTimer -= phaseTimeStart;
    return min(phaseTimer/duration, 1.0);
}


float interpolateLinear(in float val1, in float val2, in float percentage)
{
    return mix(val1, val2, percentage);
}


float interpolateSmooth(in float val1, in float val2, in float percentage)
{
    return mix(val1, val2, -cos(PI * percentage) * 0.5 + 0.5);
}


void getNoiseValues(in float seed, in float offset, in float upperLimit, out float val1, out float val2, out float percentage)
{
    vec2 texSize = iChannelResolution[0].xy;
    float maxSize = texSize.x * texSize.y - 1.0;

    offset = mod(offset, maxSize);
    upperLimit = (upperLimit <= 0.0 ? maxSize : min(upperLimit + offset, maxSize));

    seed = offset + mod(seed, upperLimit - offset);

    percentage = fract(seed);
    float lowerSeed = floor(seed);
    float upperSeed = ceil(seed);

    if(upperSeed > upperLimit){ // Make clean repetition
        upperSeed = offset;
    }

    if(seed < ceil(offset)){ // Adjust for fractions (lower)
        float maxFraction = ceil(offset) - offset;
        percentage -= fract(offset);
        percentage = percentage/maxFraction;
    }

    if(seed > floor(upperLimit)){ // Adjust for fractions (upper)
        float maxFraction = upperLimit - floor(upperLimit);
        percentage = percentage/maxFraction;
    }


    float xValue1 = mod(lowerSeed, texSize.x);
    float yValue1 = mod(floor(lowerSeed/texSize.y), texSize.y);

    float xValue2 = mod(upperSeed, texSize.x);
    float yValue2 = mod(floor(upperSeed/texSize.y), texSize.y);

    vec4 tex1 = texelFetch(iChannel0, ivec2(xValue1, yValue1), 0);
    vec4 tex2 = texelFetch(iChannel0, ivec2(xValue2, yValue2), 0);

    val1 = tex1.x;
    val2 = tex2.x;
}


float getRandSmooth(in float seed, in float offset, in float repeatAt)
{
    float val1;
    float val2;
    float percentage;

    getNoiseValues(seed, offset, repeatAt, val1, val2, percentage);
    return interpolateSmooth(val1, val2, percentage);
}


float getRandSmooth(in float seed)
{
	return getRandSmooth(seed, 0.0, 0.0);
}


float getRand(in float seed, in float offset, in float repeatAt)
{
    float val1;
    float val2;
    float percentage;

    getNoiseValues(seed, offset, repeatAt, val1, val2, percentage);
    return interpolateLinear(val1, val2, percentage);
}


float getRand(in float seed, in float offset)
{
    return getRand(seed, offset, 0.0);
}


float getRand(in float seed)
{
    return getRand(seed, 0.0, 0.0);
}


float getFixedValue(in float duration)
{
    return iTime - mod(iTime, duration);
}


mat2 rotate(in float angle)
{
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}


vec2 rotateVec(in vec2 startVec, in vec2 offset, in float angle)
{
    mat2 rotation = rotate(angle);
    startVec = startVec - offset;
    startVec *= rotation;
    return startVec + offset;
}


float normalizedCos(in float value)
{
    return 0.5*(1.0-cos(value/PI*20.0));
}


float calculateParabola(in vec2 startPoint, in vec2 centerPoint, in float parabolaX, out float a, out float height)
{
    // General parabola formula: y = a*(x - x_center) + y_center
    a = (startPoint.y - centerPoint.y) / pow(startPoint.x - centerPoint.x, 2.0);
    float parabolaY = a*pow(parabolaX - centerPoint.x, 2.0) + centerPoint.y;
    height = parabolaY - startPoint.y;
    return parabolaY;
}


void drawBackLines(inout vec3 col, in vec2 fragCoord)
{
    vec2 center = iResolution.xy*0.5;
    float angle = (ang(fragCoord, center) + PI) * 180.0/PI * 0.8 + iTime;
    angle = mod(angle, 360.0);
    float angleDisc = floor(angle);

    float timePerAngle = getRand(angleDisc, 0.0) * 2.0 + 5.0;

    float val = getRand(angleDisc, ANIMATE ? ceil(iTime/timePerAngle) * 180.0 : 0.0);

    if(val > 0.96){
        float halfTime = timePerAngle * 0.5;
        float timer = mod(iTime, timePerAngle);
        timer = (timer > halfTime ? halfTime - abs(halfTime - timer) : timer) / halfTime;

        col -= col * smoothstep(round(angle), ceil(angle)-0.5, angle) * timer * 0.7;
    }
}


void drawForeLines(inout vec3 col, in vec2 fragCoord, in float seed, in phaseInfo phase)
{
    if(!ANIMATE){
        return;
    }

    vec2 center = iResolution.xy*0.5;
    fragCoord = rotateVec(fragCoord, center, iTime * 0.02); 

    float angle = (ang(fragCoord, center) + PI) * 180.0/(2.0*PI);
    float angleDisc = floor(angle);

    float maxLength = (iResolution.x + iResolution.y) * 1.0;

    float timePerAngle = getRand(angleDisc, seed + 128.0) * 10.0 + 5.0;
    float speedPerAngle = maxLength/timePerAngle;

    float timeStart = iTime + 0.0;

    float val = getRand(angleDisc, seed + (ANIMATE ? ceil((timeStart * speedPerAngle)/maxLength) * 10.0 : 0.0));

    if(val < 0.05){
        float currentLength = mod(timeStart * speedPerAngle, maxLength);
        float dist = length(fragCoord - center);

        float endMin = dist;
        float endMax = dist + 3.0;

        float startMin = currentLength - maxLength*0.5;
        float startMax = startMin + 3.0;

        float mixValue = 0.0;

        if(phase.id == 1){
            mixValue = getTimedMix(phase.time, TIMER_PHASE_LOOK, 2.0);
        }
        else if(phase.id > 1 && phase.id < 4){
            mixValue = 1.0;
        }

        col += smoothstep(round(angle), ceil(angle)-0.5, angle) 
               * smoothstep(startMin, startMax, dist)
               * smoothstep(endMin, endMax, currentLength)
               * (1.0 - (timePerAngle / 15.0))
               * mix(COL_PUPIL_OUTER_START, COL_PUPIL_OUTER_END, mixValue);
    }
}


void drawSmoothedCircle(in vec2 position, in float radius, in float lineWidth, in vec3 lineColor, in vec3 backgroundColor, inout vec3 currentColor, in vec2 fragCoord)
{
    float centerValue = length(fragCoord.xy - position);

	currentColor += vec3(smoothstep(radius + lineWidth * 0.25, radius , centerValue)) * (lineColor - currentColor)
         + vec3(smoothstep(radius, radius - lineWidth * 0.25, centerValue)) * (backgroundColor - currentColor);
}


void drawCircle(in vec2 position, in float radius, in float lineWidth, in vec3 lineColor, inout vec3 currentColor, in vec2 fragCoord)
{
    float pixelFract = calculatePixelValue(length(fragCoord - position) - radius, lineWidth);
    currentColor += vec3(pixelFract) * (lineColor - currentColor);
}


void drawFullCircle(in vec2 position, in float radius, in vec3 color, inout vec3 currentColor, in vec2 fragCoord)
{
    float pixelFract = calculatePixelValueFilled(length(fragCoord - position) - radius, radius);
    currentColor += vec3(pixelFract) * (color - currentColor);
}


void drawFilledSmoothedOval(in vec2 center, in float width, in float height, in vec3 color, inout vec3 currentColor, in vec2 fragCoord)
{
    vec2 size = vec2(width, height);

    float curValue = length((fragCoord.xy - center)/size);
    float minValue = 1.0;
    float maxValue = 1.0 - 0.04;

    currentColor += vec3(smoothstep(maxValue, minValue, curValue)) * (color - currentColor);
}


float calcFrayHeight(in float time, in float seed, in float angle, in float divs, in float amount)
{
    return getRandSmooth(time + angle * divs, seed, TAU * divs) * amount;
}


float calcRandHeightMultiplier(in float time, in float seed)
{
    return 1.0+(getRand(1.0 + (ANIMATE ? time : 0.0) * 1.0, seed) - 0.5) * 2.0;
}


float calcRandWobble(in float seed, in float angle, in float direction, in float divs, in float offset, in float amount)
{
    return (0.5-getRandSmooth(angle * divs + (ANIMATE ? iTime * -direction : 0.0), seed + offset, TAU * divs)) * amount;
}


vec3 drawFrayedCircle(in float time, in float seed, in float direction, in vec2 position, in phaseInfo phase, in float radius, in float lineWidth, in vec3 currentColor, in vec2 fragCoord)
{  
    if(!ANIMATE){
        time = 0.0;
    }

    float mixValue = 0.0;

    if(phase.id == 1){
        mixValue = getTimedMix(phase.time, TIMER_PHASE_LOOK, 2.0);
    }
    else if(phase.id > 1 && phase.id < 4){
        mixValue = 1.0;
    }

    float angle = atan(fragCoord.y - position.y, fragCoord.x - position.x) + PI;    

    float divs = 50.0 * radius/iResolution.x + 5.0 + 10.0 * mixValue;

    radius = radius + calcFrayHeight(time, seed, angle, divs, radius * 0.1)
        * calcRandHeightMultiplier(time, seed)
        + calcRandWobble(seed, angle, direction, 3.0, 0.0, radius * 0.1);

    radius += calcFrayHeight(2.0*time, seed, angle, divs, radius * 0.1)
        * calcRandHeightMultiplier(2.0*time, seed)
        - calcRandWobble(seed, angle, direction, 4.0, TAU*10.0, radius * 0.1);;

    float valueInner = length(vec2(1.0, 2.0) * (fragCoord - position)) - radius;
    float valueOuter = length(fragCoord - position) - radius;

    float offset = 0.2 + 0.5 * abs(sin(time * 0.2));
    vec3 colorTexture = COL_ORB_BACKGROUND - vec3(0.0, 0.0, mod(valueInner - iTime * 50.0, iResolution.x/12.0)/radius);

    vec3 lineColor = mix(vec3(0.0), COL_ORB_OUTLINE, mixValue) * vec3(offset);

    return vec3(smoothstep(radius + 1.0 + lineWidth * 0.25, radius , valueOuter)) * (lineColor - currentColor)
           + vec3(smoothstep(radius, radius - lineWidth * 0.25 * offset * 3.0, valueOuter)) 
           * (0.4 * colorTexture * valueInner/100.0 - (lineColor));
}


void drawParabola(in float parabolaY, in vec2 centerPoint, in float width,  in float lineWidth, in vec3 lineColor, inout vec3 currentColor, in vec2 fragCoord, out float height)
{   
    if(abs(fragCoord.x - centerPoint.x) <= width){
        float pixelFract = calculatePixelValue(fragCoord.y - parabolaY, 2.0 * lineWidth);
        currentColor = clamp(vec3(0.0), currentColor, vec3(1.0));
        currentColor += vec3(pixelFract) * (lineColor - currentColor);
    }
}


void drawBall(in float seed, in vec2 center, in vec2 pupilPosition, in float width, inout vec3 currentColor, in vec2  fragCoord)
{
    vec3 backgroundColor = COL_EYEBALL_COLOR;

    currentColor += backgroundColor - currentColor;

    vec2 toPosition = normalize(pupilPosition - center) * length(pupilPosition - center);

    float angle = ang(fragCoord, center - pupilPosition) + PI;
    float draw = length(fragCoord - pupilPosition);

    float value = getRand(angle * iResolution.x/100.0 + getRand(draw / length(iResolution.xy) * 100.0, seed) 
                        *  iResolution.x/100.0 + iTime, seed, 2.0 * PI * 12.0) - 0.4;

    currentColor += step(width*0.45, draw)
                    * smoothstep(value, value + 8.0, (length(pupilPosition - fragCoord)+1.0)/width*2.0) * 6.0
                    * (COL_EYEBALL_COLOR);

    currentColor += smoothstep(width*0.55,width*0.6, width - draw)
                    * (COL_EYEBALL_TEXTURE_COLOR);
}


void drawCanthus(in vec2 position, in float width, inout vec3 currentColor, in vec2 fragCoord)
{
    drawFilledSmoothedOval(position, width * 1.38, width * 1.4 * 0.55, COL_CANTHUS, currentColor, fragCoord);
}


vec2 addSaccade(in float seed, in float saccadeTimeMultiplier, in float movementMultiplier)
{
    if(!ANIMATE){
    	return vec2(0.0);
    }

    float saccadeInterval = max((getRand(getFixedValue(2.0), seed)+0.3)*saccadeTimeMultiplier, 0.6);  
    float saccadeTimeFrame = getFixedValue(saccadeInterval);
    float randomValue = getRand(saccadeTimeFrame / saccadeInterval, seed) - 0.5;
    return movementMultiplier * randomValue * normalize(vec2(cos(randomValue), sin(randomValue)));
}


vec2 randomPosition(in float seed)
{
    if(!ANIMATE){
        return vec2(0.0);
    }

    float offsetInterval = max((getRand(getFixedValue(32.0), seed)+0.5)*5.0, 2.0);
    float timeFrame = getFixedValue(offsetInterval);
    float randomValue = getRand(timeFrame / offsetInterval, seed);
    float randomValue2 = getRand(timeFrame / offsetInterval, 32.0 + seed);
    return vec2(randomValue * iResolution.x, randomValue2 * iResolution.y);
}


float addPupilSize(in float seed)
{
    if(!ANIMATE){
        return 0.0;
    }

    float changeInterval = max(getRand(getFixedValue(2.0), seed), 0.6) * 15.0;
    float timeFrame = getFixedValue(changeInterval);
    float randomValue = getRand(timeFrame / changeInterval, 16.0 + seed) - 0.5;
    return randomValue;
}


vec2 calculatePupilPosition(in float seed, in vec2 center, in float width, in vec2 fragCoord)
{
    vec2 position = center;
    position += addSaccade(seed, 2.0, 5.0);
    vec2 toMouse = vec2(0.0);
    float maxDistance = width * 0.5;
    float toMouseAngle = 0.0;

    if(MOUSE_LOOK){
        toMouse = position - iMouse.xy;
        toMouseAngle = -atan(position.y - iMouse.y, position.x - iMouse.x);
    }
    else{
        vec2 offset =  randomPosition(seed) * 0.5;
        vec2 fixedPos = iResolution.xy * 0.5;
        toMouse = (fixedPos - offset) * 0.15;
        toMouseAngle = -ang(fixedPos * 0.5, offset);
    }

    float squishValue = 1.0-min(1.0, length(toMouse)/maxDistance)*0.15;
    position -= fragCoord;
    position +=  rotate(toMouseAngle) * vec2(-min(length(toMouse), maxDistance), 0.0);
    position *=  rotate(toMouseAngle);
    position.x *= 1.0/squishValue;
    position += fragCoord;
    return position;
}


void drawPupil(in float seed, in int phase, in float phaseTimer, in vec2 position, in vec2 offset, in float width, inout vec3 currentColor, in vec2 fragCoord)
{   

    float mixValue = 0.0;

    if(phase == 1){
        mixValue = getTimedMix(phaseTimer, TIMER_PHASE_LOOK, 2.0);
    }
    else if(phase > 1 && phase < 4){
        mixValue = 1.0;
    }

    vec3 pupilColor = mix(COL_PUPIL_OUTER_START, COL_PUPIL_OUTER_END, mixValue);
    float pupilOuterSize = width * 0.5;
    float pupilInnerSize = width * 0.12 + addPupilSize(seed) * 5.0;
    float pupilCenterSize = (pupilOuterSize + pupilInnerSize) * 0.5;

    drawSmoothedCircle(position, pupilCenterSize, width - pupilInnerSize, pupilColor, COL_PUPIL_INNER, currentColor, fragCoord);
    drawFullCircle(position, pupilInnerSize - 2.0, vec3(0.0), currentColor, fragCoord);
    drawCircle(position, pupilInnerSize, 15.0, pupilColor, currentColor, fragCoord);
}


void drawInnerEye(in phaseInfo phase, in float seed, in float width, in float height, in vec2 position, in vec2 offset, inout vec3 currentColor, in vec2 fragCoord)
{
    if(abs(fragCoord.y - position.y) > abs(height) ||
       abs(fragCoord.x - position.x) > abs(width)) {
    	return;
    }

    vec2 pupilPosition = position;

    if(phase.id == 0 || phase.id == 4){
        pupilPosition = calculatePupilPosition(seed, position, width*0.85, fragCoord);
    }
    else{
        pupilPosition += addSaccade(seed, 2.0, 7.0);
    }

    drawBall(seed, position, pupilPosition, width, currentColor, fragCoord);
    drawCanthus(position, width*0.68, currentColor, fragCoord);
    drawPupil(seed, phase.id, phase.time, pupilPosition, offset, width*0.85, currentColor, fragCoord);
}


void drawOrbBackground(in orbInfo orb, in phaseInfo phase, inout vec3 col, in vec2 fragCoord)
{   
    col += drawFrayedCircle(orb.timedSeed, orb.fixedSeed, 1.0, orb.position, phase, orb.size*0.65, 10.0, col, fragCoord);;
}


float calculateOpenness(in float seed)
{
    float blinkInterval = 1.5 + 1.5 * getRand(seed);
    float blinkDuration = 0.1;
    float maxOpeness = 1.0;
    float eyeOpen = maxOpeness;

    float blinkTimer = mod(iTime, blinkInterval);

    if(blinkTimer <= blinkDuration && ANIMATE){
    	eyeOpen = maxOpeness-normalizedCos(blinkTimer * maxOpeness/blinkDuration);
    }

    return eyeOpen;
}


void drawEye(in orbInfo orb, in phaseInfo phase, inout vec3 currentColor, in vec2 fragCoord)
{
    float a;
    float width = orb.size;
    float height = width * 0.5;

    float centerHeight = height * calculateOpenness(orb.fixedSeed);

    vec2 startPos = vec2(orb.position.x - width, orb.position.y);
    vec2 centerPointUp = startPos + vec2(orb.size, centerHeight);
    vec2 centerPointDown = startPos + vec2(width, -centerHeight);
    float parabolaYup = calculateParabola(startPos, centerPointUp, fragCoord.x, a, height);
    float parabolaYDown = calculateParabola(startPos, centerPointDown, fragCoord.x, a, height);

    float upperLineWidth = 5.0;
    float lowerLineWidth = 3.0;

    vec2 offset = 0.1 * (orb.position - iMouse.xy);

    drawInnerEye(phase, orb.fixedSeed, width, height, orb.position, offset, currentColor, fragCoord);
    drawParabola(parabolaYup, centerPointUp, width + 2.0, upperLineWidth, COL_OUTER_EYE, currentColor, fragCoord, height);
    drawParabola(parabolaYDown, centerPointDown, width, lowerLineWidth, COL_OUTER_EYE, currentColor, fragCoord, height);
}


void drawOrb(in orbInfo orb, in phaseInfo phase, inout vec3 currentColor, in vec2 fragCoord)
{    
    drawOrbBackground(orb, phase, currentColor, fragCoord);  
    drawEye(orb, phase, currentColor, fragCoord);
}


void splitImage(inout vec3 col, inout vec2 fragCoord, in float phaseTimer)
{
    float divs = 16.0;
    float pixels = iResolution.x/divs;
    float percentage = max((phaseTimer-TIMER_PHASE_SPLIT)/(TIMER_PHASE_FADEOUT-TIMER_PHASE_SPLIT), 0.0);

    float currentLength = length(fragCoord - iResolution.xy*0.5);
    float currentLengthDisc = currentLength/pixels - fract(currentLength/pixels);

    if(currentLengthDisc <= (percentage * length(iResolution.xy*0.5)/pixels)){
        float number = (fragCoord.y/pixels - fract(fragCoord.y/pixels));
        float direction = mod(number, 2.0) >= 1.0 ? -1.0 : 1.0;

        fragCoord.x += (iResolution.x/divs) * direction;
        fragCoord.y -= (iResolution.y/divs) * direction;
    }
}


void spliceImage(inout vec2 fragCoord)
{
    vec2 pos1 = vec2(0.8, 1.5);
    vec2 pos2 = vec2(0.8, 0.5);

    if((iResolution.y-fragCoord.y * pos1.y)/(fragCoord.x * pos1.x) * iResolution.x/iResolution.y > 1.0){
        fragCoord = fragCoord * mat2(0.5, 0.0, 0.0, 0.5);
    }

    if((fragCoord.y * pos2.y)/(fragCoord.x * pos2.x) * iResolution.x/iResolution.y > 1.0){
        fragCoord = fragCoord * mat2(1.5, 0.0, 0.0, 1.5);
    }
}


void drawRectVignette(inout vec3 currentColor, in vec2 fragCoord)
{
    float angle = ang(fragCoord, iResolution.xy * 0.5);
    float maxDistance = 50.0 + 100.0 * (0.5 - getRand(angle * 4.0, 0.0, 2.0 * PI * 4.0)) * sin(angle + iTime * 0.5);
    vec3 startColor = currentColor;

    float val = min(min(iResolution.x - fragCoord.x, iResolution.y - fragCoord.y), min(fragCoord.x, fragCoord.y));
    val = min(min(iResolution.x - fragCoord.x, iResolution.y - fragCoord.y), min(fragCoord.x, fragCoord.y));

    currentColor += vec3(smoothstep(0.0, 2.0, (maxDistance-val)/(maxDistance)) * 0.2) * 1.1;
}


vec2 calculateEyePos(in float startAngle, in float offset, in vec2 fragCoord)
{
    vec2 center = iResolution.xy*0.5;
    vec2 defDistance = vec2(offset, 0.0);
    vec2 position = center;

    position -= fragCoord;
    position += rotate(startAngle + (ANIMATE ? iTime * 0.1 : 0.0)) * defDistance;
    position += fragCoord;
    return position;
}


void drawBackground(inout vec3 currentColor, in vec2 fragCoord)
{
    float currentLength = length(fragCoord - iResolution.xy*0.5);
    float maxLength = length(iResolution.xy*0.5);
    float currentLengthRatio = currentLength/maxLength;
    float divs = iResolution.x/4.0;

    float colorValue = currentLength*2.0/maxLength;

    currentColor = COL_BACKGROUND * vec3(colorValue, colorValue, colorValue) 
                   * max(0.0, 1.0 - mod(currentLength - iTime * 0.1 * divs, divs)/maxLength);
}


void fadeOut(in float phaseTimer, inout vec3 currentColor){

    float value = getTimedMix(phaseTimer, TIMER_PHASE_FADEOUT, TIMER_PHASE_RESET - TIMER_PHASE_FADEOUT - 1.0);
    currentColor -= vec3(1.0) * value;
}


void fadeIn(in float phaseTimer, inout vec3 currentColor){

    float value = getTimedMix(phaseTimer, TIMER_PHASE_RESET, TIMER_PHASE_ALL - TIMER_PHASE_RESET);
    currentColor += vec3(1.0) * value;
}


int setPhase(in float time, out float phaseTimer)
{
    int phase = 0;
    phaseTimer = mod(time, TIMER_PHASE_ALL);

    if(phaseTimer > TIMER_PHASE_RESET){
        phase = 4;
    }
    else if(phaseTimer > TIMER_PHASE_FADEOUT){
        phase = 3;
    }
    else if(phaseTimer > TIMER_PHASE_SPLIT){
        phase = 2;
    }
    else if(phaseTimer > TIMER_PHASE_LOOK){
        phase = 1;
    }
    else{
        phase = 0;
    }

    return phase;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 col = vec3(0.0);
    float time = mod(iTime + TIME_OFFSET, 16384.0);
    float phaseTimer;

    phaseInfo phase = phaseInfo(setPhase(time, phaseTimer), phaseTimer);

    drawBackground(col, fragCoord);

    if(phase.id >= 3 && phase.id < 4){
        spliceImage(fragCoord);
    }

    if(phase.id >= 2 && phase.id < 4){
        splitImage(col, fragCoord, phaseTimer);  
    }

    drawBackLines(col, fragCoord);  
    drawForeLines(col, fragCoord, 0.0, phase);

    vec2[] positions = vec2[] (
        	iResolution.xy*0.5,
       		calculateEyePos(TAU, iResolution.x*0.3, fragCoord),
        	calculateEyePos(TAU/3.0, iResolution.x*0.3, fragCoord),
        	calculateEyePos(TAU/3.0 * 2.0, iResolution.x*0.3, fragCoord)
        );

    float[] sizes = float[] (
        	iResolution.x*0.105,
        	iResolution.x*0.055,
        	iResolution.x*0.055,
        	iResolution.x*0.055
    	);

    for(int i = 0; i < positions.length(); i++){
        float seed = float(i) * 100.0;
        orbInfo newOrb = orbInfo(positions[i], sizes[i], time + seed, seed);        
        drawOrb(newOrb, phase, col, fragCoord);
    }

    if(phase.id >= 3){
        fadeOut(phaseTimer, col);
    }

    if(phase.id >= 4){
        fadeIn(phaseTimer, col);
    }

    drawRectVignette(col, fragCoord);

    fragColor = vec4(col, 1.0);
}

float Band(float t, float start, float end, float blur){
	float step1 = smoothstep(start-blur, start + blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);

    return step1*step2;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur) {
	float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);
    return band1*band2;

}

float remap01(float a, float b, float p) {
	return (p-a)/(b-a);
}

float remap(float a, float b, float c, float d, float p) {
	return remap01(a, b, p)*(d-c)+c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float t = iTime;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);

    float mask = 0.;

    float y = uv.y;
    float a = 0.;
    float m = sin(1.3*t)*sin(y*10.+5.5*3.1415);

    m *= (2.*uv.x + 2.)*.05;

    float x = uv.x - m;

    float blurd = remap(-.5, .5, .03, .4, y);
    float bluru = remap(-.5, .5, .03, .4, -y);
    blurd = pow(blurd*3.5, 2.+ .7*uv.x);
    bluru = pow(bluru*3.5, 2.+ .7*uv.x);

    mask = Rect(vec2(x, y), -.07, .07, -.5, .5, blurd)
         + Rect(vec2(x, y), -.47, -.33, -.5, .5, blurd)
         + Rect(vec2(x, y), -.87, -.73, -.5, .5, blurd)
         + Rect(vec2(x, y), .33, .47, -.5, .5, blurd)
         + Rect(vec2(x, y), .73, .87, -.5, .5, blurd);
    mask += Rect(vec2(x, y), .13, .27, -.5, .5, bluru)
          + Rect(vec2(x, y), -.27, -.13, -.5, .5, bluru)
          + Rect(vec2(x, y), -.67, -.53, -.5, .5, bluru)
          + Rect(vec2(x, y), .53, .67, -.5, .5, bluru);
    col = vec3(1., .9, .71)*mask;

    // Output to screen
    fragColor = vec4(col,1.0);
}

