#define MouseV (iMouse.y / iResolution.y)
#define MouseU (iMouse.x / iResolution.x)
#define UV_DEBUG 0
#define SHOW_CONTROL_RIG 1
#define DEFORM_BEZIER 1

//Inspired by https://www.shadertoy.com/view/4ljfRD
float drawLine (vec2 p1, vec2 p2, vec2 uv, float a)
{
    float r = 0.;
    float one_px = 1. / iResolution.x; //not really one px

    // get dist between points
    float d = distance(p1, p2);

    // get dist between current pixel and p1
    float duv = distance(p1, uv);

    //if point is on line, according to dist, it should match current uv 
    r = 1.-floor(1.-(a*one_px)+distance (mix(p1, p2, clamp(duv/d, 0., 1.)),  uv));

    return r;
}

vec3 DrawLine(vec2 A, vec2 B, vec2 UV, float Thickness, vec3 Color)
{
    vec2 Direction = normalize(B - A);
    float Dist = distance(A, B);

    vec3 Right = cross(vec3(Direction, 0.), vec3(0., 0., 1.));

    return vec3(0.);
}

vec2 QuadraticBezier(vec2 A, vec2 B, vec2 C, float t)
{
	vec2 AB = mix(A, B, t);
	vec2 BC = mix(B, C, t);

	vec2 P = mix(AB, BC, t);

	return P;
}

vec2 CubicBezier(float t, vec2 A, vec2 B, vec2 C, vec2 D)
{
	vec2 AB = mix(A, B, t);
	vec2 BC = mix(B, C, t);
	vec2 CD = mix(C, D, t);

	vec2 P = QuadraticBezier(AB, BC, CD, t);

	return P;
}

vec2 DeformUVByControlPoints_Linear(vec2 UV, vec2 A, vec2 TangentA, vec2 TangentB, vec2 B)
{
    float x;

    if (UV.x <= TangentA.x) {
    	x = (UV.x - A.x) / (TangentA.x - A.x);

        UV.y += 0.5 - mix(A.y, TangentA.y, x);
    }
    else if (UV.x <= TangentB.x) {
        x = (UV.x - TangentA.x) / (TangentB.x - TangentA.x);

        UV.y +=  0.5 - mix(TangentA.y, TangentB.y, x);

    }
    else if (UV.x <= B.x) {
        x = (UV.x - TangentB.x) / (B.x - TangentB.x);

        UV.y += 0.5 - mix(TangentB.y, B.y, x);
    }

    return UV;
}

vec2 DeformUVByControlPoints_Bezier(vec2 UV, vec2 A, vec2 TangentA, vec2 TangentB, vec2 B)
{
	float uvx = UV.x;

    vec2 NewUV = UV;
    NewUV.y += 0.5 - CubicBezier(UV.x, A, TangentA, TangentB, B).y;

	return NewUV;
}

vec3 OverlayControlRig(vec2 uv, vec2 Start, vec2 StartControl, vec2 End, vec2 EndControl, float size)
{
 	vec3 rigColor = vec3(0.);

    vec2 aspect = vec2(1., iResolution.y / iResolution.x);
    // render control points
    if (length((Start - uv) * aspect) < size) {
    	rigColor += vec3(1.,1.,0.);
    }
    if (length((StartControl - uv) * aspect) < size) {
    	rigColor += vec3(0.,1.,0.);
    }
    if (length((End - uv) * aspect) < size) {
    	rigColor += vec3(0.,1.,1.);
    }
    if (length((EndControl - uv) * aspect) < size) {
    	rigColor += vec3(0.,0.,1.);
    }

    // draw lines for each control point segment
    float lines = 0.
        + drawLine(Start, StartControl, uv, 5.)
        + drawLine(StartControl, EndControl, uv, 5.)
        + drawLine(EndControl, End, uv, 5.)
        ;
    rigColor += vec3(lines, 0., 0.);

    return rigColor;
}

vec2 SheerEdges(vec2 UV, float LeftShearDistance, float RightShearDistance)
{
	float RightShearSpaceX = UV.x - (2. * RightShearDistance * UV.y) + RightShearDistance;
	vec2 ShearRightUV = vec2(RightShearSpaceX, UV.y);

	float TotalLeftShearDistance = LeftShearDistance + -1. * RightShearDistance;

    float TotalShearSpaceX = ShearRightUV.x - (2. * TotalLeftShearDistance * ShearRightUV.y) + TotalLeftShearDistance;
    vec2 TotalShearUV = vec2(TotalShearSpaceX, ShearRightUV.y);

    float DestretchX = TotalShearUV.x / (1. - (2. * TotalLeftShearDistance * ShearRightUV.y) + TotalLeftShearDistance);
    TotalShearUV.x = DestretchX;

	return TotalShearUV;
}

vec2 DeformAndSheer(vec2 UV, vec2 Start, vec2 StartControl, vec2 EndControl, vec2 End, float SkewLeft, float SkewRight)
{
    return DeformUVByControlPoints_Bezier(SheerEdges(UV, SkewLeft, SkewRight), Start, StartControl, EndControl, End);
}

vec2 RightVector(vec2 a, vec2 b)
{
    vec2 aa = a;
    vec2 ba = b;

    vec2 diff = ba - aa;
    vec2 right = vec2(diff.y, -diff.x);

    return vec2(right.x, right.y) + aa;
}

float PointSlope(vec2 a, vec2 b)
{
    vec2 diff = b - a;
    return diff.y / diff.x;
}

vec2 InterceptionOfHorizontalLine(vec2 a, vec2 b, float line)
{
    float x = ((line - a.y) / PointSlope(a, b)) + a.x;
    return vec2(x, line);
}

vec2 ToView(vec2 P)
{
     return P * iResolution.xy;
}

vec2 ToUV(vec2 P)
{
     return P / iResolution.xy;
}

