    if (1. < deformed_uv.x) color *= 0.;
    if (1. < deformed_uv.y) color *= 0.;

    #if SHOW_CONTROL_RIG
    color = max(OverlayControlRig(uv, Pstart, PstartControl, Pend, PendControl, 0.01), color);
    #endif

    if (length((EndSkewControl - uv) * aspect) < 0.01) {
    	color += vec3(1.,1.,1.);
    } 
    color += vec3(0,1,0) * drawLine(Pend, EndSkewControl, uv, 5.);

    fragColor = vec4(color, 1.0);
}
*/
vec2 CaluclateUVs(vec2 UV, vec2 Start, vec2 StartControl, vec2 End, vec2 EndControl, vec2 Dimensions)
{
    float Aspect = Dimensions.y / Dimensions.x;
	// Calculate the skew amount for the start point.
    vec2 StartSkewControl = RightVector(Start * Dimensions.xy, StartControl * Dimensions.xy) / Dimensions.xy;      
    float SkewStart = (InterceptionOfHorizontalLine(Start, StartSkewControl, 1.).x - Start.x) / Aspect;

    // Calculate the skew amount for the end point.
    vec2 EndSkewControl = RightVector(End * Dimensions.xy, EndControl * Dimensions.xy) / Dimensions.xy;      
    float SkewEnd = (InterceptionOfHorizontalLine(End, EndSkewControl, 1.).x - End.x) / Aspect;

    // Reproject TexCoords so x=0 is at the start point and x=1 is at the end point. 
    vec2 PointSpaceUV = vec2((UV.x - Start.x) / (End.x - Start.x), UV.y);
    // Take the reprojected uvs deform them along a cubic spline and sheer the start and end edges.
    vec2 DeformationSpaceUV = DeformAndSheer(PointSpaceUV,
    	Start, StartControl,
    	EndControl, End, 
    	SkewStart, SkewEnd
    );

    return DeformationSpaceUV;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 StartPointAndControl = vec4(0.2, 0.5, 0.33, 0.4);
    vec4 EndPointAndControl = vec4(0.8, 0.5, MouseU, MouseV);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 Dimensions = iResolution.xy;
    vec2 TexCoords = ToUV(fragCoord);

    // Setup Start, End and respective Control Points
    vec2 Start = StartPointAndControl.rg;
	vec2 StartControl = StartPointAndControl.ba + vec2(0., .2*sin(iTime));    
    vec2 End = EndPointAndControl.xy;
	vec2 EndControl = EndPointAndControl.zw;

    vec2 DeformationSpaceUV = CaluclateUVs(TexCoords, Start, StartControl, End, EndControl, Dimensions);

    vec3 FinalColor = vec3(0.);
    // Sample texture with deformed uvs.
    FinalColor += texture(iChannel0, DeformationSpaceUV).rgb;
    // Cut wrapped artefacts around deformed uv projection.
    if (0. > DeformationSpaceUV.x) FinalColor *= 0.;
    if (0. > DeformationSpaceUV.y) FinalColor *= 0.;
    if (1. < DeformationSpaceUV.x) FinalColor *= 0.;
    if (1. < DeformationSpaceUV.y) FinalColor *= 0.;

    #if SHOW_CONTROL_RIG
    // Show cubic spline control points.
    FinalColor += OverlayControlRig(TexCoords, Start, StartControl, End, EndControl, 0.01);
    #endif

    fragColor = vec4(FinalColor, 1.0);
}

