// Gradient noise with analytic derivatives (and hash function) by iq
vec2 hash( in vec2 x )  // replace this by something better
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}


// return gradient noise (in x) and its derivatives (in yz)
vec3 noised( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

#if 1
    // quintic interpolation
    vec2 u = f*f*f*(f*(f*6.0-15.0)+10.0);
    vec2 du = 30.0*f*f*(f*(f-2.0)+1.0);
#else
    // cubic interpolation
    vec2 u = f*f*(3.0-2.0*f);
    vec2 du = 6.0*f*(1.0-f);
#endif    

    vec2 ga = hash( i + vec2(0.0,0.0) );
    vec2 gb = hash( i + vec2(1.0,0.0) );
    vec2 gc = hash( i + vec2(0.0,1.0) );
    vec2 gd = hash( i + vec2(1.0,1.0) );

    float va = dot( ga, f - vec2(0.0,0.0) );
    float vb = dot( gb, f - vec2(1.0,0.0) );
    float vc = dot( gc, f - vec2(0.0,1.0) );
    float vd = dot( gd, f - vec2(1.0,1.0) );

    return vec3( va + u.x*(vb-va) + u.y*(vc-va) + u.x*u.y*(va-vb-vc+vd),   // value
                 ga + u.x*(gb-ga) + u.y*(gc-ga) + u.x*u.y*(ga-gb-gc+gd) +  // derivatives
                 du * (u.yx*(va-vb-vc+vd) + vec2(vb,vc) - va));
}

#define STEPS 8


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.,0.,0., 1.);

    vec2 p = 10.*fragCoord.xy / iResolution.y;

    vec2 o = vec2(0.0, 5);
    vec2 d = vec2(1.0, 0.0);

    float s0 = dot(p-o, d);
    float distSquared;
    float minDistSquared = -1.0;
    for (int i = -2; i <= 2; ++i) {
        float s = s0 + float(i)*0.5;
        for (int j = 0; j < STEPS; ++j) {

            vec3 nx = noised(vec2(s, 0.+iTime));
            vec3 ny = noised(vec2(s, 10.+iTime));

            vec2 dn = vec2(nx.y, ny.y);

            vec2 m = s*d + o + vec2(nx.x, ny.x);
            vec2 dm = dn + d;
            vec2 off = m - p;
            distSquared = dot(off, off);
            float ddist = dot(dm, off)/max(.5,length(off));

            s = s - ddist*0.1/min(1., length(dm));
        }
        if (distSquared < minDistSquared || minDistSquared < 0.0) {
            minDistSquared = distSquared;
        }
    }

    float c = sqrt(minDistSquared);

    fragColor.x = fragColor.y = fragColor.z = 1.0 - c*.6;

    // Mouse diagnostic points

    // This section just does the same gradient descent but starting from
    // the mouse coordinates instead of the current frag coords,
    // in order to visualize the gradient descent steps
    vec2 pm = 10.*iMouse.xy / iResolution.y;

    s0 = dot(pm-o, d);
    minDistSquared = -1.0;
    vec4 finalProposal = vec4(0., 0., 0., 0.);
    for (int i = -2; i <= 2; ++i) {
        float s = s0 + float(i)*0.5;
        vec4 colorProposal = vec4(0., 0., 0., 0.);
        for (int j = 0; j < STEPS; ++j) {

            vec3 nx = noised(vec2(s, 0.+iTime));
            vec3 ny = noised(vec2(s, 10.+iTime));

            vec2 dn = vec2(nx.y, ny.y);

            vec2 m = s*d + o + vec2(nx.x, ny.x);
            if (length(m - p) < .1) {
                float progress = float(j)/float(STEPS-1);
                progress *= progress*progress;
                colorProposal = vec4(1.0 - progress, progress, 0.2, 1.);
            }
            vec2 dm = dn + d;
            vec2 off = m - pm;
            distSquared = dot(off, off);
            float ddist = dot(dm, off)/max(.5,length(off));

            s = s - ddist*0.1/min(1., length(dm));
        }
        if (distSquared < minDistSquared || minDistSquared < 0.0) {
            finalProposal = colorProposal;
            minDistSquared = distSquared;
        }
    }
    if (finalProposal.w != 0.) {
        fragColor = finalProposal;
    }
}

