}

float sdfCircle(vec2 coord, vec2 center, float radius)
{
	vec2 offset = coord - center;
	return sqrt((offset.x * offset.x) + (offset.y * offset.y)) - radius;
}

float sdfTorus(vec2 coord, vec2 center, float radius1, float radius2)
{
    vec2 offset = coord - center;
    return abs(sqrt((offset.x * offset.x) + (offset.y * offset.y)) - radius1) - radius2;
}

float sdfEclipse(vec2 coord, vec2 center, float a, float b)
{
    float a2 = a * a;
    float b2 = b * b;
    return (b2 * (coord.x - center.x) * (coord.x - center.x) +
        a2 * (coord.y - center.y) * (coord.y - center.y) - a2 * b2) / (a2 * b2);
}

float sdfBox(vec2 coord,  vec2 center, float width, float height)
{
    vec2 d = abs(coord - center) - vec2(width, height);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdfRoundBox(vec2 coord,  vec2 center, float width, float height, float r)
{
    vec2 d = abs(coord - center) - vec2(width, height);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - r;
}

//*****************************Hard Blend******************************
float sdfUnion(const float a,const float b) {
    return min(a, b);
}

float sdfDifference(const float a, const float b) {
    return max(a, -b);
}

float sdfIntersection(const float a, const float b) {
    return max(a, b);
}
//*********************************************************************

//******************************Soft Blend*****************************
// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }
//*********************************************************************


//*********************************************************************
// https://www.jcohen.name/papers/Ferreira_Levelsets_2007.pdf
float sdfBlending(float A, float B, const float alpha, float beta, float gamma) {
    // A is the SDF the first geometry, B is the SDF of the second geometry and 
    // alpha is the filtering distance.
    float m = clamp(abs(alpha-A),0.0f,alpha)/alpha*clamp(abs(alpha-B),0.0f,alpha)/alpha;
    // the blended levelset. beta is the blending curve exponetial and gamma is the 
    // size of the extrusion in the blending area.
    float C = min(A, B) + pow(m, beta) * gamma;
    return C;
}
