#define PI acos(-1.)
#define TAU 6.283185

#define mo(p,d)p=abs(p)-d;if(p.y>p.x)p=p.yx
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define crep(p,c,l) p=(p-c*clamp(round(p/c),-l,l))
#define hash11(x) fract(sin(x)*146.4)

#define dt(speed) fract(iTime*speed)

// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

float stmin (float a, float b, float k, float n)
{
  float st = k/n;
  float u = b-k;
  return min(min(a,b),0.5*(u+a+abs(mod(u-a+st,2.*st)-st)));
}

void moda (inout vec2 p, float rep)
{
  float per = (2.*PI)/rep;
  float a = mod(atan(p.y,p.x),per)-per*0.5;
  p = vec2(cos(a),sin(a))*length(p);
}

float box (vec3 p, vec3 c)
{
  vec3 q = abs(p)-c;
  return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float cyl(vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

float torus (vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz),p.y)-t.x;
  return length(q)-t.y;
}

float buildings (vec3 p)
{
  vec3 pp = p;

  mo(p.xz, vec2(10.));
  float g = cyl(p.xzy,0.5,50.);
  p.x -= 5.;
  float d = min(g,box(p,vec3(5.,50.,6.5)));

  p = pp;
  p.z -= 3.5;
  p.x = abs(p.x)-15.;
  crep(p.x, 1.,4.);
  d = max(d,-cyl(p.xzy,0.3,45.));

  return d;
}

float staircase (vec3 p)
{
  p.xz *= rot(PI/4.);
  float width = 3.;
  vec3 pp = p;
  float d = cyl(p.xzy, 0.1, 50.);

  float per = 10.;
  p.xz *= rot(PI/5.);
  crep(p.y,per,3.);
  moda(p.xz,5.);
  p.x -= width;
  d = min(d,box(p,vec3(0.25,0.25,5.)));

  p =pp;
  moda(p.xz,5.);
  p.x -= width*0.95;
  d = min(d,cyl(p.xzy,0.1,50.));

  p = pp;
  float sper = 1.;
  float sid = round(p.y/sper);
  crep(p.y,sper, 50.);

  p.xz *= rot(sid*0.3); 
  p.x -= width*0.5;
  d = min(d,box(p,vec3(width*0.6,0.15,1.)));

  return d;
}

float staircases (vec3 p)
{
  p.xz *= rot(PI/4.);
  moda(p.xz, 4.);
  p.x -= 20.;
  return staircase(p);
}

float balcony (vec3 p)
{
  mo(p.xz, vec2(10.));
  float per = 8.;
  crep(p.y,per,6.);
  p.xz *= rot(PI/4.);
  float d = box(p,vec3(4.,1.5,4.));

  crep(p.y,per,6.);
  p.x += 2.;
  d = max(d,-box(p,vec3(4.,0.2,4.)));

  return d;
}

float g1 = 0.; float gid;
float gems (vec3 p)
{
  p.y -= 4.;
  mo(p.xz,vec2(10.));
  p.z += 4.;
  gid = round(p.y/8.);
  crep(p.y, 8.,6.);
  float d = dot(p,normalize(sign(p)))-0.4;

  g1 += 0.01/(0.01+d*d);

  return d;
}

float SDF (vec3 p)
{
  float scene = stmin(balcony(p),buildings(p),0.4,2.);
  scene = min(scene,staircases(p));
  scene = min(scene,gems(p));

  return scene;
}

vec3 getnorm (vec3 p)
{
  vec2 eps = vec2(0.001,0.);
  return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

vec3 getcam (vec3 ro, vec3 ta, vec2 uv)
{
  vec3 f = normalize(ta-ro);
  vec3 l = normalize(cross(vec3(0.,1.,0.),f));
  vec3 u = normalize(cross(f,l));
  return normalize(f+l*uv.x+u*uv.y);
}


void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = (2.*fragCoord.xy - iResolution.xy)/iResolution.y;

  vec3 ro=vec3(1.2,-6.5,-7.), rd=getcam(ro,vec3(0.),uv), p=ro, col=vec3(0.), l=normalize(vec3(1.,2.,-3.));

  bool hit=false; 

  for(float i=0.;i<64.;i++)
  {
    float d = SDF(p);
    if (d<0.001)
    {
      hit = true;
      break;
    }
    p += d*rd*0.8;
  }
  float t = length(ro-p);

  if (hit)
  {
    vec3 n = getnorm(p);
    col = vec3(dot(n,l)*0.5+0.25);
  }

  col = mix(col,
          vec3(0.1,0.4,0.9),
          1.-exp(-0.0003*t*t)+texNoise(uv*0.45).x*length((p.y+3.)*0.2));
  col += g1*hash11(gid+dt(0.03))*0.35;
  col = pow(col,vec3(2.));

  fragColor = vec4(sqrt(clamp(col,0.,1.)),1.);
}

#define TAU 6.28318530717958
#define PI TAU / 2.

/*
Heavily referenced these functions from the Wikipedia article.
I don't really know the proof for the generalization...
https://en.wikipedia.org/wiki/Smoothstep
*/

float nCr(float n, float r) {
    float res = 1.;
    for (float i = 0.; i < r; ++i) {
        res *= (n - i) / (i + 1.);
    }
    return res;
}

float generalSmoothstep(int N, float x) {
    x = clamp(x, 0., 1.);
    float NN = float(N);
    float res = 0.;
    for (float n = 0.; n <= NN; ++n) {
        res += nCr(-NN - 1., n) * nCr(2. * NN + 1., NN - n) * pow(x, NN + n + 1.);
    }
    return res;
}

float generalSmoothstep(float N, float x) {
    float fracN = fract(N);
    int pN = int(floor(N));
    return mix(generalSmoothstep(pN, x), generalSmoothstep(pN + 1, x), fracN);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float dx = 1.0/iResolution.y;
    vec2 uv = 2. * (fragCoord/iResolution.xy) - 1.;
    // Multiply x by aspect ratio, only makes sense if screen is landscape though
    uv.x *= iResolution.x/iResolution.y;

    vec3 col;
    if (uv.x > -1. && uv.x < 1.) {
        //background
        col = (sin(PI * uv.x * 5.) * sin(PI * uv.y * 5.) > 0.) ? vec3(0.2) : vec3(0.25);

        //point
        vec2 p = 0.5 * (uv + 1.);

        //function
        float N = sin(iTime);
        N = N * N * 5.;
        float y = generalSmoothstep(N, p.x);
        float dy = generalSmoothstep(N, p.x + dx) - y;
        float di = abs(p.y - y) / sqrt(dx*dx+dy*dy);
        col = mix(col, vec3(.93, .23, .55), smoothstep(2., 0., di));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

//<3d simplex noise by nikat https://www.shadertoy.com/view/XsX3zB>
vec3 random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
	 w = max(0.6 - w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 /* multiply d by w^4 */
	 w *= w;
	 w *= w;
	 d *= w;

	 /* 3. return the sum of the four surflets */
	 return dot(d, vec4(52.0));
}
//</3d simplex noise by nikat https://www.shadertoy.com/view/XsX3zB>
float fbm(vec2 xy, float z, int octs){
    float f = 1.0;
    float a = 1.0;
    float t = 0.0;
    float a_bound = 0.0;
    for(int i=0;i<octs;i++){
        t += a*simplex3d(vec3(xy*f,z*f));
        f *= 2.0;
        a_bound += a;
        a *= 0.5;
    }
    return t/a_bound;
}
float noise_final_comp(vec2 xy, float z){
    float value = fbm(vec2(xy.x / 200.0+513.0, xy.y / 200.0+124.0), z, 3);
    value = 1.0-abs(value);
    value = value*value;
    return value*2.0-1.0;
}
float noise_f(vec2 xy, float z){
        float value = fbm(
            vec2((noise_final_comp(xy,       z)*15.0+xy.x) / 100.0,
                 (noise_final_comp(xy+300.0, z)*15.0+xy.y) / 100.0), z*1.5, 5);

        return max(0.0, min(1.0, (value*0.5+0.5)*1.3));
}
float noise_a(vec2 xy, float z){
        float value = fbm(
            vec2((xy.x) / 100.0,
                 (xy.y) / 100.0), z*1.5, 1);

        return max(0.0, min(1.0, (value*0.5+0.5)*1.3));
}
float noise_b(vec2 xy, float z){
        float value = fbm(
            vec2((xy.x) / 100.0,
                 (xy.y) / 100.0), z*1.5, 2);

        return max(0.0, min(1.0, (value*0.5+0.5)*1.3));
}
vec2 noise_c(vec2 xy, float z){
        vec2 value = 
            vec2((noise_final_comp(xy,       z)*15.0),
                 (noise_final_comp(xy+300.0, z)*15.0));
        value.x = max(0.0, min(1.0, (value.x*0.5+0.5)/10.0));    
        value.y = max(0.0, min(1.0, (value.y*0.5+0.5)/10.0));    
        return value;
}
float noise_e(vec2 xy, float z){
        float value = fbm(
            vec2((xy.x) / 100.0,
                 (xy.y) / 100.0), z*1.5, 5);

        return max(0.0, min(1.0, (value*0.5+0.5)*1.3));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord*500.0/iResolution.x;

    float pA = noise_a(uv,iTime*0.025+0.05*sin(iTime*0.2+(uv.x*0.3*(sin(iTime/30.0)-0.3)+uv.y)/265.0));
    float pB = noise_b(uv,iTime*0.025+0.05*sin(iTime*0.2+(uv.x*0.3*(sin(iTime/30.0)-0.3)+uv.y)/265.0));
    vec2  pC = noise_c(uv,iTime*0.025+0.05*sin(iTime*0.2+(uv.x*0.3*(sin(iTime/30.0)-0.3)+uv.y)/265.0));
    float pE = noise_e(uv,iTime*0.025+0.05*sin(iTime*0.2+(uv.x*0.3*(sin(iTime/30.0)-0.3)+uv.y)/265.0));
    float pF = noise_f(uv,iTime*0.025+0.05*sin(iTime*0.2+(uv.x*0.3*(sin(iTime/30.0)-0.3)+uv.y)/265.0));

    vec3 c1 = vec3(152.0,193.0,217.0)/255.0;
    vec3 c2 = vec3(224.0,251.0,252.0)/255.0;
    vec3 c3 = vec3(238.0,108.0,77.0)/255.0;
    vec3 c4 = vec3(41.0,50.0,65.0)/255.0;


    // Mouse code based (loosely) from iq (https://www.shadertoy.com/view/4djSDy)
    float s = (2.0*iMouse.x-iResolution.x) / iResolution.y;
    if( iMouse.z<0.001 ) s=1.0;

    float sAB=step((8.0  *fragCoord.x-iResolution.x) / iResolution.y-s,0.0);
    float sBC=step((4.0  *fragCoord.x-iResolution.x) / iResolution.y-s,0.0);
    float sCE=step((2.685*fragCoord.x-iResolution.x) / iResolution.y-s,0.0);
    float sEF=step((2.0  *fragCoord.x-iResolution.x) / iResolution.y-s,0.0);
    float p =pA*sAB+pB*(1.0-sAB)*sBC+pE*(1.0-sCE)*sEF+pF*(1.0-sEF);
    vec3 col = clamp(p*1.5-0.75,0.0,1.0)*1.0*c2+(1.0-clamp(abs(p-0.5)*5.0,0.0,1.0))*c3;

    col+=(pC.x*c1+pC.y*c4)*(1.0-sBC)*sCE;

    vec2 rC = vec2(max(iResolution.x,iResolution.y),
                   min(iResolution.x,iResolution.y));

    fragColor = vec4(col,p*clamp(1.3-1.5*length(fragCoord-0.5*vec2(rC.x,rC.y))/rC.y,0.0,1.0));
}

#define n 80.

void mainImage(out vec4 d, in vec2 p)
{   

    float t = 1000.+iTime/30.;
    vec2 f = vec2(sin(t)*iResolution.x,(cos(t)+.2)*iResolution.y);

    if (iMouse.x>0.) {
      f = iMouse.xy;
    };

    vec3 c = vec3(0.0);
    vec2 uv = p / iResolution.xy;

    float x = -3./tan(f.y/iResolution.y*2.);
    float a = .0;
    for (int i = 0; i < int(n); ++i)
    {
        x = uv.x * (x+3.) * (20.-(f.x/iResolution.x*20.) - (x+3.));

        if (
             x > (uv.y-.9)/.1 && x <= (uv.y + 1.0 / iResolution.y -.9)/.1
           ) { 
             a+=1./n/0.05;
           };
    }

    c = vec3(a);
    c *= vec3(cos(uv.x),cos(uv.x*uv.x),cos(uv.y));

    d = vec4(c, 1.0);
}

