#define SURF_DIST .005
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

// The calculation is based on the `sdPie` distance from the following 2d distance reference.
// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdPie3d(vec3 p, float rad, float r, float h) {
    p.xy = abs(p.xy);
    vec2 c = vec2(sin(rad),cos(rad));
    float d = max(p.y-h,length(p.xz) - r);
    float m = length(p.xz-c*clamp(dot(p.xz,c),0.0,r)); 
    return max(d,m*sign(c.y*p.x-c.x*p.z));
}

vec4 GetDist(vec3 p) {
    vec3 prevP = p;
    float lt = mod(iTime*20.0,420.0);
    if(lt>=360.0){
        lt = 360.0-((lt-360.0)/60.0*360.0);
    }

    float rad = radians((lt<180.0)?lt:180.0);
    float rad2 = radians((lt>=180.0)?lt-180.0:0.0);

    float t = mod(iTime*20.0,240.0);
    t = (t<180.0)?t:180.0-((t-180.0)/60.0*180.0);
    float rad3 = radians(t);

    // loading demo
    p.z+=1.6;
    float d = sdPie3d(p,rad,1.5,0.05);
    d = max(p.x,d);
    p*=-1.0;
    float d2 = sdPie3d(p,rad2,1.5,0.05);
    d2 = (rad2 != 0.0)?max(p.x,d2):100.0;
    d = min(d,d2);
    d2 = sdPie3d(p,radians(180.),0.75,0.06);
    d = max(-d2+0.01,d);

    // other demo
    p =prevP;
    p.z-=1.6;
    float d3 = sdPie3d(p,rad3,1.5,0.05);
    d2 = sdPie3d(p*matRotateY(radians(180.0))*matRotateZ(radians(90.0)),rad3,1.2,0.1);
    d3 = min(d3,d2);

    d = min(d,d3);

    vec3 col = 0.5 + 0.5*cos(iTime+p.xyz+vec3(0,1,2));
    vec4 res = vec4(col,d);

    vec4 model = res;
    return model;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 dO= vec4(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.w;
        vec4 dS = GetDist(p);
        dO.w += dS.w;
        dO.xyz = dS.xyz;
        if(dO.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 Bg(vec3 rd) {
    float k = rd.y*0.5+0.5;
    vec3 col = mix(vec3(.9,.8,.8),vec3(.7,.9,.5),k);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 3, -5.5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-60.0));
    ro.xz *= Rot(iTime*.3+1.0);
    #endif

    vec3 rd = R(uv, ro, vec3(0,-0.3,0), 1.);

    vec4 d = RayMarch(ro, rd);

    if(d.w<MAX_DIST) {
        vec3 p = ro + rd * d.w;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd,n);
        float spec = pow(max(0.0,r.y),30.);
        float dif = dot(n,normalize(vec3(1,2,3)))*0.5+0.5;
        col = mix(Bg(r),vec3(dif),0.5)+spec;
    } else {
        // background
        col += Bg(rd);
    }

    fragColor = vec4(col,1.0);
}

