// Math
#define M_PI 3.1415926535897932384626433832795

struct Ray {
    vec3 orig;
    vec3 dir;
};

bool nearZero(vec3 v) {
    float s = 1e-8;
    return (abs(v.x) < s) && (abs(v.y) < s) && (abs(v.z) < s);
}

vec4 quaternionMultiply(vec4 q1, vec4 q2) {
    vec3 v1 = q1.xyz;
    vec3 v2 = q2.xyz;

    float w = q1.w * q2.w * dot(v1, v2);

    vec3 xyz = v1 * q2.w + v2 * q1.w + cross(v1, v2);

    return vec4(xyz, w);
}

vec3 rotate(float angle, vec3 axis, vec3 point) {
    // point in quaternion form
    vec4 p4 = vec4(point, 0.f);
    float s = sin(angle / 2.f);
    float c = cos(angle / 2.f);

    vec4 q = vec4(s * axis.x, s * axis.y, s * axis.z, c);
    vec4 qConj = vec4(-q.xyz, c);

    return quaternionMultiply(quaternionMultiply(q, p4), qConj).xyz;
}

void rotateLeft(float angle, vec3 up, inout vec3 lookFrom) {
    lookFrom = rotate(angle, up, lookFrom);
}

void rotateUp(float angle, inout vec3 up, inout vec3 lookFrom) {
    vec3 axis = normalize(cross(lookFrom, up));
    up = rotate(angle, axis, up);
    lookFrom = rotate(angle, axis, lookFrom);
}

// 2D

vec3 gradient(float start, float end, float border, vec3 colorA, vec3 colorB, float val) {
    border = mix(start, end, border);

    float endA = start + (border - start) * 2.f;
    float startA = end - (end - border) * 2.f;

    return
        ((1.f - smoothstep(start, endA, val)) * step(start, val) * colorA  
        + smoothstep(start, endA, val) * (1.f - step(end, val)) * colorB)
        * (1.f - step(border, val))

        + ((1.f - smoothstep(startA, end, val)) * step(start, val) * colorA  
        + smoothstep(startA, end, val) * (1.f - step(end, val)) * colorB)
        * step(border, val);
}

vec3 gradient(float start, float end, float borderA, float borderB,
   vec3 colorA, vec3 colorB, vec3 colorC, float val) {
   float borderMiddle = mix(borderA, borderB, 0.5f);
   float middle = mix(start, end, borderMiddle);

   borderA /= borderMiddle;
   borderB = (borderB - borderMiddle) / (1.f - borderMiddle);

   return gradient(start, middle, borderA, colorA, colorB, val) +
       gradient(middle, end, borderB, colorB, colorC, val);
}

// Presets

vec3 sky(vec2 uv) {
    vec3 color1 = vec3(0.9f, 0.9f, 0.9f);
    vec3 color2 = vec3(0.8f, 0.97f, 1.f);
    vec3 color3 = vec3(0.989f, 0.969f, 0.84f);

    return gradient(0.f, 1.f, 0.1f + 0.1f * uv.x, 0.5f + 0.4f * uv.x,
        color1, color2, color3, uv.y);
}

// Random

int seed;
int coordId;

void encryptTea(inout uvec2 v)
{
    uint v0 = v[0], v1 = v[1], sum = 0u;
    uint delta = 0x9E3779B9u,
        k0 = 238450u, k1 = 89762u,
        k2 = 10989532u, k3 = 909243993u;

    for (int i = 0; i < 5; i++) {
        sum += delta;
        v0 += ((v1<<4) + k0) ^ (v1 + sum) ^ ((v1>>5) + k1);
        v1 += ((v0<<4) + k2) ^ (v0 + sum) ^ ((v0>>5) + k3);
    }
    v[0]=v0; v[1]=v1;
}

float getRandom(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }

vec2 getRandom() {
  	uvec2 v = uvec2(coordId, seed++);
  	encryptTea(v);
  	return fract(vec2(v) / vec2(0xffffffffu));
}

vec2 getRandom(float _min, float _max) {
    vec2 v = getRandom();
    return vec2(_min + (_max - _min) * v.x, _min + (_max - _min) * v.y);
}

vec3 randVec(float _min, float _max) {
    return vec3(getRandom(_min, _max), getRandom(_min, _max).x);
}

vec3 randomInUnitSphere() {
	while (true) {
		vec3 p = randVec(-1.f, 1.f);
		if (length(p) >= 1.f)
            continue;
		return p;
	}
}

// Effects

float snow(vec2 uv) {   
    float numberOfRegions = 200.f;

    float regionSize = 1.f / numberOfRegions;
    float index = round(gl_FragCoord.x * numberOfRegions / iResolution.x);

    float r = getRandom(index);
    vec2 center;

    center.y = mod(1. - mod(iTime / (1.5f + 5.f * r), 1.f) + r, 1.f);

    float halfWavesPerScreen = 5.f;
    float wave = sin(center.y * M_PI * halfWavesPerScreen) 
        * regionSize * 0.5f * getRandom(index * index);

    center.x = index * regionSize + wave;
    return smoothstep(0.004f, 0.003f, length(uv - center));   
}

// Camera
struct Camera {
    vec3 u;
    vec3 v;
    vec3 w;

    vec3 eye;
    vec2 fov;  

    vec3 vertical;
    vec3 horizontal;
};

void initCamera(out Camera cam, vec3 lookfrom, vec3 lookat, vec3 up, float fovy) {
    cam.eye = lookfrom;

    cam.w = normalize(lookfrom - lookat);
    cam.u = normalize(cross(up, cam.w));
    cam.v = cross(cam.w, cam.u);

    cam.fov.y = radians(fovy);
    cam.fov.x = 2.f * atan(iResolution.x / iResolution.y * tan(cam.fov.y / 2.f));

    cam.horizontal = tan(cam.fov.x / 2.f) * 2.f * cam.u;
    cam.vertical = tan(cam.fov.y / 2.f) * 2.f * cam.v;
}

Ray rayThroughFrag(vec2 uvc, in Camera cam) {
    Ray r;

    r.orig = cam.eye;
    r.dir = normalize(cam.horizontal * uvc.x + cam.vertical * uvc.y - cam.w);

    return r;
}

// Materials

#define LAMBERTIAN 0
#define METAL 1
#define DIELECTRIC 2

struct Material {
    int materialId;
    vec3 color;
    float fuzz;
    float indexOfRefraction;
};

// Objects
struct Sphere {
    float radius;
    vec3 center;
    vec3 scale;

    Material material;
};


struct CollisionInfo {
    float t;
    vec3 pos;
    vec3 normal;
    bool frontFace;

    Material material;
};


void setFaceNormal(Ray r, vec3 outwardNormal, inout CollisionInfo info) {
    info.frontFace = dot(normalize(r.dir), outwardNormal) < 0.f;
    info.normal = info.frontFace ? outwardNormal : -outwardNormal;
}

bool raySphereIntersection(Ray r, Sphere s, float tolerance, out CollisionInfo info) {
    // Scaled ray
    Ray rayI;
    rayI.orig = r.orig / s.scale;
    rayI.dir = normalize(r.dir / s.scale);

    vec3 oc = rayI.orig - s.center;
	float a = dot(rayI.dir, rayI.dir);
	float halfB = dot(rayI.dir, oc);
	float c = dot(oc, oc) - s.radius * s.radius;

    float d = halfB * halfB - a * c;
    if (d < 0.f)
        return false;

    float root = (-halfB - sqrt(d)) / a; 

    if (root <= tolerance) {
        root = (-halfB + sqrt(d)) / a;
    }

    if (root > tolerance) {
        // scale intersection point back
        vec3 I = rayI.orig + rayI.dir * root;
        info.pos = I * s.scale;
        info.t = length(info.pos - r.orig);

        info.material = s.material;

        // note: we divide by radius insted of normalizing to get hollow sphere trick
        vec3 outNormal = ((I - s.center) / s.radius) / s.scale;
        setFaceNormal(r, normalize(outNormal), info);

        return true;
    }

    return false;
}


// Arrays

#define MAX_SPHERES 15
int spheresCount = 0;
Sphere spheres[MAX_SPHERES];

bool hitWorld(Ray r, out CollisionInfo resInfo) {
    CollisionInfo info;

    bool res = false;
    for(int i = 0; i < spheresCount; ++i) {
        if (raySphereIntersection(r, spheres[i], 0.005f, info)) {
            if (!res || info.t < resInfo.t) {
                resInfo = info;
                res = true;
            }
        }
    }

    return res;
}

// Materials

float reflectance(float cosinus, float refIdx) {
    float r0 = (1.f - refIdx) / (1.f + refIdx);
    r0 = r0 * r0;
    return r0 + (1.f - r0) * pow((1.f - cosinus), 5.f);
}

bool scatterDiffuse(out Ray r, in CollisionInfo info) {
    r.orig = info.pos;
    r.dir = normalize(info.normal + randomInUnitSphere());
    if (nearZero(r.dir)) {
        r.dir = info.normal;
    }

    return true;
}

bool scatterMetal(inout Ray r, in CollisionInfo info) {
    vec3 reflected = reflect(r.dir, info.normal);
    r.orig = info.pos;
    r.dir = normalize(reflected + randomInUnitSphere() * info.material.fuzz);

    return dot(r.dir, info.normal) > 0.f;
}

bool scatterDielectric(inout Ray r, in CollisionInfo info) {
    float ir = info.material.indexOfRefraction;
    float refractionRatio = info.frontFace ? (1.f / ir) : ir;

    vec3 unitDirection = normalize(r.dir);
    float cosinus = min(dot(-unitDirection, info.normal), 1.f);
    float sinus = sqrt(1.0 - cosinus * cosinus);

    bool cannotRefract = refractionRatio * sinus > 1.f;
    vec3 direction;

    if (cannotRefract || reflectance(cosinus, refractionRatio) > getRandom(0.f, 1.f).x)
        direction = reflect(unitDirection, info.normal);
    else
        direction = refract(unitDirection, info.normal, refractionRatio);

    r.orig = info.pos;
    r.dir = normalize(direction);

    return true;
}

bool scatter(inout Ray r, in CollisionInfo info) {
    switch (info.material.materialId) {
        case LAMBERTIAN:
            return scatterDiffuse(r, info);
        case METAL:
            return scatterMetal(r, info);
        case DIELECTRIC:
            return scatterDielectric(r, info);
    }
}

vec3 randomRayColor(vec2 uv, in Camera cam) {
    vec2 coord = getRandom() / iResolution.xy + uv - vec2(0.5f);
    Ray r = rayThroughFrag(coord, cam);

    int depth = maxDepth;

    vec3 color = vec3(1.f);

    Ray currentR = r;

    CollisionInfo info;
    while(true) {
        if (depth-- == 0) {
            // can't find the light!
            color = vec3(0.f);
            break;
        }

        if (hitWorld(currentR, info)) {
            if (scatter(currentR, info)) {
                color *= info.material.color;
            } else {
                // the surface absorbed the ray!
                color = vec3(0.f);
                break;
            }
        } else {
            // found the light!
            color *= sky(uv);

            break;
        }
    }

    return color;
}

Sphere insideGlassBall;    
Sphere snowPile;
vec3 snowColor = vec3(1.f, 0.95f, 0.95f);

void createObjects() {
    Material snow;
    snow.color = snowColor;
    snow.materialId = LAMBERTIAN;

    Sphere lowerBody;
    lowerBody.radius = 1.f;
    lowerBody.material = snow;
    lowerBody.scale = vec3(1.f);
    lowerBody.center = vec3(0.f, lowerBody.radius - 1.5f, 0.f);
    spheres[spheresCount++] = lowerBody;

    Sphere upperBody = lowerBody;
    upperBody.radius *= 0.8f;
    float upperY = upperBody.center.y + upperBody.radius * 1.8f;
    upperBody.center = vec3(0.f, upperY, 0.f);
    spheres[spheresCount++] = upperBody;

    Sphere head = upperBody;
    head.radius *= 0.8f;
    float headY = head.center.y + head.radius * 1.8f;
    head.center = vec3(0.f, headY, 0.f);
    spheres[spheresCount++] = head;

    Material carrotMat;
    carrotMat.color = vec3(0.97f, 0.57f, 0.13f);
    carrotMat.materialId = LAMBERTIAN;

    Sphere carrot = head;
    carrot.center = vec3(0.f);
    carrot.scale = vec3(1.f, 0.2f, 0.2f);
    carrot.center.y = head.center.y / carrot.scale.y;
    carrot.center.x -= head.radius;
    carrot.material = carrotMat;
    spheres[spheresCount++] = carrot;

    Material coal;
    coal.color = vec3(0.21f, 0.27f, 0.31f);
    coal.materialId = LAMBERTIAN;

    Sphere rightEye;
    rightEye.radius = 0.1f;
    rightEye.scale = vec3(1.f);

    rightEye.center = vec3(-head.radius * 0.8f,
        head.center.y + head.radius / 2.f, -head.radius * 0.3f);

    rightEye.material = coal;
    spheres[spheresCount++] = rightEye;

    Sphere leftEye = rightEye;
    vec3 rightEyeCenter = rightEye.center;
    leftEye.center = vec3(rightEyeCenter.x, rightEyeCenter.y, head.radius * 0.3f);
    spheres[spheresCount++] = leftEye;

    Material ice;
    ice.color = vec3(0.8f, 0.8f, 0.8f);
    ice.materialId = METAL;
    ice.fuzz = 0.045f;

    Sphere snowInGlass;
    snowInGlass.radius = 2.f;
    snowInGlass.material = snow;
    snowInGlass.scale = vec3(1.f, 0.2f, 1.f);
    float snowInGlassRadius = snowInGlass.radius * snowInGlass.scale.y;
    vec3 snowInGlassCenter = lowerBody.center;
    snowInGlassCenter.y -= (lowerBody.radius + snowInGlassRadius) / snowInGlass.scale.y;
    snowInGlass.center = snowInGlassCenter;
    spheres[spheresCount++] = snowInGlass; 
    float snowInGlassDiameter = snowInGlassRadius * 2.f;

    Sphere ground;
    ground.radius = 7.f;
    ground.material = ice;
    ground.scale = vec3(1.f, 0.1f, 1.f);
    float groundY = (lowerBody.center.y - lowerBody.radius - snowInGlassDiameter) 
        / ground.scale.y - ground.radius;
    ground.center = vec3(0.f, groundY, 0.f);
    spheres[spheresCount++] = ground;

    snowPile.radius = 1.f;
    snowPile.material = snow;
    snowPile.scale = vec3(1.f, 9.f, 6.f);
    snowPile.center = vec3(4.3f, 0.f, 0.f);
    spheres[spheresCount++] = snowPile;

    Material glass;
    glass.color = vec3(1.f);
    glass.materialId = DIELECTRIC;
    glass.indexOfRefraction = 1.5f;

    Sphere glassSphere;
    glassSphere.radius = 3.1f;
    glassSphere.material = glass;
    glassSphere.scale = vec3(1.f);
    glassSphere.center = vec3(0.f, glassSphere.radius - snowInGlassDiameter
        + lowerBody.center.y - lowerBody.radius, 0.f);
    spheres[spheresCount++] = glassSphere;

    insideGlassBall = glassSphere;
    insideGlassBall.radius = -insideGlassBall.radius * 0.92f;
    spheres[spheresCount++] = insideGlassBall;
}

void addSnow(vec2 uv, in Camera cam, inout vec3 color) {
    vec2 coord = vec2(0.5f) / iResolution.xy + uv - vec2(0.5f);
    Ray r = rayThroughFrag(coord, cam);
    CollisionInfo info;

    if(raySphereIntersection(r, insideGlassBall, 0.005f, info)) {
        CollisionInfo info2;
        bool b = raySphereIntersection(r, snowPile, 0.005f, info2);
        if (!b || info2.t > info.t) {
            color = mix(color, snowColor, snow(uv));
        }
    }
}

