float perlin(vec2 uv){
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    vec2 lb = random2(i);
    vec2 rb = random2(i + vec2(1.,0.));
    vec2 lt = random2(i + vec2(0.,1.));
    vec2 rt = random2(i + vec2(1.,1.));

    float lb_value = dot(random2(lb),f);
    float rb_value = dot(random2(rb),f - vec2(1.,0.));
    float lt_value = dot(random2(lt),f - vec2(0.,1.));
    float rt_value = dot(random2(rt),f - vec2(1.,1.));

    f =smoothstep(0.,1.,f);
    float n = mix(
            mix(lb_value,rb_value,f.x),
            mix(lt_value,rt_value,f.x),
        f.y);


    return n;
}
#define OCTAVES 5
float fbm(vec2 uv){
    float amplitude = 1.;
    float frequency = 1.;
    float result = 0.;
    for(int i = 0; i < OCTAVES; i ++){
        result += amplitude * perlin(uv * frequency);
        amplitude *= .5;
        frequency *= 2.;
    }
    return result;
}
