float N21(vec2 p){
    p = fract(p * vec2(123.24,345.45));
    p += dot(p,p + 34.345);
    return fract(p.x * p.y);
}

float Circle(vec2 uv, vec2 p, float r, float blur){
    float d = length(uv - p);
    float c = S(r,r-blur,d);

    return c;
}

float Drop(vec2 UV, float t){
    vec2 aspect = vec2(2,1);
    vec2 uv = UV * 1. * aspect;
    uv.y += t * .25;
    vec2 gv = fract(uv) - .5;
    vec2 id = floor(uv);

    float n = N21(id);
    t += n * 6.2831;

    float w = UV.y * 10.;
    float x = (n - .5) * .8;
    x += (.4 - abs(x)) * f(w) * .45;
    float y = -sin(t + sin(t + sin(t) * .5));
	y -= (gv.x - x)* (gv.x - x);

    vec2 dropPos = (gv - vec2(x ,y))/aspect;
	float drop = S(.05,.03,length(dropPos));

	vec2 trailPos = (gv - vec2(x, t * .25)) / aspect;
	trailPos.y = (fract(trailPos.y * 8.) - .5) / 8.;
	float trail = S(.03,.01,length(trailPos));
	float fogTrail = 0.;
	fogTrail = S(-.05,.05,dropPos.y);
	fogTrail *= S(.5,y,gv.y);
	trail *= fogTrail;
	float m = drop + trail;

	//if(gv.x > .48 || gv.y > .49) m = 1.0;
	return m;
}

float Face(vec2 uv, float t) {
	vec2 gv = uv;
	float x = 0.;
	float y = 0.;
	float w = gv.y * 10. * sin(t);
	x += (.5 - abs(x)) * f(w) * .2;
	w = gv.x * 10. * cos(t);
	y += (.5 - abs(y)) * f(w) * .1;

	float mask = Circle(gv,vec2(x,y), .4, .05);
	mask -= Circle(uv, vec2(-.13 + x , .2 + y), .07, .01);
	mask -= Circle(uv, vec2(.13 + x, .2 + y), .07, .01);

	float mouth = Circle(uv, vec2(x, y), .3, .02);
	mouth -= Circle(uv, vec2(0. +x, 0.1 + y), .3, .02);
	mask -= mouth;

	//float drops = Drop(uv, t);
	//drops += Drop(uv * 1.45 + 8.11, t);
	//drops += Drop(uv * 1.08 - 5.83,t);
	//mask -= drops;
	vec2 id = floor(uv);

	float n = N21(id) * 6.2813;
	vec2 dropPos1 = vec2(x, .2+ y - .45 * fract(N21(uv) + .65 * (sin(t) * sin(t) + t)));
	//n = N21(id * id) * 6.2813;
	vec2 dropPos2 = vec2( x, .2+ y - .45 * fract(N21(uv) + .65 * (sin(t) * sin(t) + t)));
	float drop = Circle(uv,dropPos1, .5, .3); //sin(t + sin(t + sin(t) * .5)))
	//drop += Circle(uv, dropPos2, .4, .3);
	/*for (int i = 0; i < 5; i ++) {
		drop += Circle(uv, float2(dropPos1.x + .15 * i, -.28 + y - .45 * frac(i + .65 * (sin(t) * sin(t) + t))), .03, .04);
		}
	*/
	float trail = 0.;

	//vec2 trailPos = vec2(dropPos1.x, dropPos1.y + frac(t * .01));
	//trail += Circle(uv, trailPos, .02, .01);


	mask += drop;
	//mask -= trail;

	return mask;
}


