float PI = acos(-1.);

mat2 rot(float r){
  return mat2(cos(r),sin(r),-sin(r),cos(r));
}

float cube(vec3 p,vec3 s){
  vec3 q = abs(p);
  vec3 m = max(s-q,0.0);
  return length(max(q-s,0.))-min(min(m.x,m.y),m.z);
}

float loncylin(vec3 p,float s){
  return length(p.xy)-s;
}

vec2 pmod(vec2 p,float n){
  float np = 2.0*PI/n;
  float r = atan(p.x,p.y)-0.5*np;
  r = mod(r,np)-0.5*np;
  return length(p)*vec2(cos(r),sin(r));
}

vec4 dist(vec3 p){

  p.zy *= rot(0.5*PI);

  float ke = 1.5;

  float cs = 0.1/abs(mod(p.z+iTime,ke)-0.5*ke);


  vec3 sp = p;

  sp.xy *= rot(p.z*10.3+0.8*iTime);

  sp.xy = pmod(sp.xy,12.);

  sp.x -= 0.02;

  float d5 = loncylin(sp,0.0001);

  p.z += 0.1*iTime;

  p.xy = pmod(p.xy,7.);

  float k = 0.6;

  vec3 ssp = p;
  p = mod(p,k)-0.5*k;
  float d= cube(p,vec3(0.1));
  float d1 = cube(p,vec3(0.03,0.03,10.));
  float d2 = cube(p,vec3(0.03,10.,0.03));
  float d3 = cube(p,vec3(10.,0.03,0.03));
  d = min(d,d3);
  d = min(d,d1);
  d = min(d,d2);

  vec3 sssp = ssp;

  ssp.xy *= rot(0.25*PI);
  sssp.xz *= rot(0.25*PI);

  ssp = mod(ssp,k)-0.5*k;

  sssp = mod(sssp,k)-0.5*k;

  float d7 = cube(ssp,vec3(10.,0.03,0.03));
   float d8 = cube(sssp,vec3(10.,0.03,0.03));
  d = min(d,d7);
  d = min(d,d8);
  vec3 col = exp(-3.0*d)*(vec3(0.2,0.2,1.0)+0.8*cs*vec3(0.1,0.6,0.2));
  if(d>d5) col = 0.03*vec3(0.5,0.5,0.1)/max(d5,0.01);
  d = min(d,d5);

  return vec4(col,d);
}

vec3 getNormal(vec3 p){
  vec2 e = vec2(0.0001,0.);
  return normalize(vec3(
    dist(p+e.xyy).w-dist(p-e.xyy).w,
    dist(p+e.yxy).w-dist(p-e.yxy).w,
    dist(p+e.yyx).w-dist(p-e.yyx).w
    ));
}


