    vec3 oc = r.origin - s.center;
    float a = dot(r.direction, r.direction);
    float half_b = dot(r.direction, oc);
    float c = dot(oc, oc) - s.radius*s.radius;
    float discriminant = half_b*half_b - a*c;
    if (discriminant > 0.) {
        float temp = (-half_b - sqrt(discriminant))/a;
        if (temp < t_max && temp > t_min) {
            rec.t = temp;
            rec.p = point_at_parameter(r, rec.t);
            rec.normal = (rec.p - s.center) / s.radius;
            rec.mat = s.mat;
            return true;
        }
        temp = (-half_b + sqrt(discriminant))/a;
        if (temp < t_max && temp > t_min) {
            rec.t = temp;
            rec.p = point_at_parameter(r, rec.t);
            rec.normal = (rec.p - s.center) / s.radius;
            rec.mat = s.mat;
            return true;
        }
    }
    return false;
}

//Hitable list functions
bool hit(hitable_list hl, ray r, float t_min, float t_max,inout hit_record rec) {
    hit_record temp_rec;
    bool hit_anything = false;
    float closest_so_far = t_max;
    for (int i = 0; i < hl.list_size; i++) {
        if (hit(hl.list[i], r, t_min, closest_so_far, temp_rec)) {
            hit_anything = true;
            closest_so_far = temp_rec.t;
            rec = temp_rec;
        }
    }
    return hit_anything;
}

//Camera functions
camera create_camera(vec3 lookfrom, vec3 lookat, vec3 vup, float vfov, float aspect, float aperture, float focus_dist) {
    camera cam;
    cam.lens_radius = aperture/2.;
    float theta = vfov*PI/180.;
    float half_height = tan(theta/2.);
    float half_width = aspect*half_height;
    cam.origin = lookfrom;
    cam.w = unit_vector(lookfrom - lookat);
    cam.u = unit_vector(cross(vup, cam.w));
    cam.v = cross(cam.w, cam.u);
    cam.lower_left_corner = cam.origin - half_width*focus_dist*cam.u - half_height*focus_dist*cam.v - focus_dist*cam.w;
    cam.horizontal = 2.*half_width*focus_dist*cam.u;
    cam.vertical = 2.*half_height*focus_dist*cam.v;

    return cam;
}

vec3 random_in_unit_disk(vec2 uv) {
    vec3 p;
    float s = 0.;
    do {
        p = 2.0*vec3(rand(uv/iTime*(s+23.345)), rand(uv/iTime*(s+345.43)), 0.) - vec3(1., 1., 0.);
        s++;
    } while (dot(p, p) >= 1.);
    return p;
}

ray get_ray(camera cam, vec2 uv) {
    vec3 rd = cam.lens_radius*random_in_unit_disk(uv);
    vec3 offset = cam.u*rd.x + cam.v*rd.y;
    return ray(cam.origin + offset, cam.lower_left_corner + uv.x*cam.horizontal + uv.y*cam.vertical - cam.origin - offset);
}

//Lambertian functions
vec3 random_in_unit_sphere(vec2 uv) {
    vec3 p;
    float s = 0.;
    do {
        p = 2.0*vec3(rand(uv/iTime*(s+23.345)), rand(uv/iTime*(s+345.43)), rand(uv/iTime*(s+12345.43))) - vec3(1., 1., 1.);
        s++;
    } while (dot(p, p) >= 1.);
    return p;
}

bool scatter(lambertian l, ray r, hit_record rec, inout vec3 attenuation, inout ray scattered, vec2 uv) {
    vec3 target = rec.p + rec.normal + random_in_unit_sphere(uv);
    scattered = ray(rec.p, target-rec.p);
    attenuation = l.albedo;
    return true;
}

//Metal functions
vec3 reflect(vec3 v, vec3 n) {
    return v - 2.*dot(v,n)*n;
}

bool scatter(metal m, ray r_in, hit_record rec, inout vec3 attenuation, inout ray scattered, vec2 uv) {
    vec3 reflected = reflect(unit_vector(r_in.direction), rec.normal);
    scattered = ray(rec.p, reflected + m.fuzz*random_in_unit_sphere(uv));
    attenuation = m.albedo;
    return (dot(scattered.direction, rec.normal) > 0.); 
}

//Dielectric functions
bool refract(vec3 v, vec3 n, float ni_over_nt, inout vec3 refracted) {
    vec3 uv = unit_vector(v);
    float dt = dot(uv, n);
    float discriminant = 1. - ni_over_nt*ni_over_nt*(1.-dt*dt);
    if (discriminant > 0.) {
        refracted = ni_over_nt*(uv - n*dt) - n*sqrt(discriminant);
        return true;
    }
    return false;
}

float schlick(float cosine, float ref_idx) {
    float r0 = (1.-ref_idx) / (1.+ref_idx);
    r0 = r0*r0;
    return r0 + (1.-r0)*pow((1.-cosine),5.);
}

bool scatter(dielectric d, ray r_in, hit_record rec, inout vec3 attenuation, inout ray scattered, vec2 uv) {
    vec3 outward_normal;
    vec3 reflected = reflect(r_in.direction, rec.normal);
    float ni_over_nt;
    attenuation = vec3(1., 1., 1.);
    vec3 refracted;
    float reflect_prob;
    float cosine;
    if (dot(r_in.direction, rec.normal) > 0.) {
        outward_normal = -rec.normal;
        ni_over_nt = d.ref_idx;
        cosine = d.ref_idx * dot(r_in.direction, rec.normal) / length(r_in.direction);
    } else {
        outward_normal = rec.normal;
        ni_over_nt = 1./d.ref_idx;
        cosine = -dot(r_in.direction, rec.normal) / length(r_in.direction);
    }
    if (refract(r_in.direction, outward_normal, ni_over_nt, refracted)) {
        reflect_prob = schlick(cosine, d.ref_idx);
    } else {
        scattered = ray(rec.p, reflected);
        reflect_prob = 1.;
    }
    if (rand(uv/iTime*(23.345)) < reflect_prob) {
        scattered = ray(rec.p, reflected);
    } else {
        scattered = ray(rec.p, refracted);
    }
    return true;
}

