vec3 color(ray r, hitable_list world, vec2 uv) {
    hit_record rec;
    vec3 mult = vec3(1.);
    ray aux_r = ray(r.origin, r.direction);
    for (float i = 0.; i < DEPTH; i++) {
        if (!hit(world, aux_r, 0.001, MAXFLOAT, rec)) break;
        ray scattered;
        vec3 attenuation;
        if ((rec.mat.num_m == 0. && scatter(rec.mat.l, aux_r, rec, attenuation, scattered, uv)) || 
            (rec.mat.num_m == 1. && scatter(rec.mat.m, aux_r, rec, attenuation, scattered, uv)) ||
            (rec.mat.num_m == 2. && scatter(rec.mat.d, aux_r, rec, attenuation, scattered, uv))) {
            mult *= attenuation;
        } else {
            return vec3(0, 0, 0);
        }
        aux_r = scattered;
    }
    vec3 unit_direction = unit_vector(r.direction);
    float t = .5*(unit_direction.y + 1.);
    return mult*((1.-t)*vec3(1., 1., 1.) + t*vec3(.5, .7, 1.));
}

