#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))

float scale;


float map(vec3 p) 
{
    vec3 pp=p;
	p.z -= -iTime *1.0;
	p.xy = abs(p.xy) - 1.0;
	if (p.x < p.y) p.xy = p.yx;
	p.z = mod(p.z, 4.0) - 2.0;

	p.x -= 3.2;
	p = abs(p);
	float s = 2.0;
	 vec3 offset =p*1.5;

    float r;

	for (float i = 0.0; i < 5.0; i++)
    {
		p = 1.0 - abs(p - 1.0);
		 r = -7.5 * clamp(0.38 * max(1.2 / dot(p, p), 1.), 0.0, 1.0);
		s *= r;
		p *= r;
        p += offset;
	}
	s = abs(s);
	scale = s;
	float a = 100.0;
	p -= clamp(p, -a, a);
	return (length(p)) /( s);
}

vec3 GetNormal(vec3 p)
{
	float d = map(p);
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(
        map(p-e.xyy),
        map(p-e.yxy),
        map(p-e.yyx));
    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
	vec3 rd = normalize(vec3(uv, 1.0));
    vec3 ro=vec3(0, 0.5, -3);
	vec3 p = ro;
    scale=1.0;
     float d;

     //vec3 light_color1 = vec3( 1.0 );
     vec3 light_pos1= vec3(2.0, 3.0, 10.0 );
     vec3 final;
     float shininess=1.0;

	for (int i = 1; i < 100; i++) {
		float d = map(p);
		p += rd * d;
		if (d < 0.001) 
        {

            vec3 color=mix(vec3(0.85), cos(vec3(2.0, 9., 3.) + log2(scale)) * 0.5 + 0.5, 0.5) * 10.0 / float(i);

            vec3 normal=GetNormal(p);
            vec3 v = normalize( p-ro);
              float diff = dot(normal, light_pos1);

            vec3 vl = normalize( light_pos1 - p );
            vec3 ref_v = reflect( v, normal );
            float diffuse  = max( 0.0, dot( vl, normal ) );
            float specular = max( 0.0, dot( vl, ref_v ) );

            specular = pow( specular, shininess );

            final += color * ( diffuse + specular);

			break;
		}


	}


    fragColor =vec4( final,1.0);

    fragColor *= 1.0 - pow(d /(100.0) , 3.5);    
    fragColor = pow(fragColor, vec4(0.5545));  


}

#define slices 180.

const float disp = 0.4;

const float eps = 0.01;

const float width = 0.00035;

float fun(float p, float py){

    float f = sin(p + iTime + cos(py*0.05 + sin(p))*0.7)*sin(py*0.1 + iTime*0.2);

    //f = cos(p*0.4- iTime + py)*cos(p*0.4*sin(p) + iTime)*(sin(py + iTime));
    //f = sin(p*0.5 + sin(py))*(cos(py*0.1 + iTime));

    f *= mix(
        smoothstep(0.,1.,abs(p + sin(py)*0.1)),
        smoothstep(0.,1.,abs(p + sin(py*0.3 + iTime)*0.1)),
        0.5 + sin(iTime*0.4 )*0.5
        );

    return f*disp;
}



float graph(float y, float fn0, float fn1, float pixelSize){
  return smoothstep(pixelSize ,0., 
                    abs(fn0-y)/length(vec2((fn1-fn0)/eps,1.))- width);
}
float graphNoAbs(float y, float fn0, float fn1, float pixelSize){
  return smoothstep(pixelSize,0., 
                    -(fn0-y)/length(vec2((fn1-fn0)/eps,1.)) - width);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);


    float pixelSize = dFdx(uv.x)*1.25;

    for(float i = 0.; i < slices; i++ ){
        vec2 p = uv + vec2(0.,i/slices*2. - 0.8);

        //float funIdx = p.x*4. + sin(p.y*i/slices*2. + iTime)*1.5*sin(p.x - iTime);
        float funIdx = p.x;

        col -= graphNoAbs( p.y + 0.0, fun(funIdx,i), fun(funIdx+eps,i), pixelSize);
        col = max(col,0.);
        col = mix(col, vec3(1), graph( p.y, fun(funIdx,i), fun(funIdx+eps,i), pixelSize ));

    }


    col = 1. - col;


    col = pow(col,vec3(0.4545));
    fragColor = vec4(col,1.0);
}

/*
Testing gradient normalization in 3D for estimating the distance to implicit surfaces. It works
surprisingly well. Some of the surfaces are based off the wikipedia article:
https://en.wikipedia.org/wiki/Implicit_surface
*/

// Just in case ;)
#define EXTRA_FUDGE_FACTOR 1.0

// Settings
#define MAX_TRACE_DIST 10.0
#define MIN_HIT_DIST 0.001
#define GRAD_STEP 0.001
#define GRAD_CAP 20.0
#define MAX_STEPS 100.0

// Uncomment one to see a different surface
//#define GENUS_2
//#define SPHERICAL_HARMONIC
//#define CASSINI_OVAL
//#define QUARTIC_SPHERE
#define TANGLECUBE

float mapSceneImplicit(in vec3 p) {
    float c = cos(iTime), s = sin(iTime);
    p.xz *= mat2(c, -s, s, c);
    p.yz *= mat2(c, -s, s, c);

    #ifdef GENUS_2
    float t1 = 2.0 * p.y * (p.y * p.y - 3.0 * p.x * p.x) * (1.0 - p.z * p.z);
    float t2 = p.x * p.x + p.y * p.y; t2 *= t2;
    float t3 = (9.0 * p.z * p.z - 1.0) * (1.0 - p.z * p.z);
    return max(t1 + t2 - t3, dot(p, p) - 4.0); // Theres other stuff that gets in the way
    #endif

    #ifdef SPHERICAL_HARMONIC
    p /= 2.0;
    const float k = 0.3153915652; // sqrt(5/)/4
    return dot(p, p) - pow(3.0 * k * p.y * p.y / dot(p, p) - k, 2.0);
    #endif

    #ifdef CASSINI_OVAL
    float q1 = length(p - vec3(-1.5,  0.0,  0.0));
    float q2 = length(p - vec3( 1.5,  0.0,  0.0));
    float q3 = length(p - vec3( 0.0, -1.5,  0.0));
    float q4 = length(p - vec3( 0.0,  1.5,  0.0));
    float q5 = length(p - vec3( 0.0,  0.0, -1.5));
    float q6 = length(p - vec3( 0.0,  0.0,  1.5));
    return q1 * q2 * q3 * q4 * q5 * q6 - 14.0;
    #endif

    #ifdef QUARTIC_SPHERE
    return dot(p * p, p * p) - 4.0;
    #endif

    #ifdef TANGLECUBE
    return dot(4.0 * p * p, 4.0 * p * p) + dot(-10.0 * p, 2.0 * p) + 11.0;
    #endif
}

float getGrad(in vec3 p) {
    vec3 e = vec3(GRAD_STEP, 0.0, 0.0);
    return length(vec3(mapSceneImplicit(p + e.xyy) - mapSceneImplicit(p - e.xyy),
                       mapSceneImplicit(p + e.yxy) - mapSceneImplicit(p - e.yxy),
                       mapSceneImplicit(p + e.yyx) - mapSceneImplicit(p - e.yyx))) / (2.0 * e.x);
}

float mapScene(in vec3 p) {
    return mapSceneImplicit(p) / max(getGrad(p), GRAD_CAP) * EXTRA_FUDGE_FACTOR;
}

vec3 getNormal(in vec3 p) {
    vec3 e = vec3(GRAD_STEP, 0.0, 0.0);
    return normalize(vec3(mapSceneImplicit(p + e.xyy) - mapSceneImplicit(p - e.xyy),
                          mapSceneImplicit(p + e.yxy) - mapSceneImplicit(p - e.yxy),
                          mapSceneImplicit(p + e.yyx) - mapSceneImplicit(p - e.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float t = 0.0;
    for (float iters=0.0; iters < MAX_STEPS; iters++) {
        vec3 p = ro + rd * t;
        float d = mapScene(p);
        if (d < MIN_HIT_DIST) {
            vec3 n = getNormal(p);
            vec3 l = vec3(-0.58, 0.58, 0.58);
            fragColor.rgb += n * max(0.2, dot(n, l));
            break;
        }

        if (t > MAX_TRACE_DIST) {
            break;
        }

        t += d;
    }

    fragColor.rgb = pow(fragColor.rgb, vec3(0.4545));
}

void mainImage(out vec4 color, in vec2 coord)
{
    coord.x += 5. * sin(iTime * 4. + coord.y / 4.);
    coord.x -= 10. * sin(iTime * 4.);
    vec2 s = iResolution.xy*.5*vec2(1.-(sin(iTime * 2.)/4.));
    vec2 c = coord - s; c.y *= -1.;

    vec2 a = step(abs(c-s.y*.35), s.yy*.4);
    vec2 b = step(abs(c+s.y*.15), s.yy*.6);
    vec3 col = (a.xxx*a.y-.5)*(b.x*b.y-.5)*4.;

    color = vec4(col,1);
}

