  t+=d=DE(ro+rd*t);
  if(d<dm){dm=d;tm=t;}
 }
 vec3 col=vec3(rd.y*rd.y);
 vec3 L=normalize(vec3(0.2,0.6,-0.3));
 bool secondMarch=(dm>0.0002*tm);
 if(secondMarch){//unless we got really close speed up the march
