const float PI = 3.14159265358979;
const mat4x3 colors = mat4x3(
    1.0, 0.7, 0.9,
    1.0, 0.5, 0.6,
    0.3, 0.5, 0.7,
    0.5, 0.6, 0.9
);

// xor-ish behavior for floats
float flXor(float x, float y) {
    return x + y - 2. * (x * y);
}

// folds 0>1>2>3>4... to 0>1<0>1<0...
float fold(float x) {
    return abs(1. - mod(x, 2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scales pixel coordinates, so that
    // the center is distance 0 and
    // diagonals are distance 1
    vec2 uvR = 2. * fragCoord - iResolution.xy;
    vec2 uv = uvR / length(iResolution.xy);

    float lenSq = log(length(uv));
    float lenAdd = -0.2;
    // logx/dx = 1/x
    float blur = 4.0 / max(0.1, length(uvR));
    float angle = atan(uv.y, uv.x) / PI;

    float timeScale = iTime * PI * 0.125;

    float spiral1 = smoothstep(0.5 - blur, 0.5 + blur, fold(
         (lenSq + lenAdd) * 0.8
       + angle * 2.
       - iTime
    ));

    float spiral2 = smoothstep(0.5 - blur, 0.5 + blur, fold(
         (lenSq + lenAdd) * 1.0
       - angle * 3.
       - iTime
    ));
    vec3 colA = mix(colors[0], colors[1], vec3(flXor(spiral1, spiral2)));


    float spiral3 = smoothstep(0.2 - blur, 0.2 + blur, fold(
         (lenSq + lenAdd) * 0.7
       + angle * 5.
       - iTime
    ));

    float spiral4 = smoothstep(0.3 - blur, 0.3 + blur, fold(
         (lenSq + lenAdd) * 1.4
       - angle * 1.
       - iTime
    ));
    vec3 colB = mix(colors[2], colors[3], vec3(spiral3 * spiral4));

    float which = smoothstep(0.5 - blur * 0.5, 0.5 + blur * 0.5, fold(
        lenSq * (1.0 + 0.5 * sin(timeScale))
      - angle
      + iTime * 0.8
    ));

    vec3 col = mix(colA, colB, which);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define renderDist 10000.
#define SUR_ACC 0.01
vec3 rotate(vec3 Coords,vec3 EulerAngles)
{
    vec3 realCoords=  vec3(0.,0.,0.);

    realCoords.x=Coords.x*cos(EulerAngles.z)-Coords.y*sin(EulerAngles.z);
    realCoords.y=Coords.y*cos(EulerAngles.z)+Coords.x*sin(EulerAngles.z);

    realCoords.z=Coords.z*cos(EulerAngles.x)-realCoords.y*sin(EulerAngles.x);
    realCoords.y=realCoords.y*cos(EulerAngles.x)+Coords.z*sin(EulerAngles.x);

    realCoords.x=realCoords.x*cos(EulerAngles.y)-realCoords.z*sin(EulerAngles.y);
    realCoords.z=realCoords.z*cos(EulerAngles.y)+realCoords.x*sin(EulerAngles.y);
    return realCoords;
}

vec4 skybox(vec2 fakeCoords, vec3 EulerAngles)
{
    vec3 Coords =  vec3(fakeCoords.x,fakeCoords.y,sqrt(sqrt(0.5*0.5*2.)-length(fakeCoords)));
    vec3 realCoords=  rotate(Coords,EulerAngles);

    vec3 hue=vec3(15./255.,125./215.,1.)*1.;
    float value=0.+1.2*abs(abs(realCoords.y)-1.);
    return vec4(hue*value,1);
}
float Box(vec3 p, float size){
vec3 q=abs(p)-size;
return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float recursiveHallowBox(vec3 p, float size, int i){


while(i>0){
p=abs(p)-size/2.;
size*=1./3.;

p*=-1.;
if(size<max(max(p.x,p.y),p.z)){
if(p.x>=p.y&&p.x>=p.z){
p.x-=size;
}else{
if(p.y>p.x&&p.y>=p.z){
p.y-=size;
}else{
if(p.z>p.x&&p.z>p.y){
p.z-=size;
}}}}
p*=-1.;
p+=size/2.;
i--;
}
return Box(p,size) ;

}

float DE(vec3 coords)
{
//float kugel=distance(vec3(25.,25.,25.),coords)-2.;

vec3 p=coords-vec3(sin(iTime)*15.,0.,30.);



return min(recursiveHallowBox(p,10.,1),coords.y+20.);
}

vec3 GetNormal(vec3 p) {
	float d = DE(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        DE(p-e.xyy),
        DE(p-e.yxy),
        DE(p-e.yyx));

    return normalize(n);
}
float RayMarch (vec3 or, vec3 dir){
    for(float i=0.;i<renderDist;){

    vec3 pos= i*dir+or;
    float DE=DE(pos);
    if(DE<SUR_ACC){
    return i;

    }else{
    i+=DE*0.9;
    }
    }
    return renderDist;
}
float GetLight(vec3 p, vec3 lightPos) {
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SUR_ACC*2., l);
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    vec2 muv=iMouse.xy/iResolution.xy;
    muv-=0.5;
    vec3 Camrotation= vec3(muv.y*6.,-muv.x*6.,0);;
    vec3 CamPosition= vec3(0,5,mod(iTime,80.)*0.-20.);
    vec3 LightPos =vec3(-10.,10.,-10.);//vec3(2,-5,300);

    vec4 color;
    vec3 albedo= vec3(1,0,0);
    float renderdistance=0.1;
    float fov=3.14159/4.;
    float XtoYratio=iResolution.y/iResolution.x;
    //vec3 movVec=vec3(sin(fov*uv.x),sin(fov*uv.y)*XtoYratio,cos(fov*uv.x));

    vec3 movVec=normalize(vec3(uv.x,uv.y*XtoYratio,1));
    movVec=normalize(rotate(movVec,Camrotation));
    float accuracy=0.000001;





    //Ray march
    float d=RayMarch(CamPosition,movVec);
    vec3 renderpos=CamPosition+movVec*d;


    if(renderpos.x>0.){
    albedo=vec3(1.-renderpos.x/(renderpos.x+1.),0.,renderpos.x/(renderpos.x+1.));
    }

    bool weirdwaytoshade=false;

    if(d>=renderDist){
    color=skybox(uv,Camrotation);
    }else{

    if(weirdwaytoshade){
        float lightlevel=1.;
        vec3 LightVec=normalize(LightPos-renderpos);
            for(float i=SUR_ACC*3.;i<renderDist;){

            vec3 pos= i*LightVec+renderpos;
            float DE=min(DE(pos),distance(pos,LightPos));

            if(DE<accuracy){
            if(distance(pos,LightPos)>accuracy){
            lightlevel*=0.01;
            }

            break;

            }else{
            i+=DE*0.5;
            }
            lightlevel*=0.98;
            }

        color=vec4(albedo*lightlevel,1);

    }else{
    color=vec4(albedo*GetLight(renderpos,LightPos),1);
    }
    }
    //fragColor=vec4(uv.x,uv.y,0.,0.);
    fragColor= color;
}

// reusing hexastairs  https://shadertoy.com/view/3sGfWm
float ease( float x ) {                              // rewritten from https://shadertoy.com/view/3ddBWB
	return x > .5 ? x = 1.-x, 1. - 4.* x*x*x         // stepier than smoothstep or .5-.5*cos                  :                4.* x*x*x ;
                  :                4.* x*x*x ;
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 1.5* ( 2.*u  - R ) / R.y;

    float t = iTime/2., s, l;
    U *= mat2( cos( 3.1416*( floor(t)+ease(fract(t)) + vec4(0,.5,-.5,0)) ) ); // rotation
    l = length(U+vec2(.18,-.94));                          // for sphere
    U += R/R.y;

    U *= mat2(1,0,.5,.87);                                 // parallelogram frame

    vec3  V = vec3( U, U.y-U.x +3.);                       // 3 axial coords
    ivec3 I = ivec3(floor(V));
          I += I.yzx;
    I.x += 4; I /= 3;                                      // I.xy = hexagon id
 // V = mod( V + vec3( I.y, I.y+I.x, I.x ), 2. ).yzx;      // local coords
    V.xy = mod( V.yz + vec2( I.y+I.x, I.x ), 2. );         // local coords

    O = mix(
        vec4( 
          I.xy==ivec2(2,1)                                 // central cube
            ? s = mod(4.5*V.y-1.,2.)-1.,                   // strip slope  for stairs. Side dents below
              abs( .1+2.*V.x-V.y +(abs(s)-5.5)/4.5 ) > .9 ? .5 : s < 0. ? .3 : 1. // draw stairs
            : 0.                                           // background
        ),
        vec4(sqrt(max(0.,1.-l/.1)),0,0,0), smoothstep(4./R.y,0., l-.1) ); // red sphere
}

