float square(vec2 st) {
    vec2 bl = step(vec2(0.), st);       // bottom-left
    vec2 tr = step(vec2(0.),1.0-st);   // top-right
    return bl.x * bl.y * tr.x * tr.y;
}

vec4 frame(vec2 st) {
    float tushka = square(st*mat2((2./5.8), 1., 1., (2./7.9)));

    mat2 sector_mat = mat2(1./.16, 0., 0., 1./.22);
    float sectors[4];
    sectors[0] = square(st * sector_mat + (2./.26)*vec2(0.001,-0.380));
    sectors[1] = square(st * sector_mat + (2./.26)*vec2(0.001,-0.070));
    sectors[2] = square(st * sector_mat + (2./.26)*vec2(-0.340,-0.380));
    sectors[3] = square(st * sector_mat + (2./.26)*vec2(-0.340,-0.070));
    vec3 sector_colors[4];
    sector_colors[0] = vec3(1.041, 0.539, 0.504) * sectors[0];
    sector_colors[1] = vec3(0.535, 0.782, 0.943) * sectors[1];
    sector_colors[2] = vec3(0.759, 0.908, 0.606) * sectors[2];
    sector_colors[3] = vec3(1.096, 0.959, 0.214) * sectors[3];

    return vec4(vec3(sector_colors[0] + sector_colors[1] +
                     sector_colors[2] + sector_colors[3]), tushka);
}

