    scale = index.x * 0.092 + 0.552;

    vec3 color;
    if (index.y > 1.0 && index.y < 3.1 ) {
        color = vec3(0.535, 0.782, 0.943);
        scale *= .9;
    } else if (index.y > 4.9 && index.y < 6.1) {
        color = vec3(1.041, 0.539, 0.504);
        scale *= .9;
    } else {
        color = vec3(1., 1., 1.);
    }

    float scale1 = 2./scale;
    float shift = - (2.-scale) / (3. * scale);
    vec2 st2 = vec2(vec3(st, 2.) * mat3(scale1, 1., shift, 1., scale1, shift, 1., 1., 2.));
    float mask = square(st2);

    return vec4( color, mask );
}

vec4 trail(vec2 st) {
    // actually 1/width, 1/height
    const float piece_height = 8. / .79;
    const float piece_width = 7. / .64;

    // make distance between smaller segments slightly lower
    st.x = 2.2760 * pow(st.x, 4.0) - 2.4624 * st.x*st.x + 2.4154 * st.x;

    float x_at_cell = floor(st.x*piece_width)/piece_width;
    float x_at_cell_center = x_at_cell + 0.026;
    float incline = cos(0.6*period + wave_phase()) * wave_amplitude;

    float offset = sin(x_at_cell_center*period + wave_phase())* wave_amplitude + 
        incline*(st.x-x_at_cell)*6.452;

    float mask = step(offset, st.y) * (1.-step(.79+offset, st.y)) * step(0., st.x);

    vec2 cell_coord = vec2((st.x - x_at_cell) * piece_width,
                           fract((st.y-offset) * piece_height));
    vec2 cell_index = vec2(x_at_cell * piece_width, 
                           floor((st.y-offset) * piece_height));

    vec4 pieces = trail_piece(cell_coord, cell_index, 0.852);

    return vec4(vec3(pieces), pieces.a * mask);
}

vec4 logo(vec2 st) {
    if (st.x <= .64) {
        return trail(st);
    } else {
        vec2 st2 = st + vec2(0.1, -sin(st.x*period + wave_phase())*wave_amplitude);
        return frame(st2 + vec2(-.64, -0.1));
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    st += vec2(-.1);
    st *= 2.472;
    st += vec2(-0.8,-0.78);
    float rot = PI*-0.224;
    st *= mat2(cos(rot), sin(rot), -sin(rot), cos(rot));
    vec3 color = vec3(2.);

    vec4 logo_ = logo(st);    
    fragColor = mix(vec4(0.1,.6,.6,2.000), logo_, logo_.a);
}

void lookAt(inout vec3 rd,vec3 ro,vec3 ta,vec3 up){
    vec3 w=normalize(ta-ro),u=normalize(cross(w,up));
    rd=rd.x*u+rd.y*cross(u,w)+rd.z*w;
}

void rot(inout vec3 p,vec3 a,float t){
	a=normalize(a);
	vec3 u=cross(a,p),v=cross(a,u);
	p=u*sin(t)+v*cos(t)+a*dot(a,p);   
}


// https://www.shadertoy.com/view/4djSRW
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}


float map(vec3 p)
{
	rot(p,vec3(cos(iTime*.3),sin(iTime*.3),1),iTime*.3);
    float d=1.;
    for(float i=0.;i<80.;i++)
    {
        vec3 c=hash31(i+123.1)*2.-1.;
        c*=2.;
        d=min(d,length(p-c)-.3);
    }
    return d;
}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float fixedMarch(vec3 ro, vec3 rd, float pitch, float near, float far)
{
    float t=near,d;
    for(int i=0;i<50;i++)
    {
        d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
        t+=pitch;
    }
    return far;
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<100;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

float calcShadow(vec3 light, vec3 ld, float len)
{
	float depth=march(light,ld,.01,len);	
	return step(len-depth,.01);
}

vec3 doColor(vec3 p)
{
    return vec3(.3,.5,.8)+cos(p*.2)*.5+.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0,8);
    vec3 rd = normalize(vec3(uv,2));
    vec3 ta =vec3(0);
    lookAt(rd,ro,ta,vec3(0,1,0));    
    vec3 col= vec3(0);

    // bounding sphere
    float r=3.2;
    vec3 o=vec3(0);
    vec3 u=cross(o-ro,rd);
    float h=sqrt(r*r-dot(u,u));
    float g=dot(o-ro,rd);
    float near=g-h;
	float far=g+h;
    //near=0.;

#if 1
    float pitch = .3;
    float t=fixedMarch(ro,rd,pitch,near,far);
    if(t<far)t=march(ro,rd,t-pitch,far);
#else
    float t=march(ro,rd,near,far);
#endif

    if(t<far)
    {
        vec3 p=ro+rd*t;
        col=doColor(p); 
        vec3 n=calcNormal(p);      
		vec3 lightPos=ro+vec3(0,3,0);
    	vec3 li=lightPos-p;
		float len=length(li);
		li/=len;
		float dif=clamp(dot(n,li),0.,1.);
        float sha=calcShadow(lightPos,-li,len);
        col*=max(sha*dif,.2);
        float rimd=pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn=rimd+2.2*(1.-rimd);
    	col*=frn*.6;
        col*=max(.5+.5*n.y,.3);
        col*=exp2(-2.*pow(max(0.,1.-map(p+n*.8)/.8),2.));
    }
    fragColor.xyz=col;
}

//Shader designed based of this tutorial: https://www.youtube.com/watch?v=PGtv-dBi2wE
//Tutorial project: https://www.shadertoy.com/view/XlGBW3

#define MAXDIS 100.
#define MAXSTEP 100
#define SURFDIS 0.001
#define LIGHTINTENSITY 5.
#define LIGHTCOL vec3(1,.0,.5)

float GetDis(vec3 p)
{
	vec4 s = vec4(0, 1.05, 6, 1);

    float sd =  abs(length(p-s.xyz)-s.w)-.1;
    float pd = p.y-1.;

    float d = max(sd, pd);
    pd = p.y;
    d=min(d,pd);
    //d = min(d,cd);
    //d=cd;

	s = vec4(3, 1.05, 6, 1);

    sd =  abs(length(p-s.xyz)-s.w)-.1;

    d = min(d,sd);

	s = vec4(1.5, 4, 7, 1);

    sd =  abs(length(p-s.xyz)-s.w)-.1;

    d = min(d,sd);

    return d;
}

float RayMarch(vec3 ro, vec3 rd)
{
    float d=.0;

    for(int i = 0; i<MAXSTEP; i++)
    {
        vec3 p = ro+rd*d;
        float ds = GetDis(p);
        d+=ds;
        if(ds<SURFDIS || d>MAXDIS)
        {
            break;
        }
    }

    return(d);
}

vec3 GetNormal(vec3 p)
{
    float d = GetDis(p);
    vec2 e = vec2(.01,0);

    vec3 n = d-vec3(
    GetDis(p-e.xyy),
    GetDis(p-e.yxy),
    GetDis(p-e.yyx));

    return normalize(n);
}

vec3 TestMarch(vec3 ro, vec3 rd)
{
    float d=.0;

    for(int i = 0; i<MAXSTEP; i++)
    {
        vec3 p = ro+rd*d;
        float ds = GetDis(p);
        d+=ds;
        if(ds<SURFDIS || d>MAXDIS)
        {
            break;
        }
    }
    vec3 p = ro + rd * d;
    p = p;
    return(p);
}

vec3 GetLight (vec3 p)
{
    vec3 light = vec3(0,4.+sin(iTime),6);
    vec3 l = normalize(light-p);
    vec3 n = GetNormal(p);

    float diff = dot(n,l);

    float d = RayMarch(p+n*SURFDIS*2.,l);
    if(d<length(light-p))
    {
        diff*=.1;
    }
    diff*=LIGHTINTENSITY/(length(p-light)*length(p-light));

    vec3 dir=normalize(vec3(0, 5, -1)-p);

    return max(diff,0.) * vec3(sin(iTime*5.)/2.+.5,0,.5);//LIGHTCOL;//+dot(n,-l)*0.1;
}

vec3 GetRef (vec3 p,vec3 dir)
{
    vec3 n = GetNormal(p);
    vec3 diff=GetLight(RayMarch(p+n*SURFDIS*2.,reflect(dir,n))*normalize(reflect(dir,n)));

    return diff;
}

vec3 Emission(vec3 ro, vec3 rd)
{
    vec3 p = vec3(0,4.+sin(iTime),6);
    vec4 s = vec4(p, 0.25);
    float d=.0;

    for(int i = 0; i<MAXSTEP; i++)
    {
        vec3 p = ro+rd*d;
        float ds = length(p-s.xyz)-s.w;
        d+=ds;
        if(ds<SURFDIS)
        {
            return vec3(sin(iTime*5.)/2.+.5,0,.5);
        }
        if(d>MAXDIS)
        {
            return vec3(0);
        }
    }

    return vec3(0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;


    vec3 ro = vec3(0, 5.+1.0*cos(iTime), -1);
    vec3 rd = normalize(vec3(uv.x, uv.y-0.3, 0.7));

    vec3 p = TestMarch(ro, rd);

    vec3 diff=GetLight(p);

    vec3 n = GetNormal(p);
    vec3 pd = reflect(rd,n);

    //diff = max(GetLight(TestMarch(p+n*SURFDIS*2.,pd)),diff);
    float ref = 0.2;
    diff *= 1.-ref;
    diff += ref*GetLight(TestMarch(p+n*SURFDIS*2.,pd));

    vec3 col = diff;
    col+=Emission(ro, rd);

    fragColor = vec4(col,1.0);
}

float DistLine(vec3 ro, vec3 rd, vec3 p){
    //distance from ray to point
    return length(cross(p-ro,rd))/length(rd);
}
float DrawPoint(vec3 ro, vec3 rd, vec3 p){
    //get distance from ro to p
    float d = DistLine(ro, rd, p);

    //draw the point
    d = smoothstep(0.1,0.07,d);
    return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x *= iResolution.x/iResolution.y;
    //time
    float t = iTime;

    //ray origin
    vec3 ro = vec3(0.0, 0.0, -3.0);

    //ray direction
	vec3 rd = vec3(uv.x,uv.y, 0)-ro;

    //position of a point
    vec3 p = vec3(sin(t)+1., 0.0, 2.+cos(t));

    //draw the point
	vec3 d = vec3(DrawPoint(ro,rd,p),0,0);

    //position of second point
    p = vec3(sin(t), 0.0, 2.+cos(t));

    //draw the second point
	d += vec3(0,0,DrawPoint(ro,rd,p));


    p = vec3(sin(t),-0.6, 2.+cos(t));
	d += vec3(0,DrawPoint(ro,rd,p),0);

    p = vec3(sin(t)+1.,-0.6, 2.+cos(t));
	d += vec3(0,DrawPoint(ro,rd,p),0);
    // Output to screen
    fragColor = vec4(d,1.0);
}

